              ) : (
                'Sign In'
              )}
            </button>
          </form>

          {/* Divider */}
          <div className="flex items-center my-6">
            <div className="flex-1 h-px bg-[#1e293b]" />
            <span className="px-4 text-xs text-gray-500 uppercase tracking-wider">or</span>
