/**
 * FlexibleCard - Universal Data Adapter Container
 * 
 * The standard rendering block for Synapse and Vault data streams.
 * Automatically selects the appropriate adapter based on data type and
 * provides a Raw/Visual toggle for all content types.
 * 
 * @example
 * ```tsx
 * <FlexibleCard
 *   type="metric"
 *   title="Memory Usage"
 *   data={{ value: 84, label: 'MB Used', unit: 'MB', source: 'synapse_plugin_metrics' }}
 *   showModeToggle
 * />
 * ```
 */

import React, { useState } from 'react';
import { Eye, Code } from '@/components/ui/HearthlinkIcons';
import type { UDAProps, UDARenderMode, UDATextProps, UDAJsonProps, UDAMetricProps, UDAListProps, UDAGraphProps } from './types';
import { TextAdapter } from './adapters/TextAdapter';
import { JsonAdapter } from './adapters/JsonAdapter';
import { MetricAdapter } from './adapters/MetricAdapter';
import { ListAdapter } from './adapters/ListAdapter';
import { GraphAdapter } from './adapters/GraphAdapter';

const ModeToggle: React.FC<{
  mode: UDARenderMode;
  onToggle: () => void;
}> = ({ mode, onToggle }) => (
  <button
    onClick={onToggle}
    className="flex items-center gap-1.5 px-2 py-1 rounded text-[10px] font-medium uppercase tracking-wider transition-all bg-black/30 border border-white/10 hover:border-cyan-500/30 hover:bg-cyan-500/5"
    title={mode === 'visual' ? 'Switch to Raw mode' : 'Switch to Visual mode'}
  >
    {mode === 'visual' ? (
      <>
        <Code size={10} className="text-cyan-400" />
        <span className="text-slate-400">Raw</span>
      </>
    ) : (
      <>
        <Eye size={10} className="text-cyan-400" />
        <span className="text-slate-400">Visual</span>
      </>
    )}
  </button>
);

export interface FlexibleCardProps extends Omit<UDAProps, 'mode'> {
  /** Initial render mode (default: 'visual') */
  defaultMode?: UDARenderMode;
}

/**
 * Render helper that properly types each adapter call.
 */
function renderAdapterForType(adapterProps: UDAProps): React.ReactNode {
  switch (adapterProps.type) {
    case 'text':
      return <TextAdapter {...(adapterProps as UDATextProps)} />;
    case 'json':
      return <JsonAdapter {...(adapterProps as UDAJsonProps)} />;
    case 'metric':
      return <MetricAdapter {...(adapterProps as UDAMetricProps)} />;
    case 'list':
      return <ListAdapter {...(adapterProps as UDAListProps)} />;
    case 'graph':
      return <GraphAdapter {...(adapterProps as UDAGraphProps)} />;
    default:
      return <div className="text-slate-500 text-xs">Unknown data type</div>;
  }
}

export const FlexibleCard: React.FC<FlexibleCardProps> = ({
  defaultMode = 'visual',
  showModeToggle = true,
  title,
  className = '',
  ...restProps
}) => {
  const [mode, setMode] = useState<UDARenderMode>(defaultMode);

  const handleModeToggle = () => {
    const newMode = mode === 'visual' ? 'raw' : 'visual';
    setMode(newMode);
    restProps.onModeChange?.(newMode);
  };

  // Construct the full adapter props with current mode
  const adapterProps: UDAProps = {
    ...restProps,
    mode,
    className: '',
  } as UDAProps;

  return (
    <div
      className={`relative rounded-xl border border-white/5 bg-black/30 backdrop-blur-sm overflow-hidden ${className}`}
    >
      {/* Header */}
      {(title || showModeToggle) && (
        <div className="flex items-center justify-between px-4 py-2 bg-black/20 border-b border-white/5">
          {title && (
            <h3 className="text-xs font-semibold text-slate-300 uppercase tracking-widest">
              {title}
            </h3>
          )}
          {showModeToggle && (
            <ModeToggle mode={mode} onToggle={handleModeToggle} />
          )}
        </div>
      )}

      {/* Content */}
      <div className="p-4">
        {renderAdapterForType(adapterProps)}
      </div>
    </div>
  );
};

export default FlexibleCard;

