        return;
      }

      // Sort by timestamp (newest first)
      const sorted = [...result.data].sort((a, b) => {
        const aTime = new Date(a.received_date || '').getTime();
        const bTime = new Date(b.received_date || '').getTime();
        return bTime - aTime;
      });
      setMessages(sorted);
    } catch (err) {
      setError(err instanceof Error ? err.message : 'Failed to load messages');
    } finally {
      setLoading(false);
    }
  }, [bridge, selectedAccountId]);

  // Initial load of accounts
  useEffect(() => {
    loadAccounts();
  }, [loadAccounts]);

  // Load messages when account changes
  useEffect(() => {
    if (selectedAccountId) {
      loadMessages();
    }
  }, [selectedAccountId, loadMessages]);

  // Update selected index when message is selected
  useEffect(() => {
    if (selectedMessage) {
      const index = filteredMessages.findIndex(m => m.id === selectedMessage.id);
      setSelectedIndex(index);
    }
  }, [selectedMessage, filteredMessages]);

  // Close folder dropdown when clicking outside
  useEffect(() => {
    const handleClickOutside = (event: MouseEvent) => {
      if (folderDropdownRef.current && !folderDropdownRef.current.contains(event.target as Node)) {
        setShowFolderDropdown(false);
      }
    };
    document.addEventListener('mousedown', handleClickOutside);
    return () => document.removeEventListener('mousedown', handleClickOutside);
  }, []);

  // Navigate through messages with j/k keys
  const navigateMessages = useCallback((direction: number) => {
    if (filteredMessages.length === 0) return;

    let newIndex = selectedIndex + direction;
    if (newIndex < 0) newIndex = 0;
    if (newIndex >= filteredMessages.length) newIndex = filteredMessages.length - 1;

    setSelectedIndex(newIndex);
    setSelectedMessage(filteredMessages[newIndex]);
  }, [filteredMessages, selectedIndex]);

  // Handle new email
  const handleNewEmail = () => {
    setComposeMode('new');
    setReplyToMessage(null);
    setShowCompose(true);
  };

  // Handle reply
  const handleReply = useCallback(() => {
    if (!selectedMessage) return;
    setComposeMode('reply');
    setReplyToMessage(selectedMessage);
    setShowCompose(true);
  }, [selectedMessage]);

  // Handle reply all
  const handleReplyAll = useCallback(() => {
    if (!selectedMessage) return;
    setComposeMode('replyAll');
    setReplyToMessage(selectedMessage);
    setShowCompose(true);
  }, [selectedMessage]);

  // Handle forward
  const handleForward = useCallback(() => {
    if (!selectedMessage) return;
    setComposeMode('forward');
    setReplyToMessage(selectedMessage);
    setShowCompose(true);
  }, [selectedMessage]);

  // Handle Convert to Task
  const handleConvertToTask = useCallback(() => {
    if (!selectedMessage) return;
    
    setTaskInitialData({
      title: `Email: ${selectedMessage.subject || '(No Subject)'}`,
      description: `From: ${selectedMessage.from}\nDate: ${new Date(selectedMessage.received_date || '').toLocaleString()}\n\n${selectedMessage.body || selectedMessage.body_html || ''}`
    });
    setShowCreateTask(true);
  }, [selectedMessage]);

  const handleTaskCreated = async (task: Partial<Task>) => {
    if (bridge) {
       try {
         // Assuming bridge has createTask method exposed or we use alden_tasks_create
         await bridge.createTask({
            title: task.title!,
            description: task.description,
            priority: task.priority,
            due_date: task.due_date,
            status: 'todo'
         } as any);
         setSuccessMessage('Task created from email');
         setTimeout(() => setSuccessMessage(null), 3000);
       } catch (err) {
         setError('Failed to create task');
       }
    }
  };

  // Handle archive
  const handleArchive = async (_messageId: string) => {
    // TODO: Implement archive functionality when backend supports it
    setSuccessMessage('Email archived');
    setTimeout(() => setSuccessMessage(null), 3000);
  };

  // Handle toggle read/unread
  const handleMarkUnread = async (messageId: string) => {
    // TODO: Implement mark unread functionality when backend supports it
    setMessages(prev => prev.map(m =>
      m.id === messageId ? { ...m, read: !m.read } : m
    ));
    setSuccessMessage('Email marked as unread');
    setTimeout(() => setSuccessMessage(null), 3000);
  };

  // Handle toggle star
  const handleToggleStar = useCallback(async (messageId: string) => {
    // TODO: Implement star toggle functionality when backend supports it
    setMessages(prev => prev.map(m =>
      m.id === messageId ? { ...m, starred: !m.starred } : m
    ));
    const message = messages.find(m => m.id === messageId);
    setSuccessMessage(message?.starred ? 'Star removed' : 'Email starred');
    setTimeout(() => setSuccessMessage(null), 3000);
  }, [messages]);

  // Handle move to folder
  const handleMoveToFolder = async (messageId: string, folder: string) => {
    // TODO: Implement move functionality when backend supports it
    setShowFolderDropdown(false);
    setSuccessMessage(`Email moved to ${folder}`);
    setTimeout(() => setSuccessMessage(null), 3000);
  };

  // Handle delete email
  const handleDeleteEmail = useCallback(async (messageId: string) => {
    // TODO: Implement delete functionality when backend supports it
    setMessages(prev => prev.filter(m => m.id !== messageId));
    if (selectedMessage?.id === messageId) {
      setSelectedMessage(null);
      setSelectedIndex(-1);
    }
    setSuccessMessage('Email deleted');
    setTimeout(() => setSuccessMessage(null), 3000);
  }, [selectedMessage]);

  // Handle keyboard shortcuts
  useEffect(() => {
    const handleKeyDown = (event: KeyboardEvent) => {
      // Ignore if typing in an input or textarea
      if (
        event.target instanceof HTMLInputElement ||
        event.target instanceof HTMLTextAreaElement ||
        showCompose ||
        showAccountSetup
      ) {
        return;
      }

      switch (event.key.toLowerCase()) {
        case 'n':
          event.preventDefault();
          handleNewEmail();
          break;
        case 'r':
          if (selectedMessage) {
            event.preventDefault();
            handleReply();
          }
          break;
        case 'a':
          if (selectedMessage) {
            event.preventDefault();
            handleReplyAll();
          }
          break;
        case 'f':
          if (selectedMessage) {
            event.preventDefault();
            handleForward();
          }
          break;
        case 'delete':
        case 'backspace':
          if (selectedMessage) {
            event.preventDefault();
            handleDeleteEmail(selectedMessage.id);
          }
          break;
        case 'j':
          event.preventDefault();
          navigateMessages(1);
          break;
        case 'k':
          event.preventDefault();
          navigateMessages(-1);
          break;
        case 's':
          if (selectedMessage) {
            event.preventDefault();
            handleToggleStar(selectedMessage.id);
          }
          break;
        case 'u':
          if (selectedMessage) {
            event.preventDefault();
            handleMarkUnread(selectedMessage.id);
          }
          break;
        case '?':
          event.preventDefault();
          setShowShortcutsHelp(prev => !prev);
          break;
        case 'escape':
          setShowShortcutsHelp(false);
          setShowFolderDropdown(false);
          if (viewMode === 'list' && selectedMessage) {
            setSelectedMessage(null);
          }
          break;
      }
    };

    window.addEventListener('keydown', handleKeyDown);
    return () => window.removeEventListener('keydown', handleKeyDown);
  }, [selectedMessage, selectedIndex, filteredMessages, showCompose, showAccountSetup, viewMode, handleDeleteEmail, handleForward, handleReply, handleReplyAll, handleToggleStar, navigateMessages]);

  const handleSync = async () => {
    if (!bridge || !selectedAccountId) return;

    setSyncing(true);
    setError(null);

    try {
      const result = await bridge.syncEmailAccount(selectedAccountId);

      if (result.success) {
        setSuccessMessage(`Synced ${result.data} new messages`);
        setTimeout(() => setSuccessMessage(null), 3000);
        await loadMessages();
      } else {
        setError(result.error || 'Failed to sync account');
      }
    } catch (err) {
      setError(err instanceof Error ? err.message : 'Failed to sync account');
    } finally {
      setSyncing(false);
    }
  };

  const handleComposeSent = async () => {
    setShowCompose(false);
    setReplyToMessage(null);
    setSuccessMessage('Email sent successfully!');
    setTimeout(() => setSuccessMessage(null), 3000);
    await loadMessages();
  };

  const handleAccountSetupSuccess = async () => {
    await loadAccounts();
    setShowAccountSetup(false);
  };

  if (loading && accounts.length === 0) {
    return (
      <div className="flex flex-col items-center justify-center p-8 gap-4">
        <LoadingSpinner size="lg" />
        <div className="text-gray-400">Loading email...</div>
      </div>
    );
  }

  if (accounts.length === 0 && !loading) {
    return (
      <div className="p-6 space-y-4" data-testid="empty-state">
        <div className="bg-blue-50 border border-blue-200 rounded-lg p-4">
          <p className="text-blue-800">No email accounts configured yet.</p>
          <p className="text-blue-600 text-sm mt-2">
            Add an email account to get started with your inbox.
          </p>
          <button
            onClick={() => setShowAccountSetup(true)}
            data-testid="add-account-button"
            className="mt-4 px-4 py-2 bg-green-600 text-white rounded-md hover:bg-green-700 transition-colors"
          >
            + Add Account
          </button>
        </div>
        <EmailAccountSetup
          isOpen={showAccountSetup}
          onClose={() => setShowAccountSetup(false)}
          onSuccess={handleAccountSetupSuccess}
        />
      </div>
    );
  }



  return (
    <div
      ref={containerRef}
      className="p-6 space-y-4 h-full flex flex-col"
      role="application"
      aria-label="Email inbox"
      data-testid="email-inbox"
    >
      {/* Header */}
      <div className="flex justify-between items-center flex-wrap gap-2">
        <h2 className="text-2xl font-bold text-gray-900">Email Inbox</h2>
        <div className="flex space-x-2 flex-wrap gap-2">
          {/* View Mode Toggle */}
          <div className="flex border border-gray-300 rounded-md overflow-hidden">
            <button
              onClick={() => setViewMode('list')}
              className={`px-3 py-2 text-sm transition-colors ${
                viewMode === 'list'
                  ? 'bg-blue-600 text-white'
                  : 'bg-white text-gray-700 hover:bg-gray-50'
              }`}
              aria-label="List view"
              aria-pressed={viewMode === 'list'}
            >
              <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 6h16M4 10h16M4 14h16M4 18h16" />
              </svg>
            </button>
            <button
              onClick={() => setViewMode('split')}
              className={`px-3 py-2 text-sm transition-colors ${
                viewMode === 'split'
                  ? 'bg-blue-600 text-white'
                  : 'bg-white text-gray-700 hover:bg-gray-50'
              }`}
              aria-label="Split view"
              aria-pressed={viewMode === 'split'}
            >
              <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 17V7m0 10a2 2 0 01-2 2H5a2 2 0 01-2-2V7a2 2 0 012-2h2a2 2 0 012 2m0 10a2 2 0 002 2h2a2 2 0 002-2M9 7a2 2 0 012-2h2a2 2 0 012 2m0 10V7m0 10a2 2 0 002 2h2a2 2 0 002-2V7a2 2 0 00-2-2h-2a2 2 0 00-2 2" />
              </svg>
            </button>
          </div>

          <button
            onClick={() => setShowAccountSetup(true)}
            data-testid="add-account-button"
            className={`px-4 py-2 rounded-md transition-colors ${
              accounts.length === 0
                ? 'bg-green-600 text-white hover:bg-green-700'
                : 'border border-green-600 text-green-700 hover:bg-green-50 bg-white'
            }`}
          >
            + Add Account
          </button>
          <button
            onClick={handleNewEmail}
            disabled={!selectedAccountId || loading}
            data-testid="compose-button"
            className="px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-blue-500 disabled:opacity-50 disabled:cursor-not-allowed"
            aria-label="Compose new email (Press N)"
          >

