/**
 * CoreDashboard - Premium Core Module Interface
 *
 * Features:
 * - MythTech color palette (Deep Navy #0b1221, Cyan #00e5ff, Gold #d6b369)
 * - Tabs: Overview, Projects (Kanban), Conferences
 * - Premium glassmorphism design
 * - Integrated chat with resident AI (Alden)
 * - Real-time agent status and event streaming
 *
 * @version 2.0.0 - Premium UI Enhancement
 */

import React, { useState, useEffect, useCallback, useMemo } from 'react';
import { UnifiedCommandCenterOptimized as UnifiedCommandCenter } from '../shared/premium/UnifiedCommandCenter.optimized';
import { CoreBridge } from '../../bridges/CoreBridge';
import { ProjectsDashboard } from './ProjectsDashboard';
import { UnifiedChatContainer } from '../shared/chat/UnifiedChatContainer';
import { useChatStateWithOrchestration } from '../shared/chat/useChatStateWithOrchestration';
import { MODULE_RESIDENT_AI, type SystemPersonaId } from '../shared/chat/types';
import { PrivateChannel } from '../shared/chat/PrivateChannel';
import type { Conference, CoreSession, ConferenceType } from '../../types/agents';
import { Activity, Users, MessageSquare, Play, Pause, Archive, Plus, AlertTriangle, CheckCircle, Clock, Share2 } from '@/components/ui/HearthlinkIcons';

interface CoreDashboardProps {
  userId: string;
  onBackToLauncher?: () => void;
  onNavigateToModule?: (moduleName: string) => void;
  onFocusAssist?: () => void;
}

interface CoreEvent {
  event_id: string;
  event_type: 'message' | 'handoff' | 'tool_call';
  timestamp: string;
  from_agent?: string;
  to_agent?: string;
  agent_id?: string;
  details?: Record<string, unknown>;
  priority?: 'low' | 'medium' | 'high' | 'urgent';
}

const CONFERENCE_TYPES: ConferenceType[] = [
  'brainstorm',
  'problem_solving',
  'review',
  'planning',
  'research',
];

// MythTech Design Tokens
const MYTHTECH_COLORS = {
  navy: '#0b1221',
  cyan: '#00e5ff',
  gold: '#d6b369',
  slate: {
    50: '#f8fafc',
    100: '#f1f5f9',
    200: '#e2e8f0',
    300: '#cbd5e1',
    400: '#94a3b8',
    500: '#64748b',
    600: '#475569',
    700: '#334155',
    800: '#1e293b',
    900: '#0f172a',
  }
};

export const CoreDashboard: React.FC<CoreDashboardProps> = ({
  userId,
  onBackToLauncher,
  onNavigateToModule,
  onFocusAssist,
}) => {
  const [activeTab, setActiveTab] = useState('overview');
  const [conferences, setConferences] = useState<Conference[]>([]);
  const [currentSession, setCurrentSession] = useState<CoreSession | null>(null);
  const [events, setEvents] = useState<CoreEvent[]>([]);
  const [loading, setLoading] = useState(true);
  const [showCreateModal, setShowCreateModal] = useState(false);
  const [conferenceForm, setConferenceForm] = useState({
    title: '',
    purpose: '',
    conferenceType: 'brainstorm' as ConferenceType
  });

  // Chat Integration
  const moduleId = 'core';
  const residentPersonaId = (MODULE_RESIDENT_AI[moduleId] || 'alden') as SystemPersonaId;

  const {
    personas: chatPersonas,
    privateMessages,
    privateLoading,
    privateError,
    privateTyping,
    sendPrivateMessage,
  } = useChatStateWithOrchestration({
    userId,
    moduleId,
    residentPersonaId,
    onError: (err) => console.error('Core Chat Error:', err),
  });

  const residentPersona = chatPersonas.find(p => p.id === residentPersonaId);
  const bridge = useMemo(() => new CoreBridge({ userId }), [userId]);

  const loadDashboardData = useCallback(async () => {
    try {
      setLoading(true);
      await bridge.initialize();
      const props = await bridge.getProps();
      setCurrentSession(props.currentSession);

      const transformedEvents: CoreEvent[] = props.messages.map(msg => ({
        event_id: msg.message_id,
        event_type: 'message',
        timestamp: msg.timestamp,
        from_agent: msg.from_agent,
        to_agent: msg.to_agent,
        details: { content: msg.content },
        priority: msg.priority === 'normal' ? 'medium' : msg.priority,
      }));
      setEvents(transformedEvents);
      setConferences(props.conferences);
    } catch (err) {
      console.error('[Core] Failed to load dashboard:', err);
    } finally {
      setLoading(false);
    }
  }, [bridge]);

  useEffect(() => {
    loadDashboardData();
  }, [loadDashboardData]);

  const handleCreateConference = async () => {
    if (!conferenceForm.title.trim() || !conferenceForm.purpose.trim()) return;

    try {
      const result = await bridge.createConference({
        userId,
        title: conferenceForm.title,
        purpose: conferenceForm.purpose,
        conferenceType: conferenceForm.conferenceType,
        participants: [],
      });

      if (result.success && result.data) {
        setConferences([...conferences, result.data]);
        setShowCreateModal(false);
        setConferenceForm({ title: '', purpose: '', conferenceType: 'brainstorm' });
      }
    } catch (err) {
      console.error('[Core] Failed to create conference:', err);
    }
  };

  const tabs = [
    { id: 'overview', label: 'Overview' },
    { id: 'projects', label: 'Projects' },
    { id: 'conferences', label: 'Conferences' },
    { id: 'chat', label: 'Chat' },
  ];

  const renderOverview = () => (
    <div className="flex flex-col h-full overflow-hidden space-y-6 p-1">
      {/* Agent Status Grid - Premium MythTech Design */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-4 shrink-0">
        {[
          { name: 'Alden', status: 'Active', color: MYTHTECH_COLORS.gold, active: true },
          { name: 'Alice', status: 'Idle', color: '#8b5cf6', active: false },
          { name: 'Mimic', status: 'Idle', color: '#a855f7', active: false }
        ].map(agent => (
          <div
            key={agent.name}
            className="glass-panel p-4 flex items-center gap-4 hover:bg-white/5 transition-all duration-300"
            style={{
              background: 'linear-gradient(135deg, rgba(11,18,33,0.8), rgba(0,229,255,0.05))',
              borderColor: agent.active ? MYTHTECH_COLORS.cyan : 'rgba(148,163,184,0.3)'
            }}
          >
            <div
              className={`w-3 h-3 rounded-full ${agent.active ? 'pulse-glow' : ''}`}
              style={{
                backgroundColor: agent.active ? '#10b981' : MYTHTECH_COLORS.slate[500],
                boxShadow: agent.active ? '0 0 10px rgba(16,185,129,0.6)' : 'none'
              }}
            />
            <div>
              <h3 className="font-bold text-white tracking-wide">{agent.name}</h3>
              <p className="text-xs font-medium" style={{ color: MYTHTECH_COLORS.slate[300] }}>
                {agent.status}
              </p>
            </div>
          </div>
        ))}
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6 min-h-0 flex-1">
        {/* Active Conferences Panel */}
        <div
          className="glass-panel p-6 flex flex-col overflow-hidden"
          style={{
            background: 'linear-gradient(135deg, rgba(11,18,33,0.9), rgba(0,229,255,0.05))',
            borderColor: 'rgba(0,229,255,0.2)'
          }}
        >
          <div className="flex justify-between items-center mb-4 shrink-0">
            <h2 className="text-lg font-bold flex items-center gap-2 tracking-wide" style={{ color: MYTHTECH_COLORS.cyan }}>
              <Users size={20} style={{ color: MYTHTECH_COLORS.cyan }} />
              Active Conferences
            </h2>
            <span
              className="text-xs px-3 py-1 rounded-full border font-bold"
              style={{
                backgroundColor: 'rgba(0,229,255,0.1)',
                color: MYTHTECH_COLORS.cyan,
                borderColor: 'rgba(0,229,255,0.3)'
              }}
            >
              {conferences.filter(c => c.state === 'active').length} Active
            </span>
          </div>
          <div className="flex-1 overflow-y-auto pr-2 space-y-3 scrollbar-thin scrollbar-thumb-slate-700">
            {conferences.filter(c => c.state === 'active').length === 0 ? (
              <div className="text-center py-8" style={{ color: MYTHTECH_COLORS.slate[500] }}>
                No active conferences
              </div>
            ) : (
              conferences.filter(c => c.state === 'active').map(conf => (
                <div
                  key={conf.conference_id}
                  className="p-4 rounded-lg border-l-4 backdrop-blur-sm hover:bg-white/5 transition-all"
                  style={{
                    backgroundColor: 'rgba(15,23,42,0.5)',
                    borderLeftColor: '#10b981'
                  }}
                >
                  <h4 className="font-medium" style={{ color: MYTHTECH_COLORS.slate[200] }}>
                    {conf.title}
                  </h4>
                  <p className="text-sm mt-1" style={{ color: MYTHTECH_COLORS.slate[400] }}>
                    {conf.purpose}
                  </p>
                  <div className="flex gap-2 mt-3 flex-wrap">
                    {conf.participants.map((p, i) => (
                      <span
                        key={i}
                        className="text-xs px-2 py-1 rounded"
                        style={{
                          backgroundColor: MYTHTECH_COLORS.slate[700],
                          color: MYTHTECH_COLORS.slate[300]
                        }}
                      >
                        {p.agent_id}
                      </span>
                    ))}
                  </div>
                </div>
              ))
            )}
          </div>
        </div>

        {/* System Events Panel */}
        <div
          className="glass-panel p-6 flex flex-col overflow-hidden"
          style={{
            background: 'linear-gradient(135deg, rgba(11,18,33,0.9), rgba(214,179,105,0.05))',
            borderColor: 'rgba(214,179,105,0.2)'
          }}
        >
          <h2 className="text-lg font-bold mb-4 flex items-center gap-2 shrink-0 tracking-wide" style={{ color: MYTHTECH_COLORS.gold }}>
            <Activity size={20} style={{ color: MYTHTECH_COLORS.gold }} />
            System Events
          </h2>
          <div className="flex-1 overflow-y-auto pr-2 space-y-3 scrollbar-thin scrollbar-thumb-slate-700">
            {events.slice(0, 10).map(event => (
              <div
                key={event.event_id}
                className="flex gap-3 text-sm p-3 rounded border backdrop-blur-sm hover:bg-white/5 transition-all"
                style={{
                  backgroundColor: 'rgba(15,23,42,0.3)',
                  borderColor: 'rgba(100,116,139,0.3)'
                }}
              >
                <div className="mt-1">
                  {event.event_type === 'message' && <MessageSquare size={14} style={{ color: MYTHTECH_COLORS.cyan }} />}
                  {event.event_type === 'handoff' && <Share2 size={14} style={{ color: '#a855f7' }} />}
                  {event.event_type === 'tool_call' && <Activity size={14} style={{ color: '#f59e0b' }} />}
                </div>
                <div>
                  <div className="flex items-center gap-2">
                    <span className="font-medium capitalize" style={{ color: MYTHTECH_COLORS.slate[300] }}>
                      {event.event_type.replace('_', ' ')}
                    </span>
                    <span className="text-xs" style={{ color: MYTHTECH_COLORS.slate[500] }}>
                      {new Date(event.timestamp).toLocaleTimeString()}
                    </span>
                  </div>
                  <p className="mt-0.5" style={{ color: MYTHTECH_COLORS.slate[400] }}>
                    {event.event_type === 'message' && `${event.from_agent} Æ’+' ${event.to_agent}`}
                    {event.event_type === 'handoff' && `${event.from_agent} handed off to ${event.to_agent}`}
                    {event.event_type === 'tool_call' && `${event.agent_id} called tool`}
                  </p>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );

  const renderConferences = () => (
    <div className="flex flex-col h-full overflow-hidden">
      <div className="flex justify-between items-center mb-6 shrink-0">
        <div>
          <h2 className="text-xl font-semibold" style={{ color: MYTHTECH_COLORS.slate[200] }}>
            Conference Management
          </h2>
          <p className="text-sm" style={{ color: MYTHTECH_COLORS.slate[400] }}>
            Orchestrate multi-agent sessions and collaborative workflows
          </p>
        </div>
        <button
          onClick={() => setShowCreateModal(true)}
          className="px-4 py-2 rounded-lg flex items-center gap-2 transition-all font-medium hover:shadow-lg"
          style={{
            backgroundColor: MYTHTECH_COLORS.cyan,
            color: MYTHTECH_COLORS.navy,
            boxShadow: `0 0 20px ${MYTHTECH_COLORS.cyan}40`
          }}
          aria-label="New Conference"
        >
          <Plus size={18} />
          New Conference
        </button>
      </div>

      <div className="flex-1 overflow-y-auto pr-2 grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4 pb-6 scrollbar-thin scrollbar-thumb-slate-700">
        {conferences.map(conf => (
          <div
            key={conf.conference_id}
            className="glass-panel p-5 flex flex-col hover:scale-[1.02] transition-all duration-300"
            style={{
              background: 'linear-gradient(135deg, rgba(15,23,42,0.5), rgba(0,229,255,0.05))',
              borderColor: 'rgba(100,116,139,0.3)'
            }}
          >
            <div className="flex justify-between items-start mb-3">
              <span
                className={`px-2 py-1 rounded text-xs font-medium uppercase tracking-wider border`}
                style={{
                  backgroundColor: conf.state === 'active' ? 'rgba(16,185,129,0.1)' :
                                 conf.state === 'archived' ? 'rgba(100,116,139,0.1)' :
                                 'rgba(251,191,36,0.1)',
                  color: conf.state === 'active' ? '#10b981' :
                        conf.state === 'archived' ? MYTHTECH_COLORS.slate[400] :
                        '#fbbf24',
                  borderColor: conf.state === 'active' ? 'rgba(16,185,129,0.3)' :
                              conf.state === 'archived' ? 'rgba(100,116,139,0.3)' :
                              'rgba(251,191,36,0.3)'
                }}
              >
                {conf.state}
              </span>
              <span className="text-xs" style={{ color: MYTHTECH_COLORS.slate[500] }}>
                {new Date(conf.created_at).toLocaleDateString()}
              </span>
            </div>

            <h3 className="font-bold mb-2" style={{ color: MYTHTECH_COLORS.slate[200] }}>
              {conf.title}
            </h3>
            <p className="text-sm mb-4 line-clamp-2 flex-1" style={{ color: MYTHTECH_COLORS.slate[400] }}>
              {conf.purpose}
            </p>

            <div className="border-t pt-4 mt-auto" style={{ borderColor: 'rgba(100,116,139,0.3)' }}>
              <div className="flex items-center justify-between">
                <div className="flex -space-x-2">
                  {conf.participants.map((p, i) => (
                    <div
                      key={i}
                      className="w-6 h-6 rounded-full border-2 flex items-center justify-center text-[10px]"
                      title={p.agent_id}
                      style={{
                        backgroundColor: MYTHTECH_COLORS.slate[700],
                        borderColor: MYTHTECH_COLORS.slate[800],
                        color: MYTHTECH_COLORS.slate[300]
                      }}
                    >
                      {p.agent_id[0]}
                    </div>
                  ))}
                </div>
                <button
                  className="text-xs font-medium hover:underline"
                  style={{ color: MYTHTECH_COLORS.cyan }}
                >
                  View Details Æ’+'
                </button>
              </div>
            </div>
          </div>
        ))}
      </div>

      {/* Create Conference Modal */}
      {showCreateModal && (
        <div className="fixed inset-0 bg-black/60 backdrop-blur-sm z-50 flex items-center justify-center p-4">
          <div
            className="rounded-xl w-full max-w-md p-6 shadow-2xl"
            style={{
              background: 'linear-gradient(135deg, rgba(11,18,33,0.95), rgba(0,229,255,0.1))',
              border: `1px solid ${MYTHTECH_COLORS.cyan}40`,
              boxShadow: `0 0 40px ${MYTHTECH_COLORS.cyan}20`
            }}
          >
            <h3 className="text-xl font-bold mb-4" style={{ color: MYTHTECH_COLORS.cyan }}>
              Create New Conference
            </h3>
            <div className="space-y-4">
              <div>
                <label className="block text-sm font-medium mb-1" style={{ color: MYTHTECH_COLORS.slate[400] }}>
                  Title
                </label>
                <input
                  type="text"
                  value={conferenceForm.title}
                  onChange={e => setConferenceForm({...conferenceForm, title: e.target.value})}
                  className="w-full rounded-lg px-3 py-2 text-white focus:outline-none focus:ring-2 transition-all"
                  placeholder="e.g., Q4 Strategy Review"
                  style={{
                    backgroundColor: MYTHTECH_COLORS.slate[800],
                    borderColor: MYTHTECH_COLORS.slate[700],
                    border: '1px solid'
                  }}
                />
              </div>
              <div>
                <label className="block text-sm font-medium mb-1" style={{ color: MYTHTECH_COLORS.slate[400] }}>
                  Type
                </label>
                <select
                  value={conferenceForm.conferenceType}
                  onChange={e => setConferenceForm({...conferenceForm, conferenceType: e.target.value as ConferenceType})}
                  className="w-full rounded-lg px-3 py-2 text-white focus:outline-none focus:ring-2 transition-all"
                  style={{
                    backgroundColor: MYTHTECH_COLORS.slate[800],
                    borderColor: MYTHTECH_COLORS.slate[700],
                    border: '1px solid'
                  }}
                >
                  {CONFERENCE_TYPES.map(t => (
                    <option key={t} value={t}>{t.replace('_', ' ').toUpperCase()}</option>
                  ))}
                </select>
              </div>
              <div>
                <label className="block text-sm font-medium mb-1" style={{ color: MYTHTECH_COLORS.slate[400] }}>
                  Purpose
                </label>
                <textarea
                  value={conferenceForm.purpose}
                  onChange={e => setConferenceForm({...conferenceForm, purpose: e.target.value})}
                  className="w-full rounded-lg px-3 py-2 text-white focus:outline-none focus:ring-2 h-24 resize-none transition-all"
                  placeholder="Describe the goal of this session..."
                  style={{
                    backgroundColor: MYTHTECH_COLORS.slate[800],
                    borderColor: MYTHTECH_COLORS.slate[700],
                    border: '1px solid'
                  }}
                />
              </div>
              <div className="flex justify-end gap-3 pt-2">
                <button
                  onClick={() => setShowCreateModal(false)}
                  className="px-4 py-2 text-sm font-medium transition-colors rounded-lg"
                  style={{ color: MYTHTECH_COLORS.slate[400] }}
                >
                  Cancel
                </button>
                <button
                  onClick={handleCreateConference}
                  className="px-4 py-2 text-sm font-medium rounded-lg transition-all"
                  style={{
                    backgroundColor: MYTHTECH_COLORS.cyan,
                    color: MYTHTECH_COLORS.navy,
                    boxShadow: `0 0 20px ${MYTHTECH_COLORS.cyan}40`
                  }}
                >
                  Create Conference
                </button>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );

  // State for sidebar collapsed mode
  const [sidebarCollapsed, setSidebarCollapsed] = useState(false);

  // Memoized Sidebar (Private Channel)
  const chatSidebar = useMemo(() => {
    if (activeTab === 'chat' || !residentPersona) return null;
    return (
      <PrivateChannel
        residentPersona={residentPersona}
        messages={privateMessages}
        isLoading={privateLoading}
        error={privateError}
        isTyping={privateTyping}
        onSendMessage={sendPrivateMessage}
        isCollapsed={sidebarCollapsed}
        onToggleCollapse={() => setSidebarCollapsed(prev => !prev)}
        onStandardView={() => setActiveTab('chat')}
        className="h-full border-l"
        onFocusAssist={onFocusAssist}
      />
    );
  }, [activeTab, residentPersona, sidebarCollapsed, privateMessages, privateLoading, privateError, privateTyping, sendPrivateMessage]);

  return (
    <UnifiedCommandCenter
      title="Core"
      subtitle="System Orchestrator"
      iconPath="/assets/Core.png"
      tabs={tabs}
      activeTab={activeTab}
      onTabChange={setActiveTab}
      onBackToLauncher={onBackToLauncher}
      onNavigateToModule={onNavigateToModule}
      sidebar={chatSidebar}
      sidebarCollapsed={sidebarCollapsed}
      fullWidthContent={activeTab === 'projects' || activeTab === 'chat'}
      accentColor={MYTHTECH_COLORS.cyan}
      themeColor={MYTHTECH_COLORS.navy}
      titleColor={MYTHTECH_COLORS.cyan}
      subtitleColor={MYTHTECH_COLORS.slate[200]}
      dateColor={MYTHTECH_COLORS.slate[300]}
      timeColor="#ffffff"
      headerStyle={{
        background: `linear-gradient(90deg, ${MYTHTECH_COLORS.navy} 0%, ${MYTHTECH_COLORS.slate[900]} 50%, ${MYTHTECH_COLORS.navy} 100%)`,
        borderBottom: `2px solid ${MYTHTECH_COLORS.cyan}`,
        boxShadow: `0 4px 20px ${MYTHTECH_COLORS.cyan}20`
      }}
    >
      {activeTab === 'chat' && (
        <UnifiedChatContainer
          userId={userId}
          moduleId="core"
          residentPersonaId={residentPersonaId}
          onClose={() => setActiveTab('overview')}
          className="h-full w-full"
        />
      )}
      {activeTab === 'overview' && renderOverview()}
      {activeTab === 'projects' && <ProjectsDashboard userId={userId} embedded={true} />}
      {activeTab === 'conferences' && renderConferences()}
    </UnifiedCommandCenter>
  );
};

