/**
 * ActiveAgentPanel - Displays the currently active AI agent with avatar and metrics
 * Shows real-time status, system metrics, and cognitive load
 * Supports both 2D (AgentAvatar) and 3D (Avatar3D) avatar rendering
 */
import React, { memo, useMemo } from 'react';
import { Activity, Zap, Cpu, HardDrive, Wifi, Brain, Layers, Users, User } from '@/components/ui/HearthlinkIcons';
import { AgentAvatar } from './AgentAvatar';
import { Avatar3D } from '../../avatar/Avatar3D';
import { useAvatarStore } from '../../avatar/useAvatarStore';
import type { Persona, SystemMetrics, PersonaStatus } from './types';
import type { AvatarAnimationState } from '../../avatar/types';

interface ActiveAgentPanelProps {
  persona: Persona;
  metrics: SystemMetrics;
  onAvatarClick?: () => void;
  className?: string;
  onBridge?: () => void;
  onMute?: () => void;
  isMuted?: boolean;
  isBridgeActive?: boolean;
  hideHeader?: boolean;
}

interface VerticalMetricBarProps {
  label: string;
  value: number;
  color: string;
  icon?: React.ReactNode;
}

const VerticalMetricBar = memo<VerticalMetricBarProps>(({ label, value, color, icon }) => {
  const isCritical = value > 90;
  const isHigh = value > 80;
  const isElevated = value > 60;
  
  // Color Logic: Normal (Base) -> Elevated (Yellow) -> High (Orange) -> Critical (Red)
  const barColor = isCritical ? '#ef4444' : isHigh ? '#f97316' : isElevated ? '#eab308' : color;
  
  return (
    <div className="flex flex-col items-center gap-2 h-24 w-full">
      <div className="flex-1 w-2 bg-[#0b1628] rounded-full overflow-hidden border border-white/5 relative flex flex-col justify-end">
        <div
          className="w-full rounded-full transition-all duration-500"
          style={{
            height: `${Math.min(100, Math.max(0, value))}%`,
            backgroundColor: barColor,
            boxShadow: isHigh ? `0 0 10px ${barColor}` : `0 0 5px ${barColor}40`,
          }}
        />
      </div>
      <div className="flex flex-col items-center gap-0.5">
        {icon && <span className="opacity-60 text-[#8fa7c7]">{icon}</span>}
        <span className="text-[9px] text-[#8fa7c7] font-bold tracking-wider">{label}</span>
      </div>
    </div>
  );
});

VerticalMetricBar.displayName = 'VerticalMetricBar';

const StatusBadge = memo<{ status: PersonaStatus }>(({ status }) => {
  const config = useMemo(() => {
    switch (status) {
      case 'speaking':
        return { bg: 'bg-[#d6b369]/20', border: 'border-[#d6b369]', text: 'text-[#d6b369]' };
      case 'thinking':
