// Semi-Circle Gauge Component (SVG)
const SemiCircleGauge = memo<{ 
  value: number; 
  label: string; 
  color?: string;
  subLabel?: string;
}>(({ value, label, color = '#d6b369', subLabel }) => {
  const percentage = Math.min(100, Math.max(0, value));
  const radius = 36;
  const strokeWidth = 6;
  const normalizedRadius = radius - strokeWidth / 2;
  const circumference = normalizedRadius * 2 * Math.PI;
  // We only want 180 degrees (half circle)
  const arcLength = circumference / 2;
  const strokeDashoffset = arcLength - (percentage / 100) * arcLength;

  // Determine color based on threshold if not provided or generic
  const getThresholdColor = (val: number) => {
    // Override base color for high usage
    if (val >= 90) return '#ef4444'; // Critical (Red)
    if (val >= 80) return '#f97316'; // High (Orange)
    if (val >= 60) return '#eab308'; // Elevated (Yellow)
    
    // Normal state - use provided color or default
    return color !== '#d6b369' ? color : 'var(--chat-accent-color)'; // Default to Theme Color instead of Cyan/Gold hardcoded if passed #d6b369
  };

  const finalColor = getThresholdColor(percentage);

  return (
    <div className="flex flex-col items-center justify-center relative p-2 bg-[#0a0f1d]/40 rounded-xl border border-white/5">
      <div className="relative w-24 h-14 overflow-hidden flex justify-center items-end pb-0">
        <svg
          height="100%"
          width="100%"
          viewBox="0 0 84 44"
          className="overflow-visible"
        >
          {/* Background Arc */}
          <path
            d="M 6 40 A 36 36 0 0 1 78 40"
            fill="none"
            stroke="#1e293b"
            strokeWidth={strokeWidth}
            strokeLinecap="round"
          />
          {/* Value Arc */}
          <path
            d="M 6 40 A 36 36 0 0 1 78 40"
            fill="none"
            stroke={finalColor}
            strokeWidth={strokeWidth}
            strokeLinecap="round"
            strokeDasharray={`${arcLength} ${circumference}`}
            strokeDashoffset={strokeDashoffset}
            className="transition-all duration-700 ease-out"
            style={{ 
              filter: `drop-shadow(0 0 4px ${finalColor}60)`
            }}
          />
        </svg>
        {/* Value Text centered at bottom of arc */}
        <div className="absolute bottom-0 flex flex-col items-center">
           <span className="text-sm font-bold font-orbitron-force" style={{ color: finalColor }}>
            {percentage.toFixed(0)}%
          </span>
        </div>
      </div>
      
      {/* Label */}
      <div className="flex flex-col items-center mt-1">
        <span className="text-[9px] text-[#8fa7c7] uppercase tracking-widest font-bold text-center">
          {label}
        </span>
        {subLabel && (
          <span className="text-[8px] text-[#8fa7c7]/50 uppercase tracking-wider text-center">
            {subLabel}
          </span>
        )}
      </div>
    </div>
  );
});

