test.describe('Focus Management', () => {
  test.beforeEach(async ({ page }) => {
    await Mocks.install(page);
    await page.goto('/core');
    await Helpers.waitForAppReady(page);
  });

  test.afterEach(async () => {
    Mocks.reset();
  });

  test('should trap focus in modals', async ({ page }) => {
    // Open command palette
    const isMac = process.platform === 'darwin';
    await page.keyboard.press(isMac ? 'Meta+KeyK' : 'Control+KeyK');
    await page.waitForTimeout(500);

    const modal = page.locator(Selectors.commandPalette);
    const exists = await modal.count();

    if (exists > 0) {
      // Tab through elements
      for (let i = 0; i < 10; i++) {
        await page.keyboard.press('Tab');
        await page.waitForTimeout(100);

        const focusedOutsideModal = await page.evaluate(() => {
          const activeEl = document.activeElement;
          const modal = document.querySelector('[data-testid="command-palette"]');
          return modal && !modal.contains(activeEl);
        });

        // Focus should stay within modal
        expect(focusedOutsideModal).toBe(false);
      }
    }
  });

  test('should restore focus after closing modal', async ({ page }) => {
    // Focus a button
    const button = page.getByRole('button').first();
    const exists = await button.count();

    if (exists > 0) {
      await button.focus();
      const initialFocused = await page.evaluate(() => document.activeElement?.tagName);

      // Open and close modal
      const isMac = process.platform === 'darwin';
      await page.keyboard.press(isMac ? 'Meta+KeyK' : 'Control+KeyK');
      await page.waitForTimeout(300);

      await page.keyboard.press('Escape');
      await page.waitForTimeout(300);

      const finalFocused = await page.evaluate(() => document.activeElement?.tagName);

      // Focus should return to original element or body
      expect(['BUTTON', 'BODY']).toContain(finalFocused);
    }
  });

  test('should skip focus on disabled elements', async ({ page }) => {
    const disabledButton = page.locator('button[disabled]').first();
    const exists = await disabledButton.count();

    if (exists > 0) {
      // Tab through page
      for (let i = 0; i < 20; i++) {
        await page.keyboard.press('Tab');
        await page.waitForTimeout(50);

        const focusedDisabled = await page.evaluate(() => {
          const activeEl = document.activeElement as HTMLElement;
          return activeEl?.hasAttribute('disabled');
        });

        // Should never focus disabled element
        expect(focusedDisabled).toBe(false);
      }
    }
  });

  test('should move focus to first error field on validation', async ({ page }) => {
    // Navigate to a form
    const aldenButton = page.locator(Selectors.moduleAlden).or(
      page.getByRole('button', { name: /alden/i })
    );
    const exists = await aldenButton.count();

    if (exists > 0) {
      await aldenButton.evaluate(el => (el as HTMLElement).click());
      await page.waitForURL(/.*alden.*/);

      const createButton = page.getByRole('button', { name: /create|new/i }).first();
      const buttonExists = await createButton.count();

      if (buttonExists > 0) {
        await createButton.click();
        await page.waitForTimeout(500);

        // Submit empty form
        const submitButton = page.getByRole('button', { name: /submit|save/i }).first();
        const submitExists = await submitButton.count();

        if (submitExists > 0) {
          await submitButton.click();
          await page.waitForTimeout(500);

          // Check if focus moved to first error field
          const focused = await page.evaluate(() => document.activeElement?.tagName);
          expect(['INPUT', 'TEXTAREA', 'SELECT']).toContain(focused);
        }
      }
    }
  });
});

