use crate::db_manager::DatabaseManager;
use crate::synapse::skill_models::*;
use std::sync::Arc;
use tokio::sync::RwLock;

pub struct SkillRegistry {
    db: Arc<RwLock<Option<DatabaseManager>>>,
}

impl SkillRegistry {
    pub fn new(db: Arc<RwLock<Option<DatabaseManager>>>) -> Self {
        Self { db }
    }

    /// Initialize the database schema for the Skill System
    pub async fn initialize_schema(&self) -> Result<(), String> {
        let db = self.db.read().await;
        let db = db.as_ref().ok_or("Database not initialized")?;

        // 1. synapses_skills
        let query_skills = r#"
            CREATE TABLE IF NOT EXISTS synapse_skills (
                skill_id TEXT PRIMARY KEY,
                name TEXT NOT NULL,
                version TEXT NOT NULL,
                description TEXT,
                author TEXT,
                category TEXT NOT NULL,
                entry_point TEXT NOT NULL,
                runtime TEXT NOT NULL,
                manifest_json TEXT NOT NULL,
                created_at INTEGER NOT NULL,
                updated_at INTEGER NOT NULL,
                UNIQUE(name, version)
            );
            CREATE INDEX IF NOT EXISTS idx_skills_category ON synapse_skills(category);
            CREATE INDEX IF NOT EXISTS idx_skills_runtime ON synapse_skills(runtime);
        "#;

        // 2. synapse_skill_instances
        let query_instances = r#"
            CREATE TABLE IF NOT EXISTS synapse_skill_instances (
                instance_id TEXT PRIMARY KEY,
                skill_id TEXT NOT NULL,
                agent_id TEXT NOT NULL,
                enabled INTEGER NOT NULL DEFAULT 1,
                configuration_json TEXT,
                installed_at INTEGER NOT NULL,
                last_used INTEGER,
                usage_count INTEGER NOT NULL DEFAULT 0,
                error_count INTEGER NOT NULL DEFAULT 0,
                FOREIGN KEY (skill_id) REFERENCES synapse_skills(skill_id) ON DELETE CASCADE,
                UNIQUE(skill_id, agent_id)
            );
            CREATE INDEX IF NOT EXISTS idx_instances_agent ON synapse_skill_instances(agent_id);
            CREATE INDEX IF NOT EXISTS idx_instances_skill ON synapse_skill_instances(skill_id);
        "#;

        // 3. synapse_skill_permissions
        let query_permissions = r#"
            CREATE TABLE IF NOT EXISTS synapse_skill_permissions (
                permission_id TEXT PRIMARY KEY,
                skill_id TEXT NOT NULL,
                permission_type TEXT NOT NULL,
                scope TEXT NOT NULL,
                justification TEXT,
                granted INTEGER NOT NULL DEFAULT 0,
                granted_at INTEGER,
                FOREIGN KEY (skill_id) REFERENCES synapse_skills(skill_id) ON DELETE CASCADE
            );
            CREATE INDEX IF NOT EXISTS idx_permissions_skill ON synapse_skill_permissions(skill_id);
        "#;

        // 4. synapse_skill_executions
        let query_executions = r#"
            CREATE TABLE IF NOT EXISTS synapse_skill_executions (
                execution_id TEXT PRIMARY KEY,
                skill_id TEXT NOT NULL,
                instance_id TEXT NOT NULL,
                agent_id TEXT NOT NULL,
                status TEXT NOT NULL,
                inputs_json TEXT,
                outputs_json TEXT,
                error TEXT,
                started_at INTEGER NOT NULL,
                completed_at INTEGER,
                duration_ms INTEGER,
                memory_mb REAL,
                triggered_by TEXT NOT NULL,
                parent_execution_id TEXT,
                FOREIGN KEY (skill_id) REFERENCES synapse_skills(skill_id) ON DELETE CASCADE,
                FOREIGN KEY (instance_id) REFERENCES synapse_skill_instances(instance_id) ON DELETE CASCADE
            );
            CREATE INDEX IF NOT EXISTS idx_executions_skill ON synapse_skill_executions(skill_id);
            CREATE INDEX IF NOT EXISTS idx_executions_agent ON synapse_skill_executions(agent_id);
            CREATE INDEX IF NOT EXISTS idx_executions_started ON synapse_skill_executions(started_at);
        "#;

        // Execute queries sequentially
        for q in [
            query_skills,
            query_instances,
            query_permissions,
            query_executions,
        ] {
            sqlx::query(q)
                .execute(&db.write_pool)
                .await
                .map_err(|e| format!("Failed to initialize skill schema: {e}"))?;
        }

        Ok(())
    }

    // Register a new skill from a manifest
    pub async fn register_skill(&self, manifest: SkillManifest) -> Result<String, String> {
        let db = self.db.read().await;
        let db = db.as_ref().ok_or("Database not initialized")?;

        let manifest_json = serde_json::to_string(&manifest)
            .map_err(|e| format!("Failed to serialize manifest: {e}"))?;
        let now = chrono::Utc::now().timestamp();

        let query = r#"
            INSERT INTO synapse_skills (
                skill_id, name, version, description, author, category, 
                entry_point, runtime, manifest_json, created_at, updated_at
            ) VALUES (?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?)
            ON CONFLICT(skill_id) DO UPDATE SET
                name=excluded.name,
                version=excluded.version,
                description=excluded.description,
                manifest_json=excluded.manifest_json,
                updated_at=excluded.updated_at
        "#;

        sqlx::query(query)
            .bind(&manifest.skill_id)
            .bind(&manifest.name)
            .bind(&manifest.version)
            .bind(&manifest.description)
            .bind(&manifest.author)
            .bind(serde_json::to_string(&manifest.category).unwrap_or_default())
            .bind(&manifest.entry_point)
            .bind(serde_json::to_string(&manifest.runtime).unwrap_or_default())
            .bind(&manifest_json)
            .bind(now) // created_at
            .bind(now) // updated_at
            .execute(&db.write_pool)
            .await
            .map_err(|e| format!("Failed to register skill: {e}"))?;

        Ok(manifest.skill_id)
    }

    // Retrieve a skill manifest by ID
    pub async fn get_skill(&self, skill_id: &str) -> Result<SkillManifest, String> {
        let db = self.db.read().await;
        let db = db.as_ref().ok_or("Database not initialized")?;

        let query = "SELECT manifest_json FROM synapse_skills WHERE skill_id = ?";
        let row: (String,) = sqlx::query_as(query)
            .bind(skill_id)
            .fetch_optional(&db.read_pool)
            .await
            .map_err(|e| format!("Database error: {e}"))?
            .ok_or_else(|| format!("Skill not found: {}", skill_id))?;

        let manifest: SkillManifest = serde_json::from_str(&row.0)
            .map_err(|e| format!("Failed to parse skill manifest: {e}"))?;

        Ok(manifest)
    }

    // Placeholder methods for registry operations (to be implemented)
    pub async fn list_skills(&self) -> Result<Vec<SkillManifest>, String> {
        let db = self.db.read().await;
        let db = db.as_ref().ok_or("Database not initialized")?;

        let query = "SELECT manifest_json FROM synapse_skills ORDER BY name ASC";
        let rows: Vec<(String,)> = sqlx::query_as(query)
            .fetch_all(&db.read_pool)
            .await
            .map_err(|e| format!("Database error: {e}"))?;

        let skills: Result<Vec<SkillManifest>, _> = rows
            .into_iter()
            .map(|r| serde_json::from_str(&r.0))
            .collect();

        skills.map_err(|e| format!("Failed to parse one or more skill manifests: {e}"))
    }
}

