use serde::{Deserialize, Serialize};
use std::collections::HashMap;

#[derive(Debug, Clone, Serialize, Deserialize, PartialEq, Eq)]
pub struct SkillManifest {
    pub id: String,
    pub name: String,
    pub version: String,
    pub description: String,
    pub persona_scope: Vec<String>,
    pub domain_tags: Vec<String>,
    pub trust_level: String,
    pub source: String,
    pub triggers: Vec<String>,
    pub constraints: Vec<String>,
    pub inputs: Vec<String>,
    pub outputs: Vec<String>,
}

#[derive(Debug, Default)]
pub struct SkillRegistry {
    skills: HashMap<String, HashMap<String, SkillManifest>>,
}

impl SkillRegistry {
    pub fn new() -> Self {
        Self {
            skills: HashMap::new(),
        }
    }

    pub fn register_skill(&mut self, manifest: SkillManifest) -> Result<(), String> {
        if manifest.id.trim().is_empty() {
            return Err("Skill id cannot be empty".to_string());
        }
        if manifest.version.trim().is_empty() {
            return Err("Skill version cannot be empty".to_string());
        }

        let id = manifest.id.clone();
        let version = manifest.version.clone();
        let versions = self.skills.entry(id.clone()).or_insert_with(HashMap::new);
        if versions.contains_key(&version) {
            return Err(format!("Skill already registered: {id}@{version}"));
        }
        versions.insert(version, manifest);
        Ok(())
    }

    pub fn get_skill(&self, id: &str, version: &str) -> Option<&SkillManifest> {
        self.skills.get(id).and_then(|versions| versions.get(version))
    }

    pub fn list_skills(&self) -> Vec<&SkillManifest> {
        self.skills
            .values()
            .flat_map(|versions| versions.values())
            .collect()
    }
}

