use serde::{Deserialize, Serialize};
use serde_json::Value as JsonValue;
use sqlx::FromRow;
use std::str::FromStr;

// ============================================================================
// Skill Manifest & Type System
// ============================================================================

#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct SkillManifest {
    pub skill_id: String,
    pub name: String,
    pub version: String,
    pub description: String,
    pub author: Option<String>,
    pub category: SkillCategory,
    // Execution metadata
    pub entry_point: String,
    pub runtime: SkillRuntime,
    // Resource requirements
    pub permissions: Vec<SkillPermission>,
    pub required_tools: Vec<String>,
    pub dependencies: Vec<SkillDependency>,
    // Configuration
    pub parameters: Vec<SkillParameter>,
    pub output_schema: JsonValue, // JsonObject
    // Metadata
    pub tags: Vec<String>,
    pub compatibility: Vec<String>,
    #[serde(skip_serializing_if = "Option::is_none")]
    pub created_at: Option<String>,
    #[serde(skip_serializing_if = "Option::is_none")]
    pub updated_at: Option<String>,
}

#[derive(Debug, Clone, Serialize, Deserialize, PartialEq)]
#[serde(rename_all = "snake_case")]
pub enum SkillCategory {
    Productivity,
    Communication,
    Analysis,
    Automation,
    Integration,
    Memory,
    Utility,
    Other(String),
}

#[derive(Debug, Clone, Serialize, Deserialize, PartialEq)]
#[serde(rename_all = "kebab-case")] // e.g. rust-wasm
pub enum SkillRuntime {
    Python,
    Javascript,
    RustWasm,
}

#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct SkillPermission {
    pub type_: String, // "type" is reserved in Rust
    pub scope: String,
    pub justification: String,
}

#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct SkillDependency {
    pub name: String,
    pub version: String,
    pub type_: String, // npm, pip, system
}

#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct SkillParameter {
    pub name: String,
    pub type_: String, // string, number, etc.
    pub description: String,
    pub required: bool,
    pub default: Option<JsonValue>,
    pub validation: Option<JsonValue>,
}

// ============================================================================
// Database & Instance Models
// ============================================================================

#[derive(Debug, Clone, FromRow, Serialize, Deserialize)]
pub struct SkillInstance {
    pub instance_id: String,
    pub skill_id: String,
    pub agent_id: String,
    pub enabled: bool,
    pub configuration: JsonValue, // stored as TEXT/JSON in DB
    pub installed_at: String,
    pub last_used: Option<String>,
    pub usage_count: i64,
    pub error_count: i64,
}

#[derive(Debug, Clone, FromRow)] // Helper for SQLx
pub struct SkillInstanceRow {
    pub instance_id: String,
    pub skill_id: String,
    pub agent_id: String,
    pub enabled: bool,
    pub configuration_json: String,
    pub installed_at: i64,
    pub last_used: Option<i64>,
    pub usage_count: i64,
    pub error_count: i64,
}

// ============================================================================
// Execution Types
// ============================================================================

#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct ExecutionResult {
    pub execution_id: String,
    pub status: ExecutionStatus,
    pub outputs: Option<JsonValue>,
    pub error: Option<String>,
    pub duration_ms: i64,
}

#[derive(Debug, Clone, Serialize, Deserialize, PartialEq)]
#[serde(rename_all = "lowercase")]
pub enum ExecutionStatus {
    Pending,
    Running,
    Completed,
    Failed,
    Cancelled,
}

impl std::fmt::Display for ExecutionStatus {
    fn fmt(&self, f: &mut std::fmt::Formatter<'_>) -> std::fmt::Result {
        match self {
            ExecutionStatus::Pending => write!(f, "pending"),
            ExecutionStatus::Running => write!(f, "running"),
            ExecutionStatus::Completed => write!(f, "completed"),
            ExecutionStatus::Failed => write!(f, "failed"),
            ExecutionStatus::Cancelled => write!(f, "cancelled"),
        }
    }
}

impl std::str::FromStr for ExecutionStatus {
    type Err = String;

    fn from_str(s: &str) -> Result<Self, Self::Err> {
        match s.to_lowercase().as_str() {
            "pending" => Ok(ExecutionStatus::Pending),
            "running" => Ok(ExecutionStatus::Running),
            "completed" => Ok(ExecutionStatus::Completed),
            "failed" => Ok(ExecutionStatus::Failed),
            "cancelled" => Ok(ExecutionStatus::Cancelled),
            _ => Err(format!("Unknown execution status: {}", s)),
        }
    }
}

#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct ExecutionRecord {
    pub execution_id: String,
    pub skill_id: String,
    pub instance_id: String,
    pub agent_id: String,
    pub status: ExecutionStatus,
    pub inputs: JsonValue,
    pub outputs: Option<JsonValue>,
    pub error: Option<String>,
    pub started_at: String,
    pub completed_at: Option<String>,
    pub duration_ms: i64,
    pub triggered_by: String,
}

#[derive(Debug, Clone, FromRow)]
pub struct ExecutionRecordRow {
    pub execution_id: String,
    pub skill_id: String,
    pub instance_id: String,
    pub agent_id: String,
    pub status: String,
    pub inputs_json: String,
    pub outputs_json: Option<String>,
    pub error: Option<String>,
    pub started_at: i64,
    pub completed_at: Option<i64>,
    pub duration_ms: i64,
    pub triggered_by: String,
}

impl ExecutionRecordRow {
    pub fn to_record(self) -> Result<ExecutionRecord, String> {
        let status = ExecutionStatus::from_str(&self.status)?;
        let inputs: JsonValue = serde_json::from_str(&self.inputs_json)
            .map_err(|e| format!("Failed to parse inputs: {e}"))?;
        let outputs = self
            .outputs_json
            .map(|o| serde_json::from_str(&o))
            .transpose()
            .map_err(|e| format!("Failed to parse outputs: {e}"))?;

        Ok(ExecutionRecord {
            execution_id: self.execution_id,
            skill_id: self.skill_id,
            instance_id: self.instance_id,
            agent_id: self.agent_id,
            status,
            inputs,
            outputs,
            error: self.error,
            started_at: format_timestamp(self.started_at),
            completed_at: self.completed_at.map(format_timestamp),
            duration_ms: self.duration_ms,
            triggered_by: self.triggered_by,
        })
    }
}

#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct ExecutionLog {
    pub log_id: String,
    pub execution_id: String,
    pub timestamp: String,
    pub level: String,
    pub message: String,
    pub context: Option<JsonValue>,
}

#[derive(Debug, Clone, FromRow)]
pub struct ExecutionLogRow {
    pub log_id: String,
    pub execution_id: String,
    pub timestamp: i64,
    pub level: String,
    pub message: String,
    pub context_json: Option<String>,
}

impl ExecutionLogRow {
    pub fn to_log(self) -> Result<ExecutionLog, String> {
        let context = self
            .context_json
            .map(|c| serde_json::from_str(&c))
            .transpose()
            .map_err(|e| format!("Failed to parse log context: {e}"))?;

        Ok(ExecutionLog {
            log_id: self.log_id,
            execution_id: self.execution_id,
            timestamp: format_timestamp(self.timestamp),
            level: self.level,
            message: self.message,
            context,
        })
    }
}

fn format_timestamp(ts: i64) -> String {
    chrono::DateTime::from_timestamp(ts, 0)
        .unwrap_or_else(|| chrono::Utc::now())
        .to_rfc3339()
}

