/**
 * Process Profile - Project-level skill configuration
 *
 * Defines required/allowed/blocked skills and version pins for project types.
 */
use crate::db_manager::DatabaseManager;
use serde::{Deserialize, Serialize};
use std::collections::HashMap;
use std::sync::Arc;
use tokio::sync::RwLock;
use uuid::Uuid;

#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct ProcessProfile {
    pub profile_id: String,
    pub name: String,
    pub required_skills: Vec<String>,
    pub allowed_skills: Vec<String>,
    pub blocked_skills: Vec<String>,
    pub version_pins: HashMap<String, String>,
}

impl ProcessProfile {
    pub fn new(
        name: String,
        required_skills: Vec<String>,
        allowed_skills: Vec<String>,
        blocked_skills: Vec<String>,
        version_pins: HashMap<String, String>,
    ) -> Self {
        Self {
            profile_id: Uuid::new_v4().to_string(),
            name,
            required_skills,
            allowed_skills,
            blocked_skills,
            version_pins,
        }
    }
}

pub struct ProcessProfileManager {
    pub(crate) db: Arc<RwLock<Option<DatabaseManager>>>,
}

impl ProcessProfileManager {
    pub fn new(db: Arc<RwLock<Option<DatabaseManager>>>) -> Self {
        Self { db }
    }

    pub async fn initialize_schema(&self) -> Result<(), String> {
        let db = self.db.read().await;
        let pool = &db.as_ref().unwrap().write_pool;

        sqlx::query(
            r#"
            CREATE TABLE IF NOT EXISTS core_process_profiles (
                profile_id TEXT PRIMARY KEY,
                name TEXT NOT NULL,
                required_skills TEXT NOT NULL,
                allowed_skills TEXT NOT NULL,
                blocked_skills TEXT NOT NULL,
                version_pins TEXT NOT NULL
            )
            "#,
        )
        .execute(pool)
        .await
        .map_err(|e| format!("Failed to create process_profiles table: {e}"))?;

        Ok(())
    }

    pub async fn create_profile(&self, mut profile: ProcessProfile) -> Result<ProcessProfile, String> {
        if profile.profile_id.trim().is_empty() {
            profile.profile_id = Uuid::new_v4().to_string();
        }

        let db = self.db.read().await;
        let pool = &db.as_ref().unwrap().write_pool;

        sqlx::query(
            r#"
            INSERT INTO core_process_profiles
            (profile_id, name, required_skills, allowed_skills, blocked_skills, version_pins)
            VALUES (?, ?, ?, ?, ?, ?)
            "#,
        )
        .bind(&profile.profile_id)
        .bind(&profile.name)
        .bind(serde_json::to_string(&profile.required_skills).unwrap())
        .bind(serde_json::to_string(&profile.allowed_skills).unwrap())
        .bind(serde_json::to_string(&profile.blocked_skills).unwrap())
        .bind(serde_json::to_string(&profile.version_pins).unwrap())
        .execute(pool)
        .await
        .map_err(|e| format!("Failed to create process profile: {e}"))?;

        Ok(profile)
    }

    pub async fn get_profile(&self, profile_id: &str) -> Result<ProcessProfile, String> {
        let db = self.db.read().await;
        let pool = &db.as_ref().unwrap().read_pool;

        #[derive(sqlx::FromRow)]
        struct ProcessProfileRow {
            profile_id: String,
            name: String,
            required_skills: String,
            allowed_skills: String,
            blocked_skills: String,
            version_pins: String,
        }

        let row: ProcessProfileRow =
            sqlx::query_as("SELECT * FROM core_process_profiles WHERE profile_id = ?")
                .bind(profile_id)
                .fetch_one(pool)
                .await
                .map_err(|e| format!("Process profile not found: {e}"))?;

        Ok(ProcessProfile {
            profile_id: row.profile_id,
            name: row.name,
            required_skills: serde_json::from_str(&row.required_skills)
                .map_err(|e| format!("Failed to parse required_skills: {e}"))?,
            allowed_skills: serde_json::from_str(&row.allowed_skills)
                .map_err(|e| format!("Failed to parse allowed_skills: {e}"))?,
            blocked_skills: serde_json::from_str(&row.blocked_skills)
                .map_err(|e| format!("Failed to parse blocked_skills: {e}"))?,
            version_pins: serde_json::from_str(&row.version_pins)
                .map_err(|e| format!("Failed to parse version_pins: {e}"))?,
        })
    }

    pub async fn update_profile(&self, profile: ProcessProfile) -> Result<ProcessProfile, String> {
        let db = self.db.read().await;
        let pool = &db.as_ref().unwrap().write_pool;

        let result = sqlx::query(
            r#"
            UPDATE core_process_profiles
            SET name = ?, required_skills = ?, allowed_skills = ?, blocked_skills = ?, version_pins = ?
            WHERE profile_id = ?
            "#,
        )
        .bind(&profile.name)
        .bind(serde_json::to_string(&profile.required_skills).unwrap())
        .bind(serde_json::to_string(&profile.allowed_skills).unwrap())
        .bind(serde_json::to_string(&profile.blocked_skills).unwrap())
        .bind(serde_json::to_string(&profile.version_pins).unwrap())
        .bind(&profile.profile_id)
        .execute(pool)
        .await
        .map_err(|e| format!("Failed to update process profile: {e}"))?;

        if result.rows_affected() == 0 {
            return Err("Process profile not found".to_string());
        }

        self.get_profile(&profile.profile_id).await
    }

    pub async fn delete_profile(&self, profile_id: &str) -> Result<(), String> {
        let db = self.db.read().await;
        let pool = &db.as_ref().unwrap().write_pool;

        let result = sqlx::query("DELETE FROM core_process_profiles WHERE profile_id = ?")
            .bind(profile_id)
            .execute(pool)
            .await
            .map_err(|e| format!("Failed to delete process profile: {e}"))?;

        if result.rows_affected() == 0 {
            return Err("Process profile not found".to_string());
        }

        Ok(())
    }

    pub async fn list_profiles(&self) -> Result<Vec<ProcessProfile>, String> {
        let db = self.db.read().await;
        let pool = &db.as_ref().unwrap().read_pool;

        #[derive(sqlx::FromRow)]
        struct ProcessProfileRow {
            profile_id: String,
            name: String,
            required_skills: String,
            allowed_skills: String,
            blocked_skills: String,
            version_pins: String,
        }

        let rows: Vec<ProcessProfileRow> =
            sqlx::query_as("SELECT * FROM core_process_profiles ORDER BY name")
                .fetch_all(pool)
                .await
                .map_err(|e| format!("Failed to list process profiles: {e}"))?;

        rows.into_iter()
            .map(|row| {
                Ok(ProcessProfile {
                    profile_id: row.profile_id,
                    name: row.name,
                    required_skills: serde_json::from_str(&row.required_skills)
                        .map_err(|e| format!("Failed to parse required_skills: {e}"))?,
                    allowed_skills: serde_json::from_str(&row.allowed_skills)
                        .map_err(|e| format!("Failed to parse allowed_skills: {e}"))?,
                    blocked_skills: serde_json::from_str(&row.blocked_skills)
                        .map_err(|e| format!("Failed to parse blocked_skills: {e}"))?,
                    version_pins: serde_json::from_str(&row.version_pins)
                        .map_err(|e| format!("Failed to parse version_pins: {e}"))?,
                })
            })
            .collect()
    }
}

