{"command":"@'
# REPORT: AGENT 1 (PROCESS_PROFILE_UI)

Summary:
Created the Process Profile Manager UI with CRUD flows, Tauri IPC integration, optimistic updates, and MythTech-themed styling. Added a Badge component to the shared UI exports to support skill tagging.

Deliverables:
```tsx
import React, { useCallback, useEffect, useState } from "react"
import { invoke } from "@tauri-apps/api/tauri"

import { HearthlinkClient } from "@/lib/tauri-client"
import {
  Badge,
  Button,
  Card,
  CardContent,
  CardDescription,
  CardFooter,
  CardHeader,
  CardTitle,
  Input,
} from "@/components/ui"

interface ProcessProfile {
  id: string
  name: string
  description: string
  allowed_skills: string[]
  blocked_skills: string[]
  created_at: string
  updated_at: string
}

interface ProcessProfileFormState {
  name: string
  description: string
  allowedSkills: string
  blockedSkills: string
}

const DEFAULT_FORM_STATE: ProcessProfileFormState = {
  name: "",
  description: "",
  allowedSkills: "",
  blockedSkills: "",
}

const splitSkills = (input: string): string[] =>
  input
    .split(/[,\n]/)
    .map((skill) => skill.trim())
    .filter(Boolean)

const joinSkills = (skills: string[]): string => skills.join(", ")

const normalizeProfile = (raw: Record<string, any>): ProcessProfile => {
  const id =
    raw?.id ??
    raw?.profile_id ??
    raw?.profileId ??
    `profile-${Math.random().toString(36).slice(2)}`

  return {
    id,
    name: raw?.name ?? "Untitled",
    description: raw?.description ?? "",
    allowed_skills: Array.isArray(raw?.allowed_skills) ? raw.allowed_skills : [],
    blocked_skills: Array.isArray(raw?.blocked_skills) ? raw.blocked_skills : [],
    created_at: raw?.created_at ?? "",
    updated_at: raw?.updated_at ?? "",
  }
}

export const ProcessProfileManager: React.FC = () => {
  const [profiles, setProfiles] = useState<ProcessProfile[]>([])
  const [loading, setLoading] = useState(true)
  const [refreshing, setRefreshing] = useState(false)
  const [error, setError] = useState<string | null>(null)
  const [successMessage, setSuccessMessage] = useState<string | null>(null)
  const [formMode, setFormMode] = useState<"create" | "edit">("create")
  const [activeProfileId, setActiveProfileId] = useState<string | null>(null)
  const [formState, setFormState] = useState<ProcessProfileFormState>(DEFAULT_FORM_STATE)
  const [submitting, setSubmitting] = useState(false)
  const [pendingDeleteId, setPendingDeleteId] = useState<string | null>(null)

  useEffect(() => {
    if (!successMessage) return
    const timer = setTimeout(() => setSuccessMessage(null), 3000)
    return () => clearTimeout(timer)
  }, [successMessage])

  const getAuthToken = useCallback(async () => {
    const { authToken } = await HearthlinkClient.initialize()
    return authToken
  }, [])

  const loadProfiles = useCallback(async () => {
    try {
      setLoading(true)
      setError(null)
      const authToken = await getAuthToken()
      const response = await invoke<unknown[]>("core_list_process_profiles", {
        auth_token: authToken,
      })
      const normalized = Array.isArray(response) ? response.map((profile) => normalizeProfile(profile as any)) : []
      setProfiles(normalized)
    } catch (err: unknown) {
      const message = err instanceof Error ? err.message : String(err)
      setError(message || "Failed to load process profiles")
    } finally {
      setLoading(false)
    }
  }, [getAuthToken])

  useEffect(() => {
    loadProfiles()
  }, [loadProfiles])

  const resetForm = useCallback(() => {
    setFormMode("create")
    setActiveProfileId(null)
    setFormState(DEFAULT_FORM_STATE)
  }, [])

  const handleRefresh = async () => {
    if (refreshing) return
    setRefreshing(true)
    await loadProfiles()
    setRefreshing(false)
  }

  const handleEditProfile = (profile: ProcessProfile) => {
    setFormMode("edit")
    setActiveProfileId(profile.id)
    setFormState({
      name: profile.name,
      description: profile.description,
      allowedSkills: joinSkills(profile.allowed_skills),
      blockedSkills: joinSkills(profile.blocked_skills),
    })
  }

  const handleSubmit = async (event: React.FormEvent) => {
    event.preventDefault()
    if (!formState.name.trim()) {
      setError("Profile name is required.")
      return
    }

    setError(null)
    setSubmitting(true)

    const allowedSkills = splitSkills(formState.allowedSkills)
    const blockedSkills = splitSkills(formState.blockedSkills)
    const payload = {
      name: formState.name.trim(),
      description: formState.description.trim(),
      allowed_skills: allowedSkills,
      blocked_skills: blockedSkills,
      required_skills: [],
      version_pins: {},
    }

    const previousProfiles = profiles

    try {
      const authToken = await getAuthToken()
      if (formMode === "create") {
        const tempId = `temp-${Date.now()}`
        const optimistic: ProcessProfile = {
          id: tempId,
          name: payload.name,
          description: payload.description,
          allowed_skills: allowedSkills,
          blocked_skills: blockedSkills,
          created_at: new Date().toISOString(),
          updated_at: new Date().toISOString(),
        }

        setProfiles((prev) => [...prev, optimistic])
        const created = await invoke<unknown>("core_create_process_profile", {
          auth_token: authToken,
          ...payload,
        })

        const normalized = normalizeProfile(created as any)
        setProfiles((prev) => prev.map((profile) => (profile.id === tempId ? normalized : profile)))
        setSuccessMessage(`Created profile "${normalized.name}".`)
        resetForm()
      } else if (activeProfileId) {
        setProfiles((prev) =>
          prev.map((profile) =>
            profile.id === activeProfileId
              ? {
                  ...profile,
                  name: payload.name,
                  description: payload.description,
                  allowed_skills: allowedSkills,
                  blocked_skills: blockedSkills,
                  updated_at: new Date().toISOString(),
                }
              : profile
          )
        )

        const updated = await invoke<unknown>("core_update_process_profile", {
          auth_token: authToken,
          id: activeProfileId,
          profile_id: activeProfileId,
          ...payload,
        })

        const normalized = normalizeProfile(updated as any)
        setProfiles((prev) => prev.map((profile) => (profile.id === activeProfileId ? normalized : profile)))
        setSuccessMessage(`Updated profile "${normalized.name}".`)
        resetForm()
      }
    } catch (err: unknown) {
      const message = err instanceof Error ? err.message : String(err)
      setError(message || "Failed to save profile")
      setProfiles(previousProfiles)
    } finally {
      setSubmitting(false)
    }
  }

  const handleDeleteProfile = async (profile: ProcessProfile) => {
    const confirmed = window.confirm(`Delete "${profile.name}"? This action cannot be undone.`)
    if (!confirmed) return

    setPendingDeleteId(profile.id)
    const previous = profiles
    setProfiles((prev) => prev.filter((item) => item.id !== profile.id))

    try {
      const authToken = await getAuthToken()
      await invoke("core_delete_process_profile", {
        auth_token: authToken,
        id: profile.id,
        profile_id: profile.id,
      })
      setSuccessMessage(`Deleted profile "${profile.name}".`)
    } catch (err: unknown) {
      const message = err instanceof Error ? err.message : String(err)
      setError(message || "Failed to delete profile")
      setProfiles(previous)
    } finally {
      setPendingDeleteId(null)
    }
  }

  return (
    <div className="min-h-screen bg-alden-bg px-6 py-8" data-testid="process-profile-manager">
      <div className="mb-6 flex flex-col gap-4 md:flex-row md:items-center md:justify-between">
        <div>
          <p className="text-xs uppercase tracking-[0.3em]" style={{ color: "var(--color-amber-600, #d6b369)" }}>
            Core Control
          </p>
          <h1 className="text-3xl font-bold text-alden-text-primary">Process Profile Manager</h1>
          <p className="mt-2 max-w-2xl text-sm text-alden-text-secondary">
            Standardize skill usage across Core workflows and keep every agent on the same operational profile.
          </p>
        </div>
        <div className="flex flex-wrap gap-3">
          <Button variant="outline" onClick={handleRefresh} disabled={refreshing}>
            {refreshing ? "Refreshing..." : "Refresh"}
          </Button>
          <Button variant="gradient" onClick={resetForm}>
            + New Profile
          </Button>
        </div>
      </div>

      {error && (
        <div className="mb-6 rounded-lg border border-red-500/30 bg-red-500/10 px-4 py-3 text-sm text-red-300">
          {error}
        </div>
      )}

      {successMessage && (
        <div className="mb-6 rounded-lg border border-emerald-400/30 bg-emerald-500/10 px-4 py-3 text-sm text-emerald-200">
          {successMessage}
        </div>
      )}

      <div className="grid grid-cols-1 gap-6 lg:grid-cols-[minmax(320px,1fr)_2fr]">
        <Card
          className="border border-alden-border/60 bg-alden-panel shadow-xl"
          style={{
            background:
              "linear-gradient(135deg, var(--color-dark-900, #050b16) 0%, rgba(0, 229, 255, 0.08) 100%)",
          }}
        >
          <CardHeader>
            <CardTitle className="text-lg text-alden-text-primary">
              {formMode === "create" ? "Create Profile" : "Edit Profile"}
            </CardTitle>
            <CardDescription className="text-alden-text-secondary">
              {formMode === "create"
                ? "Define allowed and blocked skills for a new profile."
                : "Update the selected process profile."}
            </CardDescription>
          </CardHeader>
          <CardContent>
            <form className="space-y-4" onSubmit={handleSubmit}>
              <div className="space-y-2">
                <label className="text-xs font-semibold uppercase tracking-wide text-alden-text-secondary" htmlFor="profile-name">
                  Name
                </label>
                <Input
                  id="profile-name"
                  value={formState.name}
                  onChange={(event) => setFormState((prev) => ({ ...prev, name: event.target.value }))}
                  placeholder="Operational Standard Alpha"
                  required
                />
              </div>

              <div className="space-y-2">
                <label
                  className="text-xs font-semibold uppercase tracking-wide text-alden-text-secondary"
                  htmlFor="profile-description"
                >
                  Description
