import React, { useState, useEffect } from 'react';
import { invoke } from '@tauri-apps/api/tauri';
import { CheckCircle, AlertTriangle, XCircle, Loader2 } from 'lucide-react';

// --- Types ---

type DriftStatusType = 'Allowed' | 'Warning' | 'Blocked';

export type DriftResult = 
  | { type: 'Allowed' }
  | { type: 'Warning'; message: string }
  | { type: 'Blocked'; message: string };

interface DriftIndicatorProps {
  agentId: string;
  skillId: string;
  /** Optional: pass result directly to avoid fetch */
  driftResult?: DriftResult;
  /** Size of the icon in pixels */
  size?: number;
  /** Show label next to icon? */
  showLabel?: boolean;
  className?: string;
}

// --- Cache ---
// Simple in-memory cache to avoid repeated calls for the same agent/skill pair in a session.
// Key: `${agentId}:${skillId}`
const driftCache = new Map<string, DriftResult>();

// --- Component ---

export const DriftIndicator: React.FC<DriftIndicatorProps> = ({
  agentId,
  skillId,
  driftResult: initialResult,
  size = 16,
  showLabel = false,
  className = '',
}) => {
  const [result, setResult] = useState<DriftResult | null>(initialResult || null);
  const [loading, setLoading] = useState<boolean>(!initialResult);

  useEffect(() => {
    // If we have a direct result, use it and skip fetch
    if (initialResult) {
      setResult(initialResult);
      setLoading(false);
      return;
    }

    const cacheKey = `${agentId}:${skillId}`;
    
    // Check cache first
    if (driftCache.has(cacheKey)) {
      setResult(driftCache.get(cacheKey)!);
      setLoading(false);
      return;
    }

    let isMounted = true;

    const checkDrift = async () => {
      try {
        setLoading(true);
        // Invoke backend command
        // Note: Assuming the backend returns the tagged union as specified. 
        // Rust often serializes enums as { "Allowed": null } or "Allowed" depending on serde settings.
        // We handle the shape specified in the prompt.
        const response = await invoke<DriftResult>('core_check_skill_drift', {
          agentId,
          skillId,
        });

        if (isMounted) {
          setResult(response);
          // Update cache
          driftCache.set(cacheKey, response);
        }
      } catch (error) {
        console.error('Failed to check drift status:', error);
        // Fallback to Warning on error to be safe but not blocking? Or just Blocked?
        // Let's assume Warning for UI resilience but log it.
        if (isMounted) {
            const errorResult: DriftResult = { 
                type: 'Warning', 
                message: 'Failed to verify skill drift status' 
            };
            setResult(errorResult);
        }
      } finally {
        if (isMounted) {
          setLoading(false);
        }
      }
    };

    checkDrift();

    return () => {
      isMounted = false;
    };
  }, [agentId, skillId, initialResult]);

  // --- Render Helpers ---

  if (loading) {
    return (
      <div 
        className={`inline-flex items-center justify-center opacity-50 ${className}`}
        title="Verifying skill alignment..."
      >
        <Loader2 size={size} className="animate-spin text-mythtech-cyan" />
      </div>
    );
  }

  if (!result) return null;

  const renderIcon = () => {
    switch (result.type) {
      case 'Allowed':
        return (
          <CheckCircle 
            size={size} 
            className="text-emerald-400 drop-shadow-[0_0_2px_rgba(52,211,153,0.5)]" 
          />
        );
      case 'Warning':
        return (
          <AlertTriangle 
            size={size} 
            className="text-amber-400 drop-shadow-[0_0_3px_rgba(251,191,36,0.5)] animate-pulse" 
          />
        );
      case 'Blocked':
        return (
          <XCircle 
            size={size} 
            className="text-rose-500 drop-shadow-[0_0_4px_rgba(244,63,94,0.6)]" 
          />
        );
    }
  };

  const statusColors = {
    Allowed: 'text-emerald-400',
    Warning: 'text-amber-400',
    Blocked: 'text-rose-500',
  };

  const getTooltip = () => {
    switch (result.type) {
      case 'Allowed': return 'Skill Alignment: Allowed';
      case 'Warning': return `Drift Warning: ${result.message}`;
      case 'Blocked': return `Drift Violation: ${result.message}`;
    }
  };

  return (
    <div 
      className={`inline-flex items-center gap-2 transition-all duration-300 ${className}`}
      title={getTooltip()}
      aria-label={getTooltip()}
      data-testid="drift-indicator"
    >
      <div className="relative group cursor-help">
        {renderIcon()}
        
        {/* Glow effect backing */}
        <div className={`absolute inset-0 blur-sm opacity-20 bg-current rounded-full ${statusColors[result.type]}`} />
      </div>
      
      {showLabel && (
        <span className={`text-xs font-medium tracking-wide ${statusColors[result.type]}`}>
          {result.type.toUpperCase()}
        </span>
      )}
    </div>
  );
};

