import React, { useEffect, useMemo, useState, useCallback } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import {
  Brain,
  Check,
  Download,
  Info,
  Loader2,
  Search,
  Shield,
  Star,
  Zap,
  X,
  Code,
  Mic,
  Box,
  Tag,
} from '@/components/ui/HearthlinkIcons';
import { SynapseBridge, SkillManifest, SkillInstance } from '@/bridges/SynapseBridge';
import { AgentSelectorModal } from './AgentSelectorModal';
import { SkillInstancesList } from './SkillInstancesList';

type SkillCategory = 'Automation' | 'Knowledge' | 'Security' | 'Voice' | 'General';

interface SkillCard {
  id: string;
  name: string;
  description: string;
  targetAgent: string;
  capabilities: string[];
  category: SkillCategory;
  rating: number;
  downloads: number;
  tags: string[];
  lastUpdated: string;
}

export interface SkillMarketplaceProps {
  personaId?: string;
  personaName?: string;
  onSkillToggle?: (skillId: string, enabled: boolean) => void;
}

interface SkillCardProps {
  skill: SkillManifest;
  instance?: SkillInstance;
  onToggle: (skillId: string, enabled: boolean) => void;
  isLoading: boolean;
  personaId?: string;
}

const CATEGORY_FILTERS: Array<SkillCategory | 'All'> = ['All', 'Automation', 'Knowledge', 'Security', 'Voice', 'General'];

const SKILL_OVERRIDES: Record<string, Partial<SkillCard>> = {
  'task-management': {
    category: 'Automation',
    tags: ['tasks', 'planning', 'workflow'],
    rating: 4.9,
    downloads: 2890,
    lastUpdated: '2 days ago',
  },
  'knowledge-retrieval': {
    category: 'Knowledge',
    tags: ['search', 'vault', 'summaries'],
    rating: 4.7,
    downloads: 2140,
    lastUpdated: '1 week ago',
  },
  'security-monitoring': {
    category: 'Security',
    tags: ['alerts', 'audit', 'guards'],
    rating: 4.8,
    downloads: 880,
    lastUpdated: '5 days ago',
  },
  'voice-integration': {
    category: 'Voice',
    tags: ['speech', 'hands-free', 'assistant'],
    rating: 4.5,
    downloads: 1320,
    lastUpdated: '3 weeks ago',
  },
  'development-automation': {
    category: 'Automation',
    tags: ['build', 'test', 'deploy'],
    rating: 4.6,
    downloads: 1450,
    lastUpdated: '1 month ago',
  },
  'general-assistance': {
    category: 'General',
    tags: ['general', 'assistant'],
    rating: 4.3,
    downloads: 5100,
    lastUpdated: '2 months ago',
  },
};

const titleCase = (value: string): string =>
  value
    .split('-')
    .map((segment) => segment.charAt(0).toUpperCase() + segment.slice(1))
    .join(' ');

const agentColor = (targetAgent: string) => {
  switch (targetAgent) {
    case 'sentry':
      return 'text-red-400 bg-red-500/10 border-red-500/20';
    case 'vault':
      return 'text-purple-400 bg-purple-500/10 border-purple-500/20';
    case 'synapse':
      return 'text-emerald-400 bg-emerald-500/10 border-emerald-500/20';
    case 'alden':
    default:
      return 'text-alden-gold bg-alden-gold/10 border-alden-gold/20';
  }
};

const categoryIcon = (category: SkillCategory) => {
  switch (category) {
    case 'Knowledge':
      return <Brain size={18} />;
    case 'Security':
      return <Shield size={18} />;
    case 'Voice':
      return <Mic size={18} />;
    case 'Automation':
      return <Code size={18} />;
    default:
      return <Box size={18} />;
  }
};

const categoryAccent = (category: SkillCategory) => {
  switch (category) {
    case 'Knowledge':
      return 'text-purple-300 bg-purple-500/10 border-purple-500/20';
    case 'Security':
      return 'text-red-300 bg-red-500/10 border-red-500/20';
    case 'Voice':
      return 'text-cyan-300 bg-cyan-500/10 border-cyan-500/20';
    case 'Automation':
      return 'text-emerald-300 bg-emerald-500/10 border-emerald-500/20';
    default:
      return 'text-slate-300 bg-white/5 border-white/10';
  }
};

const SKILL_COMPATIBILITY_FALLBACK = 'alden';

const normalizeManifestCategory = (category: SkillManifest['category']): string => {
  if (typeof category === 'string') return category;
  if (category && typeof category === 'object' && 'other' in category && typeof category.other === 'string') {
    return category.other;
  }
  return '';
};

const mapManifestCategory = (category: string): SkillCategory => {
  switch (category.toLowerCase()) {
    case 'analysis':
    case 'memory':
      return 'Knowledge';
    case 'automation':
    case 'productivity':
      return 'Automation';
    case 'communication':
    case 'integration':
      return 'General';
    case 'utility':
      return 'General';
    default:
      return category.includes('voice') ? 'Voice' : 'General';
  }
};

const resolveTargetAgent = (manifest: SkillManifest): string => {
  const allowed = new Set(['alden', 'alice', 'mimic', 'vault', 'synapse', 'sentry', 'core']);
  const compat = Array.isArray(manifest.compatibility) ? manifest.compatibility : [];
  const match = compat.find((entry) => allowed.has(entry.toLowerCase()));
  return match?.toLowerCase() ?? SKILL_COMPATIBILITY_FALLBACK;
};

const resolveCapabilities = (manifest: SkillManifest): string[] => {
  if (manifest.parameters?.length) {
    return manifest.parameters.map((param) => param.name);
  }
  if (manifest.required_tools?.length) {
    return manifest.required_tools;
  }
  return [];
};

const formatTimestamp = (value?: string | null): string => {
  if (!value) return 'recently';
  const parsed = new Date(value);
  if (Number.isNaN(parsed.getTime())) return value;
  return parsed.toLocaleDateString();
};

const mapSkillCard = (manifest: SkillManifest): SkillCard => {
  const overrides = SKILL_OVERRIDES[manifest.skill_id] || {};
  const category = overrides.category || mapManifestCategory(normalizeManifestCategory(manifest.category));

  return {
    id: manifest.skill_id,
    name: overrides.name || manifest.name || titleCase(manifest.skill_id),
    description: overrides.description || manifest.description || 'No description available yet.',
    targetAgent: overrides.targetAgent || resolveTargetAgent(manifest),
    capabilities: overrides.capabilities || resolveCapabilities(manifest),
    category,
    rating: overrides.rating ?? 4.4,
    downloads: overrides.downloads ?? 780,
    tags: overrides.tags ?? manifest.tags ?? [],
    lastUpdated: overrides.lastUpdated ?? formatTimestamp(manifest.updated_at || manifest.created_at),
  };
};

const buildInstanceMap = (instances: SkillInstance[]): Record<string, SkillInstance> => {
  return instances.reduce<Record<string, SkillInstance>>((acc, instance) => {
    const existing = acc[instance.skill_id];
    if (!existing) {
      acc[instance.skill_id] = instance;
      return acc;
    }
    if (existing.installed_at < instance.installed_at) {
      acc[instance.skill_id] = instance;
    }
    return acc;
  }, {});
};

const matchesCompatibility = (manifest: SkillManifest, aiSystem: string): boolean => {
  if (!manifest.compatibility || manifest.compatibility.length === 0) return true;
  return manifest.compatibility.some((entry) => entry.toLowerCase() === aiSystem.toLowerCase());
};

const getCategoryLabel = (category: SkillManifest['category']): string => {
  if (typeof category === 'string') return category;
  if (typeof category === 'object' && 'other' in category) return category.other;
  return 'utility';
};

const getCategoryIconForCard = (category: string): React.ReactNode => {
  switch (category.toLowerCase()) {
    case 'productivity': return <Zap className="w-4 h-4" />;
    case 'security': return <Shield className="w-4 h-4" />;
    case 'automation': return <Code className="w-4 h-4" />;
    default: return <Box className="w-4 h-4" />;
  }
};

const SkillCard: React.FC<SkillCardProps> = ({ skill, instance, onToggle, isLoading, personaId }) => {
  const isEnabled = instance?.enabled ?? false;
  const categoryLabel = getCategoryLabel(skill.category);

  return (
    <div
      className="relative overflow-hidden rounded-xl border-2 transition-all duration-300 hover:scale-[1.02]"
      style={{
        borderColor: isEnabled ? 'var(--mimic-accent)' : 'var(--border)',
        backgroundColor: isEnabled
          ? 'color-mix(in oklch, var(--mimic-accent) 8%, var(--card))'
          : 'var(--card)',
        boxShadow: isEnabled
          ? '0 8px 24px -4px color-mix(in oklch, var(--mimic-accent) 25%, transparent)'
          : 'none',
      }}
    >
      {/* Header */}
      <div className="p-4 pb-2">
        <div className="flex items-start justify-between gap-3">
          <div className="flex items-center gap-3">
            <div
              className="flex items-center justify-center w-10 h-10 rounded-lg"
              style={{
                backgroundColor: 'color-mix(in oklch, var(--mimic-accent) 15%, transparent)',
                color: 'var(--mimic-accent)',
              }}
            >
              {getCategoryIconForCard(categoryLabel)}
            </div>
            <div>
              <h3 className="text-sm font-semibold text-foreground">{skill.name}</h3>
              <p className="text-xs text-muted-foreground">v{skill.version}</p>
            </div>
          </div>
          {/* Toggle Button */}
          <button
            onClick={() => onToggle(skill.skill_id, !isEnabled)}
            disabled={isLoading}
            className="relative flex items-center justify-center w-10 h-6 rounded-full transition-colors disabled:opacity-50"
            style={{
              backgroundColor: isEnabled
                ? 'var(--mimic-accent)'
                : 'var(--muted)',
            }}
            aria-label={isEnabled ? 'Disable skill' : 'Enable skill'}
          >
            <span
              className="absolute w-4 h-4 rounded-full bg-white shadow transition-transform duration-200"
              style={{
                transform: isEnabled ? 'translateX(8px)' : 'translateX(-8px)',
              }}
            />
          </button>
        </div>
      </div>

      {/* Description */}
      <div className="px-4 pb-3">
        <p className="text-xs text-muted-foreground line-clamp-2">{skill.description}</p>
      </div>

      {/* Tags */}
      <div className="px-4 pb-3 flex flex-wrap gap-1.5">
        <span
          className="inline-flex items-center gap-1 text-xs px-2 py-0.5 rounded-full"
          style={{
            backgroundColor: 'color-mix(in oklch, var(--mimic-accent) 12%, transparent)',
            color: 'var(--mimic-accent)',
          }}
        >
          <Tag className="w-3 h-3" />
          {categoryLabel}
        </span>
        {skill.tags.slice(0, 2).map((tag) => (
          <span
            key={tag}
            className="text-xs px-2 py-0.5 rounded-full bg-muted text-muted-foreground"
          >
            {tag}
          </span>
        ))}
      </div>

      {/* Footer Stats */}
      {instance && (
        <div
          className="px-4 py-2 text-xs border-t flex justify-between"
          style={{ borderColor: 'var(--border)' }}
        >
          <span className="text-muted-foreground">
            Used: {instance.usage_count} times
          </span>
          {instance.last_used && (
            <span className="text-muted-foreground">
              Last: {new Date(instance.last_used).toLocaleDateString()}
            </span>
          )}
        </div>
      )}

      {/* Status Badge */}
      {isEnabled && (
        <div
          className="absolute top-2 right-2 flex items-center gap-1 px-2 py-0.5 rounded-full text-xs font-medium"
          style={{
            backgroundColor: 'var(--mimic-accent)',
            color: 'var(--accent-foreground)',
          }}
        >
          <Check className="w-3 h-3" />
          Active
        </div>
      )}
    </div>
  );
};

export const SkillMarketplace: React.FC<SkillMarketplaceProps> = ({
  personaId,
  personaName = 'Persona',
  onSkillToggle,
}) => {
  const [skills, setSkills] = useState<SkillCard[]>([]);
  const [instances, setInstances] = useState<SkillInstance[]>([]);
  const [loading, setLoading] = useState(true);
  const [operatingSkill, setOperatingSkill] = useState<string | null>(null);
  const [error, setError] = useState<string | null>(null);
  const [searchQuery, setSearchQuery] = useState('');
  const [categoryFilter, setCategoryFilter] = useState<string>('all');
  const [activeSkillIds, setActiveSkillIds] = useState<string[]>([]);
  const [selectedSkill, setSelectedSkill] = useState<SkillCard | null>(null);
  const [instancesBySkill, setInstancesBySkill] = useState<Record<string, SkillInstance>>({});
  const [showAgentModal, setShowAgentModal] = useState(false);
  const [skillToActivate, setSkillToActivate] = useState<SkillCard | null>(null);
  const [viewMode, setViewMode] = useState<'marketplace' | 'instances'>('marketplace');
  const [announcement, setAnnouncement] = useState<string>('');
  const [includeSeeding, setIncludeSeeding] = useState(false);

  const bridge = useMemo(() => new SynapseBridge(), []);

  const loadData = useCallback(async () => {
    setLoading(true);
    setError(null);
    try {
      const [skillsRes, instancesRes] = await Promise.all([
        bridge.listSkills(),
        personaId ? bridge.listSkillInstances(personaId) : Promise.resolve({ success: true, data: [] }),
      ]);

      if (skillsRes.success && skillsRes.data) {
        const available = personaId
          ? skillsRes.data.filter((skill) => matchesCompatibility(skill, personaId))
          : skillsRes.data;
        const enriched = available.map(mapSkillCard);
        setSkills(enriched);
      } else {
        setError(skillsRes.error || 'Failed to load skills');
      }

      if (instancesRes.success && instancesRes.data) {
        setInstances(instancesRes.data);
        const instanceMap = buildInstanceMap(instancesRes.data);
        setInstancesBySkill(instanceMap);
        const activeIds = Object.values(instanceMap)
          .filter((instance) => instance.enabled)
          .map((instance) => instance.skill_id);
        setActiveSkillIds(activeIds.filter((id) => enriched.some((skill: SkillCard) => skill.id === id)));
      }
    } catch (err) {
      setError(err instanceof Error ? err.message : 'Failed to load skill data');
    } finally {
      setLoading(false);
    }
  }, [bridge, personaId]);

  useEffect(() => {
    loadData();
  }, [loadData]);

  const handleToggle = useCallback(async (skillId: string, enable: boolean) => {
    setOperatingSkill(skillId);
    setError(null);

    try {
      // Find existing instance for this skill + persona
      const existingInstance = instances.find(
        (i) => i.skill_id === skillId && i.agent_id === personaId
      );

      if (enable) {
        if (existingInstance) {
          // Enable existing instance
          const res = await bridge.enableSkillInstance(existingInstance.instance_id);
          if (res.success && res.data) {
            setInstances((prev) =>
              prev.map((i) =>
                i.instance_id === existingInstance.instance_id ? res.data! : i
              )
            );
          } else {
            setError(res.error || 'Failed to enable skill');
          }
        } else {
          // Create new instance
          const res = await bridge.createSkillInstance(skillId, personaId || 'default', null);
          if (res.success && res.data) {
            // Enable it immediately
            const enableRes = await bridge.enableSkillInstance(res.data.instance_id);
            if (enableRes.success && enableRes.data) {
              setInstances((prev) => [...prev, enableRes.data!]);
            }
          } else {
            setError(res.error || 'Failed to create skill instance');
          }
        }
      } else {
        if (existingInstance) {
          const res = await bridge.disableSkillInstance(existingInstance.instance_id);
          if (res.success && res.data) {
            setInstances((prev) =>
              prev.map((i) =>
                i.instance_id === existingInstance.instance_id ? res.data! : i
              )
            );
          } else {
            setError(res.error || 'Failed to disable skill');
          }
        }
      }

      onSkillToggle?.(skillId, enable);
    } catch (err) {
      setError(err instanceof Error ? err.message : 'Operation failed');
    } finally {
      setOperatingSkill(null);
    }
  }, [bridge, instances, personaId, onSkillToggle]);

  const handleAgentSelection = async (agentId: string) => {
    if (!skillToActivate) return;
    const skillId = skillToActivate.id;
    const skillName = skillToActivate.name;

    setOperatingSkill(skillId);
    setError(null);

    try {
      const created = await bridge.createSkillInstance(skillId, agentId);
      if (!created.success || !created.data) {
        throw new Error(created.error || 'Failed to activate skill.');
      }

      // Refresh instances
      const instancesResponse = await bridge.listSkillInstances(personaId || agentId);
      if (instancesResponse.success && instancesResponse.data) {
        const instanceMap = buildInstanceMap(instancesResponse.data);
        const activeIds = Object.values(instanceMap)
          .filter((instance) => instance.enabled)
          .map((instance) => instance.skill_id);

        setInstancesBySkill(instanceMap);
        setActiveSkillIds(activeIds.filter((id) => skills.some((s) => s.id === id)));
      }

      setAnnouncement(`Skill ${skillName} activated for ${agentId}`);
      setTimeout(() => setAnnouncement(''), 3000);
    } catch (toggleError) {
      console.error('Failed to activate skill:', toggleError);
      setError('Unable to activate skill right now.');
      setAnnouncement(`Error: Failed to activate ${skillName}`);
      setTimeout(() => setAnnouncement(''), 3000);
    } finally {
      setOperatingSkill(null);
      setSkillToActivate(null);
    }
  };

  const handleDeactivateInstance = async (instanceId: string) => {
    try {
      const disabled = await bridge.disableSkillInstance(instanceId);
      if (!disabled.success) throw new Error(disabled.error);

      // Refresh
      const instancesResponse = await bridge.listSkillInstances(personaId || 'default');
      if (instancesResponse.success && instancesResponse.data) {
        const instanceMap = buildInstanceMap(instancesResponse.data);
        setInstancesBySkill(instanceMap);
        // Update active IDs
        const activeIds = Object.values(instanceMap)
          .filter((instance) => instance.enabled)
          .map((instance) => instance.skill_id);
        setActiveSkillIds(activeIds.filter((id) => skills.some((s) => s.id === id)));
      }

      setAnnouncement('Skill deactivated');
      setTimeout(() => setAnnouncement(''), 3000);
    } catch (err) {
      console.error("Failed to deactivate", err);
      setError("Failed to deactivate instance");
      setAnnouncement('Error: Failed to deactivate skill');
      setTimeout(() => setAnnouncement(''), 3000);
    }
  };

  // Filter and search skills
  const filteredSkills = useMemo(() => {
    return skills.filter((skill) => {
      const matchesSearch =
        searchQuery === '' ||
        skill.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
        skill.description.toLowerCase().includes(searchQuery.toLowerCase()) ||
        skill.tags.some((t) => t.toLowerCase().includes(searchQuery.toLowerCase()));

      const category = getCategoryLabel(skills.find(s => s.id === skill.id)?.name || skill.name).toLowerCase();
      const matchesCategory =
        categoryFilter === 'all' || category === categoryFilter.toLowerCase();

      return matchesSearch && matchesCategory;
    });
  }, [skills, searchQuery, categoryFilter]);

  // Get unique categories
  const categories = useMemo(() => {
    const cats = new Set(skills.map((s) => getCategoryLabel(s.category).toLowerCase()));
    return ['all', ...Array.from(cats)];
  }, [skills]);

  // Get instance lookup
  const instanceLookup = useMemo(() => {
    const lookup: Record<string, SkillInstance> = {};
    instances.forEach((i) => {
      lookup[i.skill_id] = i;
    });
    return lookup;
  }, [instances]);

  const enabledCount = instances.filter((i) => i.enabled).length;

  const toggleActive = async (skillId: string) => {
    // logic: if active -> deactivate. If inactive -> open modal.
    const isActive = activeSkillIds.includes(skillId);

    if (isActive) {
      // Find instance ID? The current instancesBySkill is mapped by skill_id.
      const instance = instancesBySkill[skillId];
      if (instance) {
        await handleDeactivateInstance(instance.instance_id);
      }
    } else {
      const skill = skills.find(s => s.id === skillId);
      if (skill) {
        setSkillToActivate(skill);
        setShowAgentModal(true);
      }
    }
  };

  const handleSkillCardKeyDown = (event: React.KeyboardEvent, skillId: string) => {
    if (event.key === 'Enter' || event.key === ' ') {
      event.preventDefault();
      const skill = skills.find(s => s.id === skillId);
      if (skill) {
        setSelectedSkill(skill);
      }
    }
  };

  if (loading && skills.length === 0) {
    return (
      <div className="flex flex-col items-center justify-center py-12">
        <div
          className="w-12 h-12 rounded-full animate-pulse mb-4"
          style={{ backgroundColor: 'var(--mimic-accent)', opacity: 0.3 }}
        />
        <p className="text-sm text-muted-foreground">Loading skills...</p>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* ARIA Live Region for screen reader announcements */}
      <div
        aria-live="polite"
        aria-atomic="true"
        className="sr-only"
        role="status"
      >
        {announcement}
      </div>

      {/* Header */}
      <div className="flex flex-col sm:flex-row sm:items-center sm:justify-between gap-4">
        <div>
          <h2 className="text-xl font-semibold text-foreground">Skill Marketplace</h2>
          <p className="text-sm text-muted-foreground">
            {enabledCount} skills active {personaId ? `for ${personaName}` : ''}
          </p>
        </div>

        {/* Search Bar */}
        <div className="relative max-w-xs w-full">
          <Search className="absolute left-3 top-1/2 -translate-y-1/2 w-4 h-4 text-muted-foreground" />
          <input
            type="text"
            placeholder="Search skills..."
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
            className="w-full pl-9 pr-4 py-2 text-sm rounded-lg border bg-background text-foreground placeholder:text-muted-foreground focus:outline-none focus:ring-2"
            style={{
              borderColor: 'var(--border)',
              // @ts-expect-error CSS custom property
              '--tw-ring-color': 'var(--mimic-accent)',
            }}
          />
        </div>
      </div>

      {/* Memory Seeding Toggle (Visual Only) */}
      <div className="flex items-center gap-3 p-3 rounded-lg border" style={{ borderColor: 'var(--border)', backgroundColor: 'var(--card)' }}>
        <input
          type="checkbox"
          id="memory-seeding"
          checked={includeSeeding}
          onChange={(e) => setIncludeSeeding(e.target.checked)}
          className="w-4 h-4 rounded border-gray-300 text-indigo-600 focus:ring-indigo-500"
        />
        <label htmlFor="memory-seeding" className="text-sm text-foreground cursor-pointer">
          Include expert training data (Memory Seeding)
        </label>
      </div>

      {/* View Tabs */}
      <div className="flex gap-4 border-b border-white/10 px-1" role="tablist" aria-label="View mode tabs">
        <button
          onClick={() => setViewMode('marketplace')}
          role="tab"
          aria-selected={viewMode === 'marketplace'}
          aria-controls="marketplace-panel"
          className={`pb-2 text-xs font-bold uppercase tracking-widest transition-all ${
            viewMode === 'marketplace'
              ? 'text-alden-cyan border-b-2 border-alden-cyan'
              : 'text-slate-500 hover:text-slate-300'
          }`}
        >
          Marketplace
        </button>
        <button
          onClick={() => setViewMode('instances')}
          role="tab"
          aria-selected={viewMode === 'instances'}
          aria-controls="instances-panel"
          className={`pb-2 text-xs font-bold uppercase tracking-widest transition-all ${
            viewMode === 'instances'
              ? 'text-alden-cyan border-b-2 border-alden-cyan'
              : 'text-slate-500 hover:text-slate-300'
          }`}
        >
          Active Instances
        </button>
      </div>

      {/* Category Filters */}
      <div className="flex flex-wrap gap-2">
        {categories.map((cat) => (
          <button
            key={cat}
            onClick={() => setCategoryFilter(cat)}
            className="px-3 py-1.5 text-xs font-medium rounded-full transition-colors capitalize"
            style={{
              backgroundColor:
                categoryFilter === cat
                  ? 'var(--mimic-accent)'
                  : 'var(--muted)',
              color:
                categoryFilter === cat
                  ? 'var(--accent-foreground)'
                  : 'var(--muted-foreground)',
            }}
          >
            {cat === 'all' ? 'All Skills' : cat}
          </button>
        ))}
      </div>

      {/* Error */}
      {error && (
        <div
          className="p-3 rounded-lg flex items-center gap-2"
          style={{
            backgroundColor: 'color-mix(in oklch, var(--destructive) 15%, transparent)',
            color: 'var(--destructive)',
          }}
        >
          <X className="w-4 h-4" />
          <span className="text-sm">{error}</span>
        </div>
      )}

      <div className="flex-1 min-h-0 overflow-hidden relative">
        {viewMode === 'marketplace' ? (
          <div
            id="marketplace-panel"
            role="tabpanel"
            aria-labelledby="tab-marketplace"
            className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-4 overflow-y-auto pr-2 pb-2 scrollbar-thin scrollbar-thumb-alden-gold/40 scrollbar-track-transparent"
            data-testid="skills-list"
          >
            <AnimatePresence mode="popLayout">
              {!loading && filteredSkills.length === 0 && (
                <motion.div
                  initial={{ opacity: 0 }}
                  animate={{ opacity: 1 }}
                  className="col-span-full text-center text-sm text-slate-400 py-12 italic"
                >
                  No skills match your filters.
                </motion.div>
              )}

              {filteredSkills.map((skill, index) => {
                const isActive = activeSkillIds.includes(skill.id);
                return (
                  <motion.div
                    layout
                    initial={{ opacity: 0, y: 12 }}
                    animate={{ opacity: 1, y: 0 }}
                    exit={{ opacity: 0, scale: 0.96 }}
                    transition={{ duration: 0.25, delay: index * 0.03 }}
                    key={skill.id}
                    onClick={() => setSelectedSkill(skill)}
                    onKeyDown={(e) => handleSkillCardKeyDown(e, skill.id)}
                    tabIndex={0}
                    className="relative group bg-black/35 border border-white/10 rounded-xl p-4 hover:border-alden-cyan/40 hover:bg-black/50 transition-all cursor-pointer focus:outline-none focus:ring-2 focus:ring-alden-cyan/50"
                    data-testid="skill-card"
                    role="listitem"
                    aria-label={`Skill: ${skill.name}. ${skill.description}. Category: ${skill.category}. Rating: ${skill.rating.toFixed(1)}. ${isActive ? 'Currently active' : 'Not active'}. Press Enter or Space to view details.`}
                  >
                    <div className="flex items-start justify-between gap-3 mb-3">
                      <div className={`p-2.5 rounded-lg border ${categoryAccent(skill.category)}`} aria-hidden="true">
                        {categoryIcon(skill.category)}
                      </div>
                      <div className="flex flex-col items-end gap-1">
                        <span className="px-2 py-0.5 rounded-full bg-white/5 text-[10px] text-slate-400 border border-white/10">
                          v1.0
                        </span>
                        <div className="flex items-center text-amber-400 text-[10px]">
                          <Star size={10} fill="currentColor" />
                          <span className="ml-1 font-mono">{skill.rating.toFixed(1)}</span>
                        </div>
                      </div>
                    </div>

                    <h3
                      className="text-sm font-semibold text-slate-100 group-hover:text-alden-cyan transition-colors"
                      data-testid="skill-name"
                    >
                      {skill.name}
                    </h3>
                    <p className="text-[11px] text-slate-500 mt-1" data-testid="skill-description">
                      {skill.description}
                    </p>

                    <div className="flex flex-wrap gap-1.5 mt-3">
                      {skill.tags.slice(0, 3).map((tag) => (
                        <span key={tag} className="px-2 py-0.5 rounded-full bg-white/5 text-[10px] text-slate-400">
                          #{tag}
                        </span>
                      ))}
                    </div>

                    <div className="flex items-center justify-between mt-4 pt-3 border-t border-white/10">
                      <span className={`text-[10px] uppercase tracking-widest px-2 py-1 rounded-full border ${agentColor(skill.targetAgent)}`}>
                        {skill.targetAgent}
                      </span>
                      <button
                        onClick={(event) => {
                          event.stopPropagation();
                          void toggleActive(skill.id);
                        }}
                        disabled={operatingSkill === skill.id}
                        data-testid="activate-skill"
                        aria-label={`${isActive ? 'Deactivate' : 'Activate'} ${skill.name} skill`}
                        className={`px-3 py-1.5 rounded-md text-[10px] font-bold uppercase tracking-wider flex items-center gap-2 transition-all focus:outline-none focus:ring-2 focus:ring-alden-cyan/50 ${
                          isActive
                            ? 'bg-alden-cyan/20 text-alden-cyan border border-alden-cyan/40'
                            : 'bg-alden-gold/15 text-alden-gold border border-alden-gold/30 hover:bg-alden-gold/25'
                        }`}
                      >
                        {isActive ? (
                          <>
                            <Check size={12} />
                            Active
                          </>
                        ) : (
                          <>
                            <Zap size={12} />
                            Activate
                          </>
                        )}
                      </button>
                    </div>
                  </motion.div>
                );
              })}
            </AnimatePresence>
          </div>
        ) : (
          <div id="instances-panel" role="tabpanel" aria-labelledby="tab-instances" aria-label="Active skill instances">
            <SkillInstancesList
               instances={Object.values(instancesBySkill)}
               onDeactivate={handleDeactivateInstance}
               resolveSkillName={(id) => skills.find(s => s.id === id)?.name || id}
            />
          </div>
        )}
      </div>

      <AgentSelectorModal
        isOpen={showAgentModal}
        onClose={() => setShowAgentModal(false)}
        onConfirm={handleAgentSelection}
        skillName={skillToActivate?.name || 'Skill'}
      />

      <AnimatePresence>
        {selectedSkill && (
          <motion.div
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            exit={{ opacity: 0 }}
            className="absolute inset-0 z-50 flex items-center justify-center p-4 bg-black/70 backdrop-blur-sm"
            onClick={() => setSelectedSkill(null)}
            role="dialog"
            aria-modal="true"
            aria-labelledby={`skill-details-title-${selectedSkill.id}`}
            aria-label="Skill details dialog"
          >
            <motion.div
              initial={{ scale: 0.96, opacity: 0, y: 10 }}
              animate={{ scale: 1, opacity: 1, y: 0 }}
              exit={{ scale: 0.96, opacity: 0, y: 10 }}
              onClick={(event) => event.stopPropagation()}
              className="w-full max-w-2xl bg-[#0b1221] border border-alden-cyan/30 rounded-2xl shadow-[0_0_40px_rgba(0,0,0,0.7)] overflow-hidden flex flex-col max-h-[90%]"
              data-testid="skill-details"
              role="document"
            >
              <div className="relative p-6 border-b border-white/10 bg-gradient-to-b from-alden-cyan/10 to-transparent">
                <button
                  onClick={() => setSelectedSkill(null)}
                  aria-label="Close skill details"
                  className="absolute top-4 right-4 p-2 text-slate-400 hover:text-white rounded-full hover:bg-white/10 transition-colors focus:outline-none focus:ring-2 focus:ring-alden-cyan/50"
                >
                  <X size={18} />
                </button>
                <div className="flex items-start gap-4">
                  <div className={`p-3 rounded-xl border ${categoryAccent(selectedSkill.category)}`} aria-hidden="true">
                    {categoryIcon(selectedSkill.category)}
                  </div>
                  <div>
                    <h3
                      id={`skill-details-title-${selectedSkill.id}`}
                      className="text-xl font-semibold text-white"
