/**
 * Vault Command Center - MythologIQ Branded Dashboard
 *
 * Advanced Memory System tactical interface for Memory, RAG, Knowledge Graph, and Context
 * Inspired by Starcraft command interfaces and Star Trek LCARS with business polish
 */

import React, { useState, useEffect, useCallback, useMemo } from 'react';
import { VaultBridge, type MemorySlice, type RagSearchResult, type ContextEntry } from '../../bridges/VaultBridge';
import { CreateMemoryModal } from './CreateMemoryModal';
import { VaultKnowledgeGraph } from './VaultKnowledgeGraph';
import { PrivateChannel } from '../shared/chat/PrivateChannel';
import { useChatStateWithOrchestration } from '../shared/chat/useChatStateWithOrchestration';
import { MODULE_RESIDENT_AI, type SystemPersonaId } from '../shared/chat/types';
import { UnifiedChatContainer } from '../shared/chat/UnifiedChatContainer';

interface VaultCommandCenterProps {
  userId: string;
}

type ViewMode = 'overview' | 'memory' | 'rag' | 'knowledge-graph' | 'context' | 'chat';

export const VaultCommandCenter: React.FC<VaultCommandCenterProps> = ({ userId }) => {
  const [viewMode, setViewMode] = useState<ViewMode>('overview');
  const [isSidebarCollapsed, setIsSidebarCollapsed] = useState(false);
  const [memories, setMemories] = useState<MemorySlice[]>([]);
  const [loading, setLoading] = useState(true);

  // Interactive State
  const [isCreateModalOpen, setIsCreateModalOpen] = useState(false);
  const [searchQuery, setSearchQuery] = useState('');
  const [searchResults, setSearchResults] = useState<RagSearchResult[]>([]);
  const [isSearching, setIsSearching] = useState(false);
  const [selectedSessionId, setSelectedSessionId] = useState<string | null>(null);
  const [contextEntries, setContextEntries] = useState<ContextEntry[]>([]);

  // Chat Integration
  const moduleId = 'vault';
  const residentPersonaId = (MODULE_RESIDENT_AI[moduleId] || 'sentry') as SystemPersonaId;
  const {
    personas: chatPersonas,
    privateMessages,
    privateLoading,
    privateError,
    privateTyping,
    sendPrivateMessage,
  } = useChatStateWithOrchestration({
    userId,
    moduleId,
    residentPersonaId,
    onError: (err) => console.error('Vault Chat Error:', err),
  });

  const residentPersona = chatPersonas.find((p) => p.id === residentPersonaId);

  const [stats, setStats] = useState({
    totalMemories: 0,
    activeMemories: 0,
    totalEmbeddings: 0,
    totalEntities: 0,
    totalRelationships: 0,
    contextWindows: 0,
  });

  const loadDashboardData = useCallback(async () => {
    try {
      setLoading(true);

      // Load memories
      const memoryResponse = await VaultBridge.getMemories(userId);
      if (memoryResponse.success) {
        setMemories(memoryResponse.data);
        const active = memoryResponse.data.filter((m: MemorySlice) => m.status === 'active').length;
        setStats(prev => ({
          ...prev,
          totalMemories: memoryResponse.data.length,
          activeMemories: active,
        }));
      }

      // Load memory stats
      const memoryStatsResponse = await VaultBridge.getMemoryStats(userId);
      if (memoryStatsResponse.success) {
        setStats(prev => ({
          ...prev,
          totalMemories: memoryStatsResponse.data.total_slices,
          activeMemories: memoryStatsResponse.data.active_slices,
        }));
      }

      // Load RAG stats
      const ragStatsResponse = await VaultBridge.getRagStats(userId);
      if (ragStatsResponse.success) {
        setStats(prev => ({
          ...prev,
          totalEmbeddings: ragStatsResponse.data.total_embeddings,
        }));
      }

      // Load Knowledge Graph stats
      const kgStatsResponse = await VaultBridge.getGraphStats(userId);
      if (kgStatsResponse.success) {
        setStats(prev => ({
          ...prev,
          totalEntities: kgStatsResponse.data.total_entities,
          totalRelationships: kgStatsResponse.data.total_relationships,
        }));
      }

      // Load Context stats (use a session ID placeholder since we need one)
      const contextStatsResponse = await VaultBridge.getContextStats('overview-session');
      if (contextStatsResponse.success) {
        setStats(prev => ({
          ...prev,
          contextWindows: contextStatsResponse.data.total_entries,
        }));
      }

    } catch (error) {
      console.error('[Vault] Failed to load dashboard data:', error);
    } finally {
      setLoading(false);
    }
  }, [userId]);

  useEffect(() => {
    loadDashboardData();
  }, [loadDashboardData]);

  const renderHeader = () => (
    <div className="tactical-panel-header">
      <div className="flex items-center gap-3">
        <div className="w-10 h-10 rounded-lg bg-gradient-to-br from-[var(--alden-panel)] to-[var(--alden-border)] flex items-center justify-center">
          <span className="text-2xl font-bold text-[var(--alden-text-primary)]">V</span>
        </div>
        <div>
          <h1 className="tactical-panel-title">Vault Command Center</h1>
          <p className="text-xs text-[var(--alden-text-secondary)]">Advanced Memory & Knowledge Intelligence</p>
        </div>
      </div>
      <div className="flex items-center gap-2">
        <span className="status-active"></span>
        <span className="text-sm text-[var(--alden-gold)]">Memory Banks Online</span>
      </div>
    </div>
  );

  const renderMetrics = () => (
    <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 xl:grid-cols-6 gap-4 mb-6">
      <div className="metric-card">
        <div className="metric-value">{stats.totalMemories}</div>
        <div className="metric-label">Memory Slices</div>
        <div className="mt-2 flex items-center gap-2 text-[var(--alden-gold)]">
          <span className="status-active"></span>
          <span className="text-xs">{stats.activeMemories} active</span>
        </div>
      </div>

      <div className="metric-card">
        <div className="metric-value">{stats.totalEmbeddings}</div>
        <div className="metric-label">Vector Embeddings</div>
        <div className="mt-2 text-xs text-muted-foreground">RAG Engine</div>
      </div>

      <div className="metric-card">
        <div className="metric-value">{stats.totalEntities}</div>
        <div className="metric-label">Graph Entities</div>
        <div className="mt-2 text-xs text-muted-foreground">Knowledge nodes</div>
      </div>

      <div className="metric-card">
        <div className="metric-value">{stats.totalRelationships}</div>
        <div className="metric-label">Relationships</div>
        <div className="mt-2 text-xs text-muted-foreground">Graph edges</div>
      </div>

      <div className="metric-card">
        <div className="metric-value">{stats.contextWindows}</div>
        <div className="metric-label">Context Windows</div>
        <div className="mt-2 text-xs text-muted-foreground">Active contexts</div>
      </div>

      <div className="metric-card">
        <div className="metric-value">
          {stats.totalEntities > 0 ? (stats.totalRelationships / stats.totalEntities).toFixed(1) : '0.0'}
        </div>
        <div className="metric-label">Avg Connections</div>
        <div className="mt-2 text-xs text-muted-foreground">Per entity</div>
      </div>
    </div>
  );

  const renderViewSelector = () => (
    <div className="flex gap-2 mb-6 flex-wrap">
      {(['overview', 'memory', 'rag', 'knowledge-graph', 'context'] as ViewMode[]).map(mode => (
        <button
          key={mode}
          onClick={() => setViewMode(mode)}
          className={`
            px-4 py-2 rounded-md font-medium uppercase text-sm tracking-wide
            transition-all duration-200
            ${viewMode === mode
              ? 'bg-[var(--alden-gold)] text-[var(--mythtech-navy)] shadow-[0_0_20px_var(--alden-gold)]'
              : 'bg-[var(--alden-panel)] text-[var(--alden-gold)] hover:bg-[var(--alden-border)] border border-[var(--alden-gold)]/30'
            }
          `}
        >
          {mode.replace('-', ' ')}
        </button>
      ))}
    </div>
  );

  const renderMemoryBrowser = () => (
    <div className="tactical-panel">
      <div className="tactical-panel-header">
        <h2 className="tactical-panel-title">Memory Archives</h2>
        <button 
          onClick={() => setIsCreateModalOpen(true)}
          className="holo-button text-xs px-3 py-1"
        >
          + New Memory
        </button>
      </div>
      <div className="tactical-panel-body space-y-2">
        {loading ? (
          <div className="text-center py-8 text-muted-foreground">
            <div className="inline-block w-6 h-6 border-2 border-[var(--alden-gold)] border-t-transparent rounded-full animate-spin"></div>
            <p className="mt-2">Loading memories...</p>
          </div>
        ) : memories.length === 0 ? (
          <div className="text-center py-12 text-muted-foreground">
            <p className="text-lg mb-2">No memories stored</p>
            <p className="text-sm">Create your first memory to get started</p>
          </div>
        ) : (
          memories.slice(0, 15).map(memory => (
            <div
              key={memory.id}
              className="glass-panel p-4 hover:shadow-lg transition-shadow cursor-pointer group"
            >
              <div className="flex items-start justify-between">
                <div className="flex-1">
                  <div className="flex items-center gap-2 mb-2">
                    <span className={`px-2 py-1 rounded text-xs font-medium ${
                      memory.content_type === 'conversation' ? 'bg-[var(--mythtech-cyan)]/20 text-[var(--mythtech-cyan)]' :
                      memory.content_type === 'task' ? 'bg-[var(--alden-gold)]/20 text-[var(--alden-gold)]' :
                      memory.content_type === 'knowledge' ? 'bg-[var(--alden-border)]/40 text-[var(--alden-text-primary)]' :
                      'bg-[var(--alden-border)]/20 text-[var(--alden-text-secondary)]'
                    }`}>
                      {memory.content_type}
                    </span>
                    <span className="text-xs text-muted-foreground">v{memory.version}</span>
                    {memory.status === 'active' && <span className="status-active"></span>}
                  </div>
                  <div className="mb-2">
                    <code className="text-sm text-foreground/80 line-clamp-2">
                      {memory.content.substring(0, 150)}...
                    </code>
                  </div>
                  {memory.tags && (
                    <div className="flex gap-2 flex-wrap mb-2">
                      {(() => {
                        const parsedTags: string[] = typeof memory.tags === 'string' ? JSON.parse(memory.tags || '[]') : (memory.tags || []);
                        return parsedTags.slice(0, 5).map((tag: string, i: number) => (
                          <span key={i} className="text-xs px-2 py-1 rounded bg-muted text-muted-foreground">
                            #{tag}
                          </span>
                        ));
                      })()}
                    </div>
                  )}
                  <div className="flex items-center gap-4 text-xs text-muted-foreground">
                    <span>{new Date(memory.created_at).toLocaleDateString()}</span>
                    <span>Accessed {memory.access_count} times</span>
                    <span className="capitalize">{memory.status}</span>
                  </div>
                </div>
                <div className="flex gap-2">
                  <button className="p-2 rounded-md hover:bg-muted transition-colors" title="View history" aria-label="View memory history">
                    dY"o
                  </button>
                  <button className="p-2 rounded-md hover:bg-muted transition-colors" title="More options" aria-label="More memory options">
                    <_
                  </button>
                </div>
              </div>
            </div>
          ))
        )}
      </div>
    </div>
  );

  const handleSearch = async () => {
    if (!searchQuery.trim()) return;
    
    setIsSearching(true);
    try {
      // Create a dummy embedding for the query since we don't have a local embedder yet
      // In a real scenario, we'd generate this or use the text-based fallback in the bridge
      const response = await VaultBridge.searchSemantic({
        query_embedding: [], // Backend will auto-generate using query_text
        query_text: searchQuery,
        user_id: userId,
        min_similarity: 0.0, // Return all matches for now
        max_results: 10
      });
      
      // If the bridge's pure semantic search fails or returns empty (likely due to missing embeddings),
      // we can try the text-based search utility which falls back gracefully
      if (!response.success || response.data.length === 0) {
         const textResponse = await VaultBridge.searchMemoriesSemantic(userId, searchQuery);
         if (textResponse.success) {
            // Map MemorySlice to RagSearchResult shape for display
            const mappedResults: RagSearchResult[] = textResponse.data.map(m => ({
                memory_slice_id: m.id,
                content: m.content,
                content_type: m.content_type,
                similarity_score: m.similarity_score || 0,
                chunk_index: 0,
                total_chunks: 1,
                metadata: m.metadata,
                tags: m.tags,
                created_at: m.created_at
            }));
            setSearchResults(mappedResults);
         }
      } else {
        setSearchResults(response.data);
      }
    } catch (error) {
      console.error('Search failed:', error);
    } finally {
      setIsSearching(false);
    }
  };

  const renderRAGInterface = () => (
    <div className="tactical-panel">
      <div className="tactical-panel-header">
        <h2 className="tactical-panel-title">Semantic Search Engine</h2>
        <div className="flex items-center gap-2">
          <span className="text-xs text-[var(--alden-gold)]">{stats.totalEmbeddings} embeddings indexed</span>
        </div>
      </div>
      <div className="tactical-panel-body">
        <div className="mb-6">
          <label htmlFor="semantic-query-input" className="block text-sm font-medium mb-2 text-foreground">
            Semantic Query
          </label>
          <div className="relative">
            <input
              id="semantic-query-input"
              type="text"
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              onKeyDown={(e) => e.key === 'Enter' && handleSearch()}
              placeholder="Search by meaning, not just keywords..."
              className="command-input w-full"
            />
            <button
              onClick={handleSearch}
              disabled={isSearching}
              className={`absolute right-2 top-1/2 -translate-y-1/2 holo-button text-xs px-4 py-1 transition-all duration-300 ${
                isSearching ? 'animate-pulse shadow-[0_0_20px_rgba(6,182,212,0.6)]' : ''
              }`}
            >
              {isSearching ? (
                <span className="flex items-center gap-3">
                  <span className="rag-scanning-dots">
                    <span className="rag-scan-dot" />
                    <span className="rag-scan-dot" />
                    <span className="rag-scan-dot" />
                  </span>
                  <span className="tracking-[0.25em] uppercase text-[10px]">Scanning</span>
                </span>
              ) : 'Search'}
            </button>
          </div>
          <p className="text-xs text-muted-foreground mt-2">
            Vector similarity search using embeddings - finds semantically related content
          </p>
        </div>

        {searchResults.length > 0 ? (
          <div className="space-y-3">
            <h3 className="text-sm font-medium text-[var(--alden-gold)]">Top Results</h3>
            {searchResults.map((result, idx) => (
              <div key={idx} className="glass-panel p-4 border-l-2 border-l-[var(--alden-gold)]">
                <div className="flex justify-between items-start mb-2">
                  <span className="text-xs font-mono text-[var(--alden-gold)]">
                    Match: {(result.similarity_score * 100).toFixed(1)}%
                  </span>
                  <span className="text-xs text-muted-foreground">
                    {new Date(result.created_at).toLocaleDateString()}
                  </span>
                </div>
                <p className="text-sm text-[var(--alden-text-primary)]">{result.content}</p>
                {result.tags && (
                   <div className="mt-2 flex gap-2">
                      {/* Handle tags whether they are string or array */}
                      {(typeof result.tags === 'string' ? JSON.parse(result.tags || '[]') : (result.tags || [])).map((tag: string, i: number) => (
                        <span key={i} className="text-[10px] px-1.5 py-0.5 rounded bg-[var(--alden-border)]/30 text-[var(--alden-gold)] border border-[var(--alden-border)]">
                          #{tag}
                        </span>
                      ))}
                   </div>
                )}
              </div>
            ))}
          </div>
        ) : (
          <div className="glass-panel p-6 text-center">
            <div className="text-4xl mb-4">dY"?</div>
            <p className="text-lg font-medium mb-2">RAG Search Interface</p>
            <p className="text-sm text-muted-foreground mb-4">
              Enter a query above to search through {stats.totalEmbeddings} vector embeddings
            </p>
            <div className="grid grid-cols-3 gap-4 text-center">
              <div>
                <div className="text-2xl font-bold text-[var(--alden-gold)]">{stats.totalEmbeddings}</div>
                <div className="text-xs text-muted-foreground">Vectors</div>
              </div>
              <div>
                <div className="text-2xl font-bold text-[var(--mythtech-cyan)]">768</div>
                <div className="text-xs text-muted-foreground">Dimensions</div>
              </div>
              <div>
                <div className="text-2xl font-bold text-[var(--alden-text-secondary)]">0.0</div>
                <div className="text-xs text-muted-foreground">Avg Similarity</div>
              </div>
            </div>
          </div>
        )}
      </div>
    </div>
