import React, { useState, useMemo } from 'react';
import { UnifiedCommandCenterOptimized as UnifiedCommandCenter } from '../shared/premium/UnifiedCommandCenter.optimized';
import { Database, FileText, Folder, HardDrive, Lock, Search, Shield, ShieldCheck } from '@/components/ui/HearthlinkIcons';
import { VaultKnowledgeGraph } from './VaultKnowledgeGraph';
import { MemorySearchBar } from './MemorySearchBar';
import { UnifiedChatContainer } from '../shared/chat/UnifiedChatContainer';
import { useChatStateWithOrchestration } from '../shared/chat/useChatStateWithOrchestration';
import { MODULE_RESIDENT_AI, type SystemPersonaId } from '../shared/chat/types';
import { PrivateChannel } from '../shared/chat/PrivateChannel';
import type { MemorySlice } from '../../bridges/VaultBridge';

interface VaultDashboardProps {
  userId?: string;
  onBackToLauncher?: () => void;
  onNavigateToModule?: (moduleName: string) => void;
  onFocusAssist?: () => void;
}

export const VaultDashboard: React.FC<VaultDashboardProps> = ({
  userId = 'default_user',
  onBackToLauncher,
  onNavigateToModule,
  onFocusAssist,
}) => {
  const [activeTab, setActiveTab] = useState('overview');
  const [selectedMemory, setSelectedMemory] = useState<MemorySlice | null>(null);
  const [sidebarCollapsed, setSidebarCollapsed] = useState(false);

  // Chat Integration
  const moduleId = 'vault';
  const residentPersonaId = (MODULE_RESIDENT_AI[moduleId] || 'sentry') as SystemPersonaId; // Vault usually Sentry-guarded or separate? 'vault' persona? Map says 'sentry' or undefined?
  // Let's check shared/chat/types.ts or assume 'sentry' or 'alden'.
  // Alice uses 'alice'. Mimic 'mimic'. Alden 'alden'.
  // Vault doesn't have a resident persona?
  // Maybe 'sentry' is appropriate as guard.
  // Code in other files uses `sentry` for Sentry.
  // For Vault, let's use 'alden' or 'sentry'.
  // Let's use 'sentry' for now as it's secure storage.

  const {
    personas: chatPersonas,
    privateMessages,
    privateLoading,
    privateError,
    privateTyping,
    sendPrivateMessage,
  } = useChatStateWithOrchestration({
    userId,
    moduleId,
    residentPersonaId: 'sentry', // Explicitly Sentry for Vault
    onError: (err) => console.error('Vault Chat Error:', err),
  });

  const residentPersona = chatPersonas.find(p => p.id === 'sentry');

  const tabs = [
    { id: 'overview', label: 'Overview' },
    { id: 'memories', label: 'Memory Bank' },
    { id: 'knowledge-graph', label: 'Knowledge Graph' },
    { id: 'files', label: 'Secure Storage' },
    { id: 'audit', label: 'Access Logs' },
    { id: 'chat', label: 'Chat' },
  ];

  const renderOverview = () => (
    <div className="flex flex-col h-full p-5 gap-4 overflow-hidden">
      {/* Stats Row */}
      <div className="grid grid-cols-2 lg:grid-cols-4 gap-3 shrink-0">
        {[
          { label: 'Total Memories', value: '8,492', icon: <Database size={20} />, color: 'text-amber-400' }, // Keep amber/gold
          { label: 'Encrypted Files', value: '1,204', icon: <Lock size={20} />, color: 'text-cyan-400' },     // switch emerald to cyan for mythtech alignment
          { label: 'Storage Used', value: '45%', icon: <HardDrive size={20} />, color: 'text-blue-400' },
          { label: 'Security Status', value: 'SECURE', icon: <ShieldCheck size={20} />, color: 'text-green-400' },
        ].map((stat, i) => (
          <div key={i} className="bg-[var(--alden-panel)]/60 border border-[var(--alden-border)] p-3 rounded-lg flex items-center justify-between gap-3">
            <div>
              <div className="text-[var(--alden-text-secondary)] text-[10px] uppercase tracking-wider font-semibold mb-1">{stat.label}</div>
              <div className="text-lg font-bold text-[var(--alden-text-primary)] font-mono leading-tight">{stat.value}</div>
            </div>
            <div className={`p-2 rounded-md bg-[var(--alden-border)] ${stat.color}`}>{stat.icon}</div>
          </div>
        ))}
      </div>

      {/* Main Content Area */}
      <div className="flex-1 grid grid-cols-12 gap-4 min-h-0">
        <div className="col-span-9 bg-[var(--alden-panel)]/60 border border-[var(--alden-border)] rounded-2xl p-4 flex flex-col h-full overflow-hidden">
          <div className="flex items-center justify-between mb-4 shrink-0">
            <h3 className="text-[var(--alden-text-primary)] font-orbitron font-bold tracking-widest flex items-center gap-2">
              <Folder size={18} className="text-amber-500" />
              RECENT ACCESS
            </h3>
            <div className="relative">
              <Search size={14} className="absolute left-3 top-1/2 -translate-y-1/2 text-[var(--alden-text-secondary)]" />
              <input
                type="text"
                placeholder="Search secure files..."
                className="bg-[var(--alden-border)] border border-[var(--alden-border)] rounded-full py-1.5 pl-9 pr-4 text-xs text-[var(--alden-text-primary)] focus:outline-none focus:border-[var(--alden-gold)]/50 w-56"
              />
            </div>
          </div>

          <div className="flex-1 overflow-y-auto space-y-2 pr-2 scrollbar-thin scrollbar-thumb-[#334155]">
            {[...Array(15)].map((_, i) => (
              <div key={i} className="flex items-center justify-between p-2.5 bg-[var(--alden-border)]/30 rounded-lg border border-transparent hover:border-[var(--alden-border)] hover:bg-[var(--alden-border)]/50 transition-all cursor-pointer group shrink-0">
                <div className="flex items-center gap-3 min-w-0">
                  <div className="p-2 rounded bg-amber-500/10 text-amber-500 group-hover:bg-amber-500/20 transition-colors">
                    <FileText size={16} />
                  </div>
                  <div className="min-w-0">
                    <div className="text-[var(--alden-text-primary)] text-sm font-medium">project_chimera_specs_v{i + 1}.enc</div>
                    <div className="text-[var(--alden-text-secondary)] text-xs">Updated 2h ago ? 2.4 MB</div>
                  </div>
                </div>
                <div className="flex items-center gap-4">
                  <span className="text-[var(--alden-text-secondary)] text-xs font-mono">AES-256</span>
                  <Lock size={14} className="text-emerald-500/70" />
                </div>
              </div>
            ))}
          </div>
        </div>

        {/* Right Column: Security Log */}
        <div className="col-span-3 bg-[var(--alden-panel)]/60 border border-[var(--alden-border)] rounded-2xl p-4 flex flex-col relative overflow-hidden h-full">
          <div className="absolute top-0 right-0 p-3 opacity-10 pointer-events-none">
            <Shield size={120} />
          </div>
          <h3 className="text-[var(--alden-text-secondary)] text-xs font-bold uppercase tracking-widest mb-3 relative z-10 shrink-0">Security Log</h3>
          <div className="flex-1 overflow-y-auto space-y-2 relative z-10 pr-2 scrollbar-thin scrollbar-thumb-[#334155]">
            {[...Array(10)].map((_, i) => (
              <div key={i} className="flex gap-3 shrink-0">
                <div className="flex flex-col items-center gap-1">
                  <div className="w-2 h-2 rounded-full bg-emerald-500" />
                  <div className="w-0.5 h-full bg-[var(--alden-border)]/50" />
                </div>
                <div className="pb-3">
                  <div className="text-[var(--alden-text-primary)] text-xs font-medium">Access Granted</div>
                  <div className="text-[var(--alden-text-secondary)] text-[10px] font-mono">User: KRKNA ? 10:4{i} AM</div>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );

  const renderMemoryBank = () => (
    <div className="flex flex-col h-full p-5 gap-4 overflow-hidden">
      {/* Memory Search Bar */}
      <div className="shrink-0">
        <MemorySearchBar 
          userId={userId} 
          onMemorySelect={(memory) => setSelectedMemory(memory)}
        />
      </div>

      {/* Selected Memory Preview */}
      {selectedMemory && (
        <div className="bg-[var(--alden-panel)]/60 border border-[var(--alden-gold)]/30 rounded-xl p-4 shrink-0">
          <h4 className="text-amber-400 text-xs uppercase tracking-wider font-semibold mb-2">Selected Memory</h4>
          <pre className="text-[var(--alden-text-primary)] text-sm font-mono overflow-auto max-h-40">
            {JSON.stringify(selectedMemory, null, 2)}
          </pre>
        </div>
      )}

      {/* Memory List Placeholder */}
      <div className="flex-1 bg-[var(--alden-panel)]/60 border border-[var(--alden-border)] rounded-2xl p-6 flex items-center justify-center">
        <div className="text-center">
          <Database size={48} className="text-[var(--alden-gold)]/50 mx-auto mb-4" />
          <p className="text-[var(--alden-text-secondary)] text-sm">Use the search bar above to find memories</p>
          <p className="text-[var(--alden-text-secondary)] text-xs mt-1">Supports keyword and semantic search</p>
        </div>
      </div>
    </div>
  );

  const renderKnowledgeGraph = () => (
    <div className="h-full overflow-hidden">
      <VaultKnowledgeGraph userId={userId} />
    </div>
  );

  const renderSecureStorage = () => (
    <div className="flex items-center justify-center h-full">
      <div className="text-center">
        <Lock size={48} className="text-[var(--alden-gold)]/50 mx-auto mb-4" />
        <p className="text-[var(--alden-text-secondary)] text-sm">Secure Storage</p>
        <p className="text-[var(--alden-text-secondary)] text-xs mt-1">Coming soon</p>
      </div>
    </div>
  );

  const renderAccessLogs = () => (
    <div className="flex items-center justify-center h-full">
      <div className="text-center">
        <Shield size={48} className="text-[var(--alden-gold)]/50 mx-auto mb-4" />
        <p className="text-[var(--alden-text-secondary)] text-sm">Access Logs</p>
        <p className="text-[var(--alden-text-secondary)] text-xs mt-1">Coming soon</p>
      </div>
    </div>
  );

  const renderTabContent = () => {
    if (activeTab === 'chat') {
      return (
        <UnifiedChatContainer
          userId={userId}
          moduleId="vault"
          residentPersonaId="sentry"
          onClose={() => setActiveTab('overview')}
          className="h-full w-full"
        />
      );
    }

    switch (activeTab) {
      case 'overview':
        return renderOverview();
      case 'memories':
        return renderMemoryBank();
      case 'knowledge-graph':
        return renderKnowledgeGraph();
      case 'files':
        return renderSecureStorage();
      case 'audit':
        return renderAccessLogs();
      default:
        return renderOverview();
    }
  };

  // Memoized Sidebar (Private Channel)
  const chatSidebar = useMemo(() => {
    if (activeTab === 'chat' || !residentPersona) return null;
    return (
      <PrivateChannel
        residentPersona={residentPersona}
        messages={privateMessages}
        isLoading={privateLoading}
        error={privateError}
        isTyping={privateTyping}
        onSendMessage={sendPrivateMessage}
        isCollapsed={sidebarCollapsed}
        onToggleCollapse={() => setSidebarCollapsed(prev => !prev)}
        onStandardView={() => setActiveTab('chat')}
        onFocusAssist={onFocusAssist}
        className="h-full border-l border-[var(--alden-gold)]/20"
      />
    );
  }, [activeTab, residentPersona, sidebarCollapsed, privateMessages, privateLoading, privateError, privateTyping, sendPrivateMessage, onFocusAssist]);

  return (
    <UnifiedCommandCenter
      title="Vault"
      subtitle="Secure Storage"
      iconPath="/assets/Vault.png"
      tabs={tabs}
      activeTab={activeTab}
      onTabChange={setActiveTab}
