    async fn encrypt_content(&self, plaintext: &str) -> Result<(String, String), String> {
        let key = self.key_manager.get_vault_key().await?;
        let key_array = GenericArray::from_slice(&key);
        let cipher = Aes256Gcm::new(key_array);
        let mut nonce_bytes = [0u8; 12];
        OsRng.fill_bytes(&mut nonce_bytes);
        let nonce = Nonce::from_slice(&nonce_bytes);
        let ciphertext = cipher
            .encrypt(nonce, plaintext.as_bytes())
            .map_err(|e| format!("Encryption failed: {e}"))?;
        Ok((
            general_purpose::STANDARD.encode(&ciphertext),
            general_purpose::STANDARD.encode(nonce_bytes),
        ))
    }

    async fn decrypt_content(
        &self,
        ciphertext_b64: &str,
        nonce_b64: &str,
    ) -> Result<String, String> {
        let key = self.key_manager.get_vault_key().await?;
        let key_array = GenericArray::from_slice(&key);
        let cipher = Aes256Gcm::new(key_array);

        let nonce_bytes = general_purpose::STANDARD
            .decode(nonce_b64)
            .map_err(|e| format!("Invalid nonce: {e}"))?;
        let nonce = Nonce::from_slice(&nonce_bytes);

        let ciphertext = general_purpose::STANDARD
            .decode(ciphertext_b64)
            .map_err(|e| format!("Invalid ciphertext: {e}"))?;

        let plaintext_bytes = cipher
            .decrypt(nonce, ciphertext.as_ref())
            .map_err(|e| format!("Decryption failed: {e}"))?;

        String::from_utf8(plaintext_bytes).map_err(|e| format!("Invalid UTF-8: {e}"))
