                onClick={() => setSelectedTask(task)}
              >
                <div className="flex items-center gap-4 flex-1">
                  <div className={`w-2 h-2 rounded-full shrink-0 ${task.priority === 'high' ? 'bg-[#ef4444]' : 'bg-alden-cyan'}`} />
                  <div className="flex flex-col">
                    <span className="text-[#c5d2e8] font-medium text-sm">{task.title}</span>
                    <span className="text-[10px] text-[#9fb4d6] line-clamp-1">{task.description}</span>
