          Marketplace + Execution
        </span>
      </div>
      <div className="grid grid-cols-1 xl:grid-cols-2 gap-5 flex-1 min-h-0">
        <div className="bg-black/20 border border-white/10 rounded-xl p-4 overflow-hidden">
          <SkillMarketplace />
        </div>
        <div className="bg-black/20 border border-white/10 rounded-xl p-4 overflow-hidden">
          <SkillExecutionMonitor />
        </div>
      </div>
    </div>
  ), [frameStyle]);

  const renderContent = useCallback(() => {
    if (activeView === 'chat') {
