             style={{ filter: 'drop-shadow(0 0 15px rgba(251, 191, 36, 0.6))' }} />
      </button>

      {currentLevel === 'personas' && isLoaded && (
        <div className="absolute top-[15%] left-[10%] z-30">
          <button
            onClick={handleBackToMain}
            className="flex items-center gap-2 px-4 py-2 bg-black/60 border border-cyan-500/50
                     text-cyan-500 rounded-lg hover:bg-cyan-500/10 hover:border-cyan-500
