          {/* Visual Circle Background - Centered & Tighter */}
          <div className={`absolute left-1/2 top-1/2 -translate-x-1/2 -translate-y-1/2 rounded-full bg-gradient-radial from-cyan-500/30 via-cyan-500/15 to-transparent ${isLoaded ? 'animate-stable-glow w-64 h-64' : 'animate-heartbeat w-64 h-64'}`}
               style={{ animationDuration: isLoaded ? '4s' : '1.5s' }} />
          
          {isLoaded && (
            <div className="absolute left-1/2 top-1/2 -translate-x-1/2 -translate-y-1/2 w-64 h-52 z-0">
