              <div className="absolute inset-0 bg-gradient-to-t from-blue-400/30 via-cyan-300/20 to-transparent rounded-full opacity-70" style={{ animation: 'flicker 3.2s ease-in-out infinite 0.8s' }} />
            </div>
          )}

          {heroVisible ? (
            <button
              onClick={() => setShowSettings(true)}
              className="relative z-10 group cursor-pointer bg-transparent border-none p-0 overflow-visible pointer-events-auto flex items-center justify-center"
