      {showSettings && (
        <div className="fixed inset-0 z-50 flex items-center justify-center bg-black/80 backdrop-blur-sm animate-fadeIn">
          <div className="relative w-full max-w-6xl max-h-[90vh] overflow-auto rounded-xl border border-cyan-500/30 bg-slate-900/95 shadow-2xl">
            {/* Close Modal Button */}
            <button
