    page.on('console', msg => console.log(`[BROWSER] ${msg.type()}: ${msg.text()}`));
    page.on('pageerror', err => console.log(`[BROWSER-ERROR]: ${err}`));

    // Install mocks with email data
    await Mocks.install(page);
    
    // Disable tutorial
    await page.addInitScript(() => {
      window.localStorage.setItem('hl_first7_state', JSON.stringify({
        currentDay: 7,
        completedDays: [1, 2, 3, 4, 5, 6, 7],
        completedSteps: [],
        lastActive: new Date().toISOString(),
        dismissed: true,
        autoOpened: true
      }));
    });

    // Navigate to Alden
    await page.goto('/');
    await Helpers.waitForAppReady(page);

    // Navigate to Alden Module
    await page.locator(selectors.SELECTOR_MODULE_ALDEN).click();
    await expect(page).toHaveURL(/.*alden.*/);

    // Navigate to Email tab if present
    const emailTab = page.locator(selectors.SELECTOR_NAV_EMAIL_TAB);
    if (await emailTab.count() > 0) {
      console.log('Clicking Email tab via data-testid');
      await emailTab.click({ force: true });
    } else {
        console.log('Email tab not found via data-testid, trying text fallback');
        const fallback = page.getByRole('button', { name: /email/i });
        if (await fallback.count() > 0) {
             console.log('Clicking Email tab via text fallback');
             await fallback.click({ force: true });
        } else {
             console.log('Email tab ABSENT. Dumping page content for analysis.');
        }
    }
    
    // Wait for View Container first (confirms navigation)
    try {
        await page.locator(selectors.SELECTOR_EMAIL_VIEW_CONTAINER).waitFor({ state: 'visible', timeout: 30000 });
        console.log('Email View Container loaded');
    } catch (e) {
        console.log('Timeout waiting for Email View Container.');
        throw e;
    }
    
    // Check if loading spinner is stuck
    try {
        const spinner = page.locator('[data-testid="loading-spinner"]');
        if (await spinner.isVisible()) {
            console.log('Loading spinner visible. Waiting for it to disappear...');
            await spinner.waitFor({ state: 'hidden', timeout: 30000 });
        }
    } catch (e) {
        console.log('Timeout waiting for Loading Spinner to disappear.');
    }

    // Wait for Inbox or Empty State with debug
    try {
      await Promise.race([
        page.locator(selectors.SELECTOR_EMAIL_INBOX).waitFor({ state: 'visible', timeout: 30000 }),
        page.locator(selectors.SELECTOR_EMPTY_STATE).waitFor({ state: 'visible', timeout: 30000 })
      ]);
