/**
 * E2E Tests for Synapse Skill System
 *
 * Tests the Synapse skill system functionality:
 * - List available skills from marketplace
 * - Activate a skill for an agent
 * - Deactivate an active skill
 * - Verify skill instance persistence after reload
 * - Error handling for non-existent skill activation
 *
 * @see src/bridges/SynapseBridge.ts
 * @see src-tauri/src/synapse/commands.rs
 */

import { test, expect, Mocks, Helpers } from './setup';

test.describe('Synapse Skill System', () => {
  test.beforeEach(async ({ page }) => {
    // Install mocks with skill data
    await Mocks.install(page);
    Mocks.scenarios.skillsAvailable();

    // Disable tutorial
    await page.addInitScript(() => {
      window.localStorage.setItem('hl_first7_state', JSON.stringify({
        currentDay: 7,
        completedDays: [1, 2, 3, 4, 5, 6, 7],
        completedSteps: [],
        lastActive: new Date().toISOString(),
        dismissed: true,
        autoOpened: true
      }));
    });

    // Navigate to Synapse
    await page.goto('/');
    await page.waitForLoadState('domcontentloaded');

    const synapseButton = page.locator('[data-testid="module-synapse"]').or(
      page.getByRole('button', { name: /synapse/i })
    );
    await synapseButton.click();
    await page.waitForURL(/.*synapse.*/);
  });

  test.afterEach(async () => {
    Mocks.reset();
  });

  test.describe('Skill Marketplace', () => {
    test('should display available skills', async ({ page }) => {
      const skillsContainer = page.locator('[data-testid="skills-marketplace"]').or(
        page.locator('.skills-marketplace')
      );

      const exists = await skillsContainer.count();
      if (exists > 0) {
        await expect(skillsContainer).toBeVisible();

        // Check for skill cards
        const skillCards = page.locator('[data-testid="skill-card"]').or(
          page.locator('.skill-card')
        );
        const cardCount = await skillCards.count();
        expect(cardCount).toBeGreaterThan(0);
      }
    });

    test('should list skills from marketplace', async ({ page }) => {
      const skillsList = page.locator('[data-testid="skills-list"]').or(
        page.locator('.skills-list')
      );

      const exists = await skillsList.count();
      if (exists > 0) {
        await expect(skillsList).toBeVisible();

        // Verify skill metadata is displayed
        const skillNames = page.locator('[data-testid="skill-name"]').or(
          page.locator('.skill-name')
        );
        const nameCount = await skillNames.count();
        expect(nameCount).toBeGreaterThan(0);
      }
    });

    test('should display skill details', async ({ page }) => {
      // Click on first skill card
      const firstSkill = page.locator('[data-testid="skill-card"]').or(
        page.locator('.skill-card')
      ).first();

      const exists = await firstSkill.count();
      if (exists > 0) {
        await firstSkill.click();

        // Wait for details panel
        await page.waitForTimeout(500);

        const detailsPanel = page.locator('[data-testid="skill-details"]').or(
          page.locator('.skill-details')
        );

        const detailsExists = await detailsPanel.count();
        if (detailsExists > 0) {
          await expect(detailsPanel).toBeVisible();

          // Check for skill metadata
          const skillDescription = page.locator('[data-testid="skill-description"]').or(
            page.locator('.skill-description')
          );
          const descExists = await skillDescription.count();
          if (descExists > 0) {
            await expect(skillDescription).toBeVisible();
          }
        }
      }
    });
  });

  test.describe('Skill Activation', () => {
    test('should activate a skill for an agent', async ({ page }) => {
      // Navigate to skills marketplace
      const skillsTab = page.getByRole('tab', { name: /skills/i }).or(
        page.getByRole('button', { name: /skills/i })
      );
      const tabExists = await skillsTab.count();
      if (tabExists > 0) {
        await skillsTab.click();
      }

      // Click activate button on first skill
      const activateButton = page.locator('[data-testid="activate-skill"]').or(
        page.getByRole('button', { name: /activate|install/i })
      ).first();

      const buttonExists = await activateButton.count();
      if (buttonExists > 0) {
        await activateButton.click();

        // Wait for activation to complete
        await page.waitForTimeout(1000);

        // Check for success toast
        const toast = page.locator('[data-sonner-toast]');
        const toastExists = await toast.count();
        if (toastExists > 0) {
          await expect(toast).toBeVisible();
          await expect(toast).toContainText(/activated|installed|success/i);
        }
      }
    });

    test('should show agent selector when activating skill', async ({ page }) => {
      const activateButton = page.locator('[data-testid="activate-skill"]').or(
        page.getByRole('button', { name: /activate/i })
      ).first();

      const buttonExists = await activateButton.count();
      if (buttonExists > 0) {
        await activateButton.click();

        // Check for agent selector modal
        const agentSelector = page.locator('[data-testid="agent-selector"]').or(
          page.locator('.agent-selector')
        );

        const selectorExists = await agentSelector.count();
        if (selectorExists > 0) {
          await expect(agentSelector).toBeVisible();

          // Verify agent options
          const agentOptions = page.locator('[data-testid="agent-option"]').or(
            page.locator('.agent-option')
          );
          const optionCount = await agentOptions.count();
          expect(optionCount).toBeGreaterThan(0);
        }
      }
    });

    test('should create skill instance after activation', async ({ page }) => {
      // Activate a skill
      const activateButton = page.locator('[data-testid="activate-skill"]').or(
        page.getByRole('button', { name: /activate/i })
      ).first();

      const buttonExists = await activateButton.count();
      if (buttonExists > 0) {
        await activateButton.click();

        // Select agent if selector appears
        const agentSelector = page.locator('[data-testid="agent-selector"]');
        const selectorExists = await agentSelector.count();
        if (selectorExists > 0) {
          const firstAgent = page.locator('[data-testid="agent-option"]').first();
          await firstAgent.click();
        }

        await page.waitForTimeout(1000);

        // Navigate to active instances
        const instancesTab = page.getByRole('tab', { name: /instances|active/i }).or(
          page.getByRole('button', { name: /instances/i })
        );
        const tabExists = await instancesTab.count();
        if (tabExists > 0) {
          await instancesTab.click();

          // Verify instance appears
          const instanceCard = page.locator('[data-testid="skill-instance"]').or(
            page.locator('.skill-instance')
          );
          const instanceExists = await instanceCard.count();
          if (instanceExists > 0) {
            await expect(instanceCard.first()).toBeVisible();
          }
        }
      }
    });
  });

  test.describe('Skill Deactivation', () => {
    test('should deactivate an active skill', async ({ page }) => {
      // Navigate to active instances
      const instancesTab = page.getByRole('tab', { name: /instances|active/i }).or(
        page.getByRole('button', { name: /instances/i })
      );
      const tabExists = await instancesTab.count();
      if (tabExists > 0) {
        await instancesTab.click();
      }

      // Click deactivate button on first instance
      const deactivateButton = page.locator('[data-testid="deactivate-skill"]').or(
        page.getByRole('button', { name: /deactivate|disable/i })
      ).first();

      const buttonExists = await deactivateButton.count();
      if (buttonExists > 0) {
        await deactivateButton.click();

        // Wait for deactivation
        await page.waitForTimeout(1000);

        // Check for success toast
        const toast = page.locator('[data-sonner-toast]');
        const toastExists = await toast.count();
        if (toastExists > 0) {
          await expect(toast).toContainText(/deactivated|disabled/i);
        }
      }
    });

    test('should update instance status after deactivation', async ({ page }) => {
      const instancesTab = page.getByRole('tab', { name: /instances|active/i }).or(
        page.getByRole('button', { name: /instances/i })
      );
      const tabExists = await instancesTab.count();
      if (tabExists > 0) {
        await instancesTab.click();

        const deactivateButton = page.locator('[data-testid="deactivate-skill"]').or(
          page.getByRole('button', { name: /deactivate|disable/i })
        ).first();

        const buttonExists = await deactivateButton.count();
        if (buttonExists > 0) {
          await deactivateButton.click();
          await page.waitForTimeout(1000);

          // Check status indicator
          const statusIndicator = page.locator('[data-testid="instance-status"]').or(
            page.locator('.instance-status')
          ).first();

          const indicatorExists = await statusIndicator.count();
          if (indicatorExists > 0) {
            const status = await statusIndicator.textContent();
            expect(status?.toLowerCase()).toMatch(/disabled|inactive/i);
          }
        }
      }
    });
  });

  test.describe('Skill Persistence', () => {
    test('should persist skill instances after page reload', async ({ page }) => {
      // Activate a skill
      const activateButton = page.locator('[data-testid="activate-skill"]').or(
        page.getByRole('button', { name: /activate/i })
      ).first();

      const buttonExists = await activateButton.count();
      if (buttonExists > 0) {
        await activateButton.click();

        // Select agent if needed
        const agentSelector = page.locator('[data-testid="agent-selector"]');
        const selectorExists = await agentSelector.count();
        if (selectorExists > 0) {
          const firstAgent = page.locator('[data-testid="agent-option"]').first();
          await firstAgent.click();
        }

        await page.waitForTimeout(1000);

        // Reload page
        await page.reload();
        await Helpers.waitForAppReady(page);

        // Navigate to instances
        const instancesTab = page.getByRole('tab', { name: /instances|active/i }).or(
          page.getByRole('button', { name: /instances/i })
        );
        const tabExists = await instancesTab.count();
        if (tabExists > 0) {
          await instancesTab.click();

          // Verify instance still exists
          const instanceCard = page.locator('[data-testid="skill-instance"]').or(
            page.locator('.skill-instance')
          );
          const instanceExists = await instanceCard.count();
          if (instanceExists > 0) {
            await expect(instanceCard.first()).toBeVisible();
          }
        }
      }
    });

    test('should maintain skill configuration after reload', async ({ page }) => {
      // This test assumes skill has been configured
      const instancesTab = page.getByRole('tab', { name: /instances|active/i }).or(
        page.getByRole('button', { name: /instances/i })
      );
      const tabExists = await instancesTab.count();
      if (tabExists > 0) {
        await instancesTab.click();

        // Get initial configuration
        const configPanel = page.locator('[data-testid="skill-config"]').or(
          page.locator('.skill-config')
        ).first();

        const configExists = await configPanel.count();
        if (configExists > 0) {
          const initialConfig = await configPanel.textContent();

          // Reload page
          await page.reload();
          await Helpers.waitForAppReady(page);

          await instancesTab.click();

          // Verify configuration is maintained
          const configPanelAfter = page.locator('[data-testid="skill-config"]').or(
            page.locator('.skill-config')
          ).first();

          const configAfterExists = await configPanelAfter.count();
          if (configAfterExists > 0) {
            const configAfter = await configPanelAfter.textContent();
            expect(configAfter).toBe(initialConfig);
          }
        }
      }
    });
  });

  test.describe('Error Handling', () => {
    test('should handle non-existent skill activation gracefully', async ({ page }) => {
      // Mock scenario with non-existent skill
      Mocks.scenarios.skillNotFound();

      const activateButton = page.locator('[data-testid="activate-skill"]').or(
        page.getByRole('button', { name: /activate/i })
      ).first();

      const buttonExists = await activateButton.count();
      if (buttonExists > 0) {
        await activateButton.click();

        // Wait for error response
        await page.waitForTimeout(1000);

        // Check for error message
        const errorMessage = page.locator('[data-testid="error-message"]').or(
          page.locator('.error-message').or(
            page.getByText(/skill.*not.*found|error/i)
          )
        );

        const errorExists = await errorMessage.count();
        if (errorExists > 0) {
          await expect(errorMessage).toBeVisible();
        }
      }
    });

    test('should show error toast on activation failure', async ({ page }) => {
      // Mock scenario with activation error
      Mocks.scenarios.skillActivationError();

      const activateButton = page.locator('[data-testid="activate-skill"]').or(
        page.getByRole('button', { name: /activate/i })
      ).first();

      const buttonExists = await activateButton.count();
      if (buttonExists > 0) {
        await activateButton.click();

        await page.waitForTimeout(1000);

        // Check for error toast
        const toast = page.locator('[data-sonner-toast]');
        const toastExists = await toast.count();
        if (toastExists > 0) {
          await expect(toast).toContainText(/error|failed/i);
        }
      }
    });

    test('should handle invalid agent selection', async ({ page }) => {
      const activateButton = page.locator('[data-testid="activate-skill"]').or(
        page.getByRole('button', { name: /activate/i })
      ).first();

      const buttonExists = await activateButton.count();
      if (buttonExists > 0) {
        await activateButton.click();

        // Try to activate without selecting agent
        const confirmButton = page.getByRole('button', { name: /confirm|activate/i });
        const confirmExists = await confirmButton.count();
        if (confirmExists > 0) {
          await confirmButton.click();

          await page.waitForTimeout(500);

          // Check for validation error
          const validationError = page.locator('[data-testid="validation-error"]').or(
            page.locator('.validation-error')
          );

          const errorExists = await validationError.count();
          if (errorExists > 0) {
            await expect(validationError).toBeVisible();
          }
        }
      }
    });
  });

  test.describe('Accessibility', () => {
    test('should have proper ARIA labels on skill cards', async ({ page }) => {
      const skillCards = page.locator('[data-testid="skill-card"]').or(
        page.locator('.skill-card')
      );

      const cardCount = await skillCards.count();
      if (cardCount > 0) {
        const firstCard = skillCards.first();
        const ariaLabel = await firstCard.getAttribute('aria-label');
        expect(ariaLabel || await firstCard.getAttribute('aria-labelledby')).toBeTruthy();
      }
    });

    test('should be keyboard navigable', async ({ page }) => {
      // Tab through skill cards
      await page.keyboard.press('Tab');
      await page.keyboard.press('Tab');

      const focused = await page.evaluate(() => document.activeElement?.tagName);
      expect(['BUTTON', 'DIV', 'A']).toContain(focused);
    });

    test('should announce skill activation to screen readers', async ({ page }) => {
      const activateButton = page.locator('[data-testid="activate-skill"]').or(
        page.getByRole('button', { name: /activate/i })
      ).first();

      const buttonExists = await activateButton.count();
      if (buttonExists > 0) {
        await activateButton.click();

        // Check for ARIA live region
        const liveRegion = page.locator('[aria-live]');
        const liveRegionExists = await liveRegion.count();
        if (liveRegionExists > 0) {
          await expect(liveRegion).toBeVisible();
        }
      }
    });
  });
});

