/**
 * Mock with delays to test loading states
 */
export async function mockTauriCommandsWithDelay(page: Page, delay: number = 1000) {
  const delayedHandlers = Object.fromEntries(
    Object.entries(commandHandlers).map(([cmd, handler]) => [
      cmd,
      async (args: any) => {
        await new Promise((resolve) => setTimeout(resolve, delay));
        return handler(args);
      },
    ])
  );

  await mockTauriCommands(page, delayedHandlers);
}

/**
 * Mock with errors to test error handling
 */
export async function mockTauriCommandsWithErrors(
  page: Page,
  errorCommands: string[] = []
) {
  const errorHandlers = Object.fromEntries(
    errorCommands.map((cmd) => [
      cmd,
      async () => {
        throw new Error(`Mock error for command: ${cmd}`);
      },
    ])
  );

  await mockTauriCommands(page, errorHandlers);
}

