import React, { useEffect, useMemo, useState } from 'react';
import { Download, Loader2, Search, Shield, X, Zap } from '@/components/ui/HearthlinkIcons';
import { MarketplaceBridge, MarketplaceEntry, InstallStatus } from '@/bridges/MarketplaceBridge';

export interface SkillMarketplaceProps {
  personaId?: string;
  personaName?: string;
  onSkillToggle?: (skillId: string, enabled: boolean) => void;
}

const statusLabel = (status: InstallStatus) => {
  if (typeof status === 'string') return status;
  return 'failed';
};

const statusMessage = (status: InstallStatus) => {
  if (typeof status === 'string') return null;
  return status.failed;
};

// Extracted Sub-component for Action Logic (KISS Rule 1: Screen Test)
const PluginActionButtons: React.FC<{
  entry: MarketplaceEntry;
  operatingId: string | null;
  onInstall: (id: string) => void;
  onUninstall: (id: string) => void;
}> = ({ entry, operatingId, onInstall, onUninstall }) => {
  const { metadata, status } = entry;
  const isBusy = operatingId === metadata.id || status === 'installing';
  const label = statusLabel(status);

  if (isBusy) {
    return (
      <span className="inline-flex items-center gap-2 text-[11px] text-slate-400">
        <Loader2 className="w-3 h-3 animate-spin" />
        Working...
      </span>
    );
  }

  const btnBase = "px-3 py-1.5 rounded-md text-[10px] font-bold uppercase tracking-wider transition-all";
  const btnInstall = "bg-alden-gold/15 text-alden-gold border border-alden-gold/30 hover:bg-alden-gold/25";
  const btnUninstall = "bg-alden-cyan/20 text-alden-cyan border border-alden-cyan/40 hover:bg-alden-cyan/30";
  const btnUpdate = "bg-emerald-500/15 text-emerald-300 border border-emerald-500/30 hover:bg-emerald-500/25";

  if (label === 'available') {
    return (
      <button onClick={() => onInstall(metadata.id)} className={`${btnBase} ${btnInstall}`}>
        Install
      </button>
    );
  }
  if (label === 'installed') {
    return (
      <button onClick={() => onUninstall(metadata.id)} className={`${btnBase} ${btnUninstall}`}>
        Uninstall
      </button>
    );
  }
  if (label === 'update_available') {
    return (
      <button onClick={() => onInstall(metadata.id)} className={`${btnBase} ${btnUpdate}`}>
        Update
      </button>
    );
  }

  return (
    <button onClick={() => onInstall(metadata.id)} className={`${btnBase} ${btnInstall}`}>
      Retry
    </button>
  );
};

export const SkillMarketplace: React.FC<SkillMarketplaceProps> = ({ personaName = 'Persona' }) => {
  const [plugins, setPlugins] = useState<MarketplaceEntry[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [operatingId, setOperatingId] = useState<string | null>(null);
  const [searchQuery, setSearchQuery] = useState('');

  const loadPlugins = async () => {
    setLoading(true);
    setError(null);
    try {
      const data = await MarketplaceBridge.list();
      setPlugins(data);
    } catch (err) {
      console.error(err);
      setError('Unable to load marketplace plugins right now.');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    void loadPlugins();
  }, []);

  const handleInstall = async (pluginId: string) => {
    setOperatingId(pluginId);
    setError(null);
    try {
      await MarketplaceBridge.install(pluginId);
      await loadPlugins();
    } catch (err) {
      console.error(err);
      setError('Install failed. Please try again.');
    } finally {
      setOperatingId(null);
    }
  };

  const handleUninstall = async (pluginId: string) => {
    setOperatingId(pluginId);
    setError(null);
    try {
      await MarketplaceBridge.uninstall(pluginId);
      await loadPlugins();
    } catch (err) {
      console.error(err);
      setError('Uninstall failed. Please try again.');
    } finally {
      setOperatingId(null);
    }
  };

  const filteredPlugins = useMemo(() => {
    if (!searchQuery) return plugins;
    const query = searchQuery.toLowerCase();
    return plugins.filter(({ metadata }) => {
      return (
        metadata.name.toLowerCase().includes(query) ||
        metadata.description.toLowerCase().includes(query) ||
        metadata.category.toLowerCase().includes(query) ||
        metadata.author.toLowerCase().includes(query)
      );
    });
  }, [plugins, searchQuery]);

  if (loading && plugins.length === 0) {
    return (
      <div className="flex flex-col items-center justify-center py-12">
        <div
          className="w-12 h-12 rounded-full animate-pulse mb-4"
          style={{ backgroundColor: 'var(--mimic-accent)', opacity: 0.3 }}
        />
        <p className="text-sm text-muted-foreground">Loading marketplace...</p>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      <div className="flex flex-col sm:flex-row sm:items-center sm:justify-between gap-4">
        <div>
          <h2 className="text-xl font-semibold text-foreground">Skill Marketplace</h2>
          <p className="text-sm text-muted-foreground">
            {plugins.length} plugins available {personaName ? `for ${personaName}` : ''}
          </p>
        </div>
        <div className="relative max-w-xs w-full">
          <Search className="absolute left-3 top-1/2 -translate-y-1/2 w-4 h-4 text-muted-foreground" />
          <input
            type="text"
            placeholder="Search plugins..."
            value={searchQuery}
            onChange={(event) => setSearchQuery(event.target.value)}
            className="w-full pl-9 pr-4 py-2 text-sm rounded-lg border bg-background text-foreground placeholder:text-muted-foreground focus:outline-none focus:ring-2"
            style={{
              borderColor: 'var(--border)',
              // @ts-expect-error CSS custom property
              '--tw-ring-color': 'var(--mimic-accent)',
            }}
          />
        </div>
      </div>

      {error && (
        <div
          className="p-3 rounded-lg flex items-center gap-2"
          style={{
            backgroundColor: 'color-mix(in oklch, var(--destructive) 15%, transparent)',
            color: 'var(--destructive)',
          }}
        >
          <X className="w-4 h-4" />
          <span className="text-sm">{error}</span>
        </div>
      )}

      <div className="grid grid-cols-1 md:grid-cols-2 xl:grid-cols-3 gap-4" data-testid="skills-list">
        {filteredPlugins.length === 0 && !loading ? (
          <div className="col-span-full text-center text-sm text-slate-400 py-12 italic">
            No plugins match your search.
          </div>
        ) : null}

        {filteredPlugins.map((entry) => {
          const { metadata, status } = entry;
          const badgeStatus = statusLabel(status);
          const failure = statusMessage(status);
          return (
            <div
              key={metadata.id}
              className="bg-black/35 border border-white/10 rounded-xl p-4 hover:border-alden-cyan/40 hover:bg-black/50 transition-all"
            >
              <div className="flex items-start justify-between gap-3">
                <div>
                  <h3 className="text-sm font-semibold text-slate-100">{metadata.name}</h3>
                  <p className="text-[11px] text-slate-500 mt-1">{metadata.description}</p>
                </div>
                <span className="px-2 py-0.5 rounded-full bg-white/5 text-[10px] text-slate-400 border border-white/10">
                  v{metadata.version}
                </span>
              </div>

              <div className="flex flex-wrap gap-2 mt-3 text-[10px] uppercase tracking-widest">
                <span className="px-2 py-1 rounded-full border border-alden-cyan/30 text-alden-cyan/80">
                  {metadata.category}
                </span>
                <span className="px-2 py-1 rounded-full border border-white/10 text-slate-400">
                  {metadata.trust_level}
                </span>
                {badgeStatus === 'update_available' && (
                  <span className="px-2 py-1 rounded-full border border-emerald-500/30 text-emerald-300">
                    Update Available
                  </span>
                )}
              </div>

              <div className="mt-3 text-[11px] text-slate-500 flex items-center gap-2">
                <Shield className="w-3 h-3" />
                <span>{metadata.permissions.length} permissions</span>
                <Zap className="w-3 h-3" />
                <span>{metadata.dependencies.length} dependencies</span>
              </div>

              {failure ? (
                <div className="mt-2 text-[11px] text-red-400 flex items-center gap-1">
                  <X className="w-3 h-3" />
                  {failure}
                </div>
              ) : null}

              <div className="flex items-center justify-between mt-4 pt-3 border-t border-white/10">
                <span className="text-[10px] text-slate-400 flex items-center gap-1">
                  <Download className="w-3 h-3" />
                  {metadata.author}
                </span>
                <PluginActionButtons
                  entry={entry}
                  operatingId={operatingId}
                  onInstall={handleInstall}
                  onUninstall={handleUninstall}
                />
              </div>
            </div>
          );
        })}
      </div>
    </div>
  );
};

export default SkillMarketplace;

