import React, { useEffect, useMemo, useState } from 'react';
import {
  Activity,
  AlertCircle,
  CheckCircle2,
  Clock,
  Filter,
  History,
  Loader2,
  Search,
} from '@/components/ui/HearthlinkIcons';
import AISkillBridge, { type AISkillResponse } from '@/ai_system_integration/ai_skill_bridge';

type ExecutionStatus = 'success' | 'failed' | 'running';

interface SkillExecution {
  id: string;
  skillName: string;
  status: ExecutionStatus;
  executionTimeMs: number;
  agents: string[];
  suggestions: number;
  timestamp: string;
}

const statusBadge = (status: ExecutionStatus) => {
  switch (status) {
    case 'success':
      return 'bg-emerald-500/15 text-emerald-300 border-emerald-500/30';
    case 'failed':
      return 'bg-red-500/15 text-red-300 border-red-500/30';
    default:
      return 'bg-amber-500/15 text-amber-200 border-amber-500/30';
  }
};

const statusLabel = (status: ExecutionStatus) => {
  switch (status) {
    case 'success':
      return 'Success';
    case 'failed':
      return 'Failed';
    default:
      return 'Running';
  }
};

export const SkillExecutionMonitor: React.FC = () => {
  const [executions, setExecutions] = useState<SkillExecution[]>([]);
  const [searchQuery, setSearchQuery] = useState('');
  const [statusFilter, setStatusFilter] = useState<ExecutionStatus | 'all'>('all');
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    let mounted = true;
    const loadLocalSessions = async () => {
      try {
        const instance = AISkillBridge.getInstance();
        const props = await instance.getProps();
        const derived = props.activeSessions.flatMap(([key, session]) => {
          const messages = session.context.conversationHistory || [];
          return messages
            .filter((message) => message.role === 'assistant' && message.skillUsed)
            .map((message, index) => ({
              id: `${key}-${index}`,
              skillName: message.skillUsed || 'unknown',
              status: 'success' as ExecutionStatus,
              executionTimeMs: 0,
              agents: [],
              suggestions: 0,
              timestamp: message.timestamp || new Date().toISOString(),
            }));
        });
        if (mounted && derived.length > 0) {
          setExecutions(derived);
        }
      } catch (err) {
        console.error('Failed to read local skill sessions:', err);
      } finally {
        if (mounted) setLoading(false);
      }
    };

    loadLocalSessions();
    return () => {
      mounted = false;
    };
  }, []);

  useEffect(() => {
    const handleSkillEvent = (payload: unknown) => {
      const responsePayload = payload as { conversationId?: string; response?: AISkillResponse };
      if (!responsePayload.response) return;
      const response = responsePayload.response;
      const primarySkill = response.skillsUsed[0] || 'unknown';
      const newExecution: SkillExecution = {
        id: `${responsePayload.conversationId || 'session'}-${Date.now()}`,
        skillName: primarySkill,
        status: response.success ? 'success' : 'failed',
        executionTimeMs: response.executionTimeMs || 0,
        agents: response.agentsCoordinated || [],
        suggestions: response.suggestions?.length || 0,
        timestamp: new Date().toISOString(),
      };
      setExecutions((prev) => [newExecution, ...prev].slice(0, 30));
    };

    AISkillBridge.addEventListener('skill-response', handleSkillEvent);
    return () => {
      AISkillBridge.removeEventListener('skill-response', handleSkillEvent);
    };
  }, []);

  const filteredExecutions = useMemo(() => {
    const query = searchQuery.trim().toLowerCase();
    return executions.filter((execution) => {
      const matchesQuery = !query || execution.skillName.toLowerCase().includes(query);
      const matchesStatus = statusFilter === 'all' || execution.status === statusFilter;
      return matchesQuery && matchesStatus;
    });
  }, [executions, searchQuery, statusFilter]);

  const summary = useMemo(() => {
    const total = executions.length;
    const successCount = executions.filter((execution) => execution.status === 'success').length;
    const avgTime =
      executions.length > 0
        ? Math.round(executions.reduce((sum, item) => sum + item.executionTimeMs, 0) / executions.length)
        : 0;
    return { total, successCount, avgTime };
  }, [executions]);

  return (
    <div className="h-full flex flex-col space-y-5 min-h-0">
      <div className="flex flex-col gap-3 md:flex-row md:items-center md:justify-between">
        <div>
          <h2 className="text-lg font-semibold text-alden-gold uppercase tracking-widest font-orbitron-force flex items-center gap-2">
            <Activity size={18} />
            Skill Execution Monitor
          </h2>
          <p className="text-xs text-slate-400 mt-1">
            Track live skill activity and execution outcomes.
          </p>
        </div>
        <div className="flex items-center gap-4 text-[11px] uppercase tracking-widest text-slate-500">
          <div className="flex items-center gap-2">
            <CheckCircle2 size={14} className="text-emerald-400" />
            {summary.successCount}/{summary.total} success
          </div>
          <div className="flex items-center gap-2">
            <Clock size={14} className="text-cyan-400" />
            {summary.avgTime}ms avg
          </div>
        </div>
      </div>

      <div className="grid grid-cols-1 md:grid-cols-3 gap-3">
        <div className="flex items-center gap-2 px-4 py-3 bg-black/40 border border-white/10 rounded-lg">
          <History size={16} className="text-alden-cyan" />
          <div>
            <div className="text-xs text-slate-500 uppercase tracking-widest">Executions</div>
            <div className="text-lg font-semibold text-slate-100">{summary.total}</div>
          </div>
        </div>
        <div className="flex items-center gap-2 px-4 py-3 bg-black/40 border border-white/10 rounded-lg">
          <CheckCircle2 size={16} className="text-emerald-400" />
          <div>
            <div className="text-xs text-slate-500 uppercase tracking-widest">Success Rate</div>
            <div className="text-lg font-semibold text-slate-100">
              {summary.total > 0 ? Math.round((summary.successCount / summary.total) * 100) : 0}%
            </div>
          </div>
        </div>
        <div className="flex items-center gap-2 px-4 py-3 bg-black/40 border border-white/10 rounded-lg">
          <Clock size={16} className="text-cyan-400" />
          <div>
            <div className="text-xs text-slate-500 uppercase tracking-widest">Average Time</div>
            <div className="text-lg font-semibold text-slate-100">{summary.avgTime}ms</div>
          </div>
        </div>
      </div>

      <div className="flex flex-col gap-3 md:flex-row md:items-center">
        <div className="relative flex-1">
          <Search className="absolute left-3 top-1/2 -translate-y-1/2 text-slate-500" size={16} />
          <input
            type="text"
            placeholder="Search execution history..."
            className="w-full pl-9 pr-4 py-2.5 bg-black/40 border border-white/10 rounded-lg text-slate-200 placeholder:text-slate-500 focus:outline-none focus:border-alden-cyan/60 focus:bg-black/60 transition-all"
            value={searchQuery}
            onChange={(event) => setSearchQuery(event.target.value)}
          />
        </div>
        <div className="flex items-center gap-2">
          <Filter size={14} className="text-slate-500" />
          <select
            value={statusFilter}
            onChange={(event) => setStatusFilter(event.target.value as typeof statusFilter)}
            className="bg-[#0b1221] border border-white/10 rounded px-3 py-2 text-xs text-[#9fb4d6] focus:outline-none focus:border-alden-cyan/80"
            style={{ colorScheme: 'dark' }}
          >
            <option value="all">All Status</option>
            <option value="success">Success</option>
            <option value="failed">Failed</option>
            <option value="running">Running</option>
          </select>
        </div>
      </div>

      {loading && (
        <div className="flex items-center gap-2 text-sm text-slate-400">
          <Loader2 size={16} className="text-alden-cyan" />
          Loading execution history...
        </div>
      )}

      <div className="space-y-3 overflow-y-auto pr-2 flex-1 scrollbar-thin scrollbar-thumb-alden-gold/40 scrollbar-track-transparent">
        {!loading && filteredExecutions.length === 0 && (
          <div className="text-center text-sm text-slate-400 py-12 italic">
            No skill executions recorded yet.
          </div>
        )}

        {filteredExecutions.map((execution) => (
          <div
            key={execution.id}
            className="p-4 rounded-lg border border-white/10 bg-black/35 hover:border-alden-cyan/40 transition-all"
          >
            <div className="flex items-start justify-between gap-3">
              <div>
                <div className="text-sm font-semibold text-slate-100">{execution.skillName}</div>
                <div className="text-xs text-slate-500 mt-1">
                  {new Date(execution.timestamp).toLocaleString()}
                </div>
              </div>
              <span
                className={`px-2 py-0.5 rounded-full text-[10px] uppercase tracking-widest border ${statusBadge(
                  execution.status,
                )}`}
              >
                {statusLabel(execution.status)}
              </span>
            </div>

            <div className="flex flex-wrap gap-4 text-xs text-slate-400 mt-3">
              <div className="flex items-center gap-1.5">
                <Clock size={12} />
                {execution.executionTimeMs}ms
              </div>
              <div className="flex items-center gap-1.5">
                <CheckCircle2 size={12} />
                {execution.suggestions} follow-ups
              </div>
              <div className="flex items-center gap-1.5">
                <Activity size={12} />
                {execution.agents.length > 0 ? execution.agents.join(', ') : 'No agents linked'}
              </div>
            </div>

            {execution.status === 'failed' && (
              <div className="mt-3 text-xs text-red-200 flex items-center gap-2">
                <AlertCircle size={12} />
                Execution failed. Review logs for details.
              </div>
            )}
          </div>
        ))}
      </div>
    </div>
  );
};

export default SkillExecutionMonitor;

