export interface AldenCommandCenterProps {
  userId: string;
  onBackToLauncher?: () => void;
  onNavigateToModule?: (moduleName: string) => void;
  onFocusAssist?: () => void;
}

type ViewMode = 'overview' | 'tasks' | 'calendar' | 'email' | 'skills' | 'chat' | 'terminal';
type DashboardPreset = 'standard' | 'mission' | 'schedule' | 'compact';
type DashboardTileId =
  | 'counter-tasks'
  | 'counter-schedule'
  | 'counter-inbox'
  | 'counter-uptime'
  | 'panel-mission'
  | 'panel-schedule';

const FRAME_PADDING = 16;
const TILE_GAP = 12;
const MIN_Y_BUFFER = 120;
const ROW_HEIGHT = 180;
const ROW_GAP = 24;
const ROW0_Y = FRAME_PADDING + MIN_Y_BUFFER;
const ROW1_Y = ROW0_Y + ROW_HEIGHT + ROW_GAP;

interface DashboardLayoutItem {
  id: DashboardTileId;
  x: number;
  y: number;
  width: number;
  height: number;
  locked?: boolean;
}

const DEFAULT_DASHBOARD_LAYOUT: DashboardLayoutItem[] = [
  { id: 'counter-tasks', x: FRAME_PADDING, y: ROW0_Y, width: 240, height: 180, locked: true },
  { id: 'counter-schedule', x: 272, y: ROW0_Y, width: 240, height: 180, locked: true },
  { id: 'counter-inbox', x: 528, y: ROW0_Y, width: 240, height: 180, locked: true },
  { id: 'counter-uptime', x: 784, y: ROW0_Y, width: 240, height: 180, locked: true },
  { id: 'panel-mission', x: FRAME_PADDING, y: ROW1_Y, width: 752, height: 240, locked: true },
  { id: 'panel-schedule', x: 784, y: ROW1_Y, width: 240, height: 240, locked: true },
];

// Helper function for layout packing
const packLayout = (items: DashboardLayoutItem[]): DashboardLayoutItem[] => {
  // Separate counters and panels but maintain their relative order from the input array
  const counters = items.filter(i => i.id.startsWith('counter'));
  const panels = items.filter(i => !i.id.startsWith('counter'));

  let xCounter = FRAME_PADDING;
  const packedCounters = counters.map(item => {
    const packed = { ...item, x: xCounter, y: ROW0_Y };
    xCounter += item.width + TILE_GAP;
    return packed;
  });

  let xPanel = FRAME_PADDING;
  const packedPanels = panels.map(item => {
    const packed = { ...item, x: xPanel, y: ROW1_Y };
    xPanel += item.width + TILE_GAP;
    return packed;
  });

  const packed = [...packedCounters, ...packedPanels];
  // Return items in the same order as DEFAULT_DASHBOARD_LAYOUT to ensure consistent rendering
  return DEFAULT_DASHBOARD_LAYOUT.map(def => packed.find(i => i.id === def.id) || def);
};

// Memoized MetricCard component
// MetricCard replaced with shared component

export const AldenCommandCenterOptimized: React.FC<AldenCommandCenterProps> = ({ userId, onBackToLauncher, onNavigateToModule, onFocusAssist }) => {
  const [activeView, setActiveView] = useState<ViewMode>('overview');
  
  
  const handleTabChange = useCallback((tabId: string) => {
    setActiveView(tabId as ViewMode);
  }, []);
  
  const [isSidebarCollapsed, setIsSidebarCollapsed] = useState(false);
  const [tasksViewMode, setTasksViewMode] = useState<'list' | 'card'>('card');
  const [tasksSortBy, setTasksSortBy] = useState<'priority' | 'date' | 'status'>('priority');
  const dashboardPreset: DashboardPreset = 'standard';
  const [tasks, setTasks] = useState<Task[]>([]);
  const [events, setEvents] = useState<CalendarEvent[]>([]);
  const [isTaskModalOpen, setIsTaskModalOpen] = useState(false);
  const [isEventModalOpen, setIsEventModalOpen] = useState(false);
  const [selectedTask, setSelectedTask] = useState<Task | null>(null);
  const [selectedEvent, setSelectedEvent] = useState<CalendarEvent | null>(null);
  const [showSettings, setShowSettings] = useState(false);
  const isConnected = true;
  const [dashboardLayout, setDashboardLayout] = useState<DashboardLayoutItem[]>(() => packLayout(DEFAULT_DASHBOARD_LAYOUT));
  const { isZenMode } = useZenMode();

  // Chat Integration
  const moduleId = 'alden';
  const residentPersonaId = (MODULE_RESIDENT_AI[moduleId] || 'alden') as SystemPersonaId;
  const {
    personas: chatPersonas,
    privateMessages,
    privateLoading,
    privateError,
    privateTyping,
    sendPrivateMessage,
  } = useChatStateWithOrchestration({
    userId,
    moduleId,
    residentPersonaId,
    onError: (err) => console.error('Chat Error:', err),
  });

  const residentPersona = chatPersonas.find(p => p.id === residentPersonaId);

  // Memoized packLayout function
  const memoizedPackLayout = useCallback((items: DashboardLayoutItem[]) => packLayout(items), []);

  const aldenBridge = useRef<AldenBridge | null>(null);
  const [stats, setStats] = useState({
    totalTasks: 0,
    completedToday: 0,
    overdue: 0,
    upcomingEvents: 0,
    unreadEmails: 0,
  });

  // Memoized style objects to prevent recreation
  const frameStyle = useMemo(() => isZenMode ? {
    border: '2px solid #334155', // Slate-700
    boxShadow: 'none',
    background: '#0f172a', // Slate-900
  } : {
    border: '2px solid rgba(197,160,89,0.75)',
    boxShadow: '0 0 22px rgba(0,240,255,0.18), inset 0 0 18px rgba(0,0,0,0.55)',
    background: 'linear-gradient(135deg, rgba(4,10,20,0.92), rgba(6,14,28,0.82))',
  }, [isZenMode]);

  // Memoized pill functions
  const priorityPill = useCallback((priority?: string) => {
    switch (priority) {
      case 'high': return 'bg-[#f97316]/20 text-[#f97316] border border-[#f97316]/60';
      case 'critical': return 'bg-[#ef4444]/20 text-[#ef4444] border border-[#ef4444]/60';
      case 'medium': return 'bg-[#0ef]/10 text-[#0ef] border border-[#0ef]/50';
      default: return 'bg-white/5 text-[#8fa7c7] border border-white/10';
    }
  }, []);

  const statusPill = useCallback((status?: string) => {
    switch (status) {
      case 'completed': return 'bg-emerald-500/15 text-emerald-300 border border-emerald-400/50';
      case 'in progress': return 'bg-[#0ef]/12 text-[#0ef] border border-[#0ef]/45';
      case 'queued': return 'bg-[#f59e0b]/15 text-[#fcd34d] border border-[#f59e0b]/50';
      default: return 'bg-white/5 text-[#8fa7c7] border border-white/10';
    }
  }, []);

  // Optimized bridge initialization with proper cleanup
  const loadDashboardData = useCallback(async () => {
    const bridge = aldenBridge.current;
    if (!bridge) return;

    try {
      const taskResponse = await bridge.getTasks();
      if (taskResponse.success) {
        setTasks(taskResponse.data);
        const today = new Date().setHours(0, 0, 0, 0);
        const completedToday = taskResponse.data.filter(
          t => t.status === 'completed' && new Date(t.updated_at || '').setHours(0, 0, 0, 0) === today
        ).length;
        const overdue = taskResponse.data.filter(
          t => t.status !== 'completed' && t.due_date && new Date(t.due_date) < new Date()
        ).length;
        setStats(prev => ({
          ...prev,
          totalTasks: taskResponse.data.filter(t => t.status !== 'completed').length,
          completedToday,
          overdue,
        }));
      }

      const calendarResponse = await bridge.getCalendarEvents(new Date(), new Date(Date.now() + 7 * 24 * 60 * 60 * 1000));
      if (calendarResponse.success) {
        setEvents(calendarResponse.data);
        setStats(prev => ({ ...prev, upcomingEvents: calendarResponse.data.length }));
      }

      const emailResponse = await bridge.getEmailMessages('default', 'inbox', 50, 1); 
      if (emailResponse.success) {
        setStats(prev => ({ ...prev, unreadEmails: emailResponse.data?.filter(m => !m.read).length || 0 }));
      }
    } catch (err) {
      console.error('Failed to load Alden dashboard data:', err);
    }
  }, [userId]);

  useEffect(() => {
    const bridge = new AldenBridge({ userId });
    aldenBridge.current = bridge;

    const initializeBridge = async () => {
      try {
        await bridge.initialize();
        await loadDashboardData();

        // Emit tutorial event for Alden opening
        emitTutorialEvent('hl:event:alden:opened');
      } catch (err) {
        console.error('Failed to initialize Alden bridge:', err);
      }
    };

    initializeBridge();

    return () => {
      try {
        bridge.destroy();
      } catch (e) {
        console.warn('Failed to destroy Alden bridge:', e);
      }
      aldenBridge.current = null;
    };
  }, [userId, memoizedPackLayout, loadDashboardData]);

  const handleTaskCreated = useCallback((taskUpdates: Partial<Task>) => {
    const newTask: Task = {
      id: Date.now().toString(),
      title: taskUpdates.title || 'New Task',
      description: taskUpdates.description || '',
      status: taskUpdates.status || 'todo',
      priority: taskUpdates.priority || 'medium',
      created_at: new Date().toISOString(),
      updated_at: new Date().toISOString(),
      ...taskUpdates,
    } as Task;
    setTasks(prev => [...prev, newTask]);
  }, []);

  const handleEventCreated = useCallback((eventUpdates: Partial<CalendarEvent>) => {
    const newEvent: CalendarEvent = {
      id: Date.now().toString(),
      title: eventUpdates.title || 'New Event',
      description: eventUpdates.description || '',
      start_time: eventUpdates.start_time || new Date().toISOString(),
      end_time: eventUpdates.end_time || new Date().toISOString(),
      created_at: new Date().toISOString(),
      updated_at: new Date().toISOString(),
      ...eventUpdates,
    } as CalendarEvent;
    setEvents(prev => [...prev, newEvent]);
  }, []);

  const handleTaskUpdate = useCallback((taskId: string, updates: Partial<Task>) => {
    setTasks(prev => prev.map(t => t.id === taskId ? { ...t, ...updates, updated_at: new Date().toISOString() } : t));
    setSelectedTask(null);
  }, []);

  const handleEventUpdate = useCallback((eventId: string, updates: Partial<CalendarEvent>) => {
    setEvents(prev => prev.map(e => e.id === eventId ? { ...e, ...updates, updated_at: new Date().toISOString() } : e));
    setSelectedEvent(null);
  }, []);

  const handleDeleteTask = useCallback((taskId: string) => {
    setTasks(prev => prev.filter(t => t.id !== taskId));
    setSelectedTask(null);
  }, []);

  const handleDeleteEvent = useCallback((eventId: string) => {
    setEvents(prev => prev.filter(e => e.id !== eventId));
    setSelectedEvent(null);
  }, []);

  const sortedTasks = useMemo(() => {
    return [...tasks].sort((a, b) => {
      if (tasksSortBy === 'priority') {
        const priorityOrder: Record<string, number> = { critical: 0, high: 1, medium: 2, low: 3 };
        return (priorityOrder[a.priority || 'medium'] ?? 2) - (priorityOrder[b.priority || 'medium'] ?? 2);
      }
      if (tasksSortBy === 'status') {
        const statusOrder: Record<string, number> = { 'in progress': 0, todo: 1, queued: 2, completed: 3 };
        return (statusOrder[a.status || 'todo'] ?? 1) - (statusOrder[b.status || 'todo'] ?? 1);
      }
      if (tasksSortBy === 'date') {
        return new Date(a.due_date || '9999-12-31').getTime() - new Date(b.due_date || '9999-12-31').getTime();
      }
      return 0;
    });
  }, [tasks, tasksSortBy]);

  // Memoized dashboard canvas height calculation
  const dashboardCanvasHeight = useMemo(() => {
    const counters = dashboardLayout.filter(i => i.id.startsWith('counter')).map(i => i.height);
    const panels = dashboardLayout.filter(i => !i.id.startsWith('counter')).map(i => i.height);
    const maxRow0 = Math.max(0, ...counters);
    const maxRow1 = Math.max(0, ...panels);
    const bottom = Math.max(ROW0_Y + maxRow0, ROW1_Y + maxRow1);
    return Math.max(720, bottom + FRAME_PADDING * 2);
  }, [dashboardLayout]);

  const toggleTileLock = useCallback((id: DashboardTileId) => {
    setDashboardLayout(prev => prev.map(item => item.id === id ? { ...item, locked: !item.locked } : item));
  }, []);

  // Update dashboard layout when needed
  const updateDashboardLayout = useCallback((items: DashboardLayoutItem[]) => {
    setDashboardLayout(memoizedPackLayout(items));
  }, [memoizedPackLayout]);

  // Optimized scaling with ResizeObserver and proper cleanup
  const [scale, setScale] = useState(1);
  const containerRef = useRef<HTMLDivElement>(null);
  const resizeObserverRef = useRef<ResizeObserver | null>(null);

  useEffect(() => {
    if (!containerRef.current) return;

    const updateScale = () => {
      if (containerRef.current) {
        const parent = containerRef.current.parentElement;
        if (parent) {
          const availableWidth = parent.clientWidth - 48; // Padding
          const targetWidth = 1100; // Approximate width of dashboard layout
          const newScale = Math.min(1.25, Math.max(0.5, availableWidth / targetWidth));
          setScale(newScale);
        }
      }
    };

    // Use ResizeObserver for better performance than window resize events
    resizeObserverRef.current = new ResizeObserver(() => {
      // Throttle updates using requestAnimationFrame
      requestAnimationFrame(updateScale);
    });

    // Observe the parent element for size changes
    const parent = containerRef.current.parentElement;
    if (parent) {
      resizeObserverRef.current.observe(parent);
    }

    // Initial calculation
    updateScale();

    return () => {
      if (resizeObserverRef.current) {
        resizeObserverRef.current.disconnect();
        resizeObserverRef.current = null;
      }
    };
  }, []);

  // Memoized metric tiles configuration - RIGOROUS (7-Point Standard)
  // Note: These are used by the legacy Rnd-based layout. Main view uses AldenDashboardGrid.
  const metricTiles = useMemo(() => {
    // Task Velocity: completed / hours elapsed (8am start)
    const now = new Date();
    const dayStart = new Date(now);
    dayStart.setHours(8, 0, 0, 0);
    const hoursElapsed = Math.max(1, (now.getTime() - dayStart.getTime()) / (1000 * 60 * 60));
    const velocity = stats.completedToday / hoursElapsed;

    // Commitment Ratio: completed / total planned
    const totalPlanned = stats.completedToday + stats.totalTasks;
    const commitmentRatio = totalPlanned > 0 ? Math.round((stats.completedToday / totalPlanned) * 100) : 0;

    return {
      'counter-tasks': {
        title: 'Active Tasks',
        value: stats.totalTasks,
        subtitle: `${stats.overdue} overdue`,
        accent: 'cyan' as const,
        icon: <CheckCircle2 size={24} />,
        active: true
      },
      'counter-schedule': {
        title: 'Schedule',
        value: stats.upcomingEvents,
        subtitle: 'Next 7 Days',
        accent: 'gold' as const,
        icon: <Calendar size={24} />,
        active: false
      },
      'counter-inbox': {
        title: 'Task Velocity',
        value: velocity.toFixed(1),
        subtitle: `tasks/hr (${hoursElapsed.toFixed(1)}h)`,
        accent: 'cyan' as const,
        icon: <Clock size={24} />,
        active: stats.completedToday > 0
      },
      'counter-uptime': {
        title: 'Commitment',
        value: `${commitmentRatio}%`,
        subtitle: `${stats.completedToday}/${totalPlanned}`,
        accent: commitmentRatio >= 50 ? 'green' as const : 'gold' as const,
        icon: <CheckSquare size={24} />,
        active: commitmentRatio > 0
      }
    };
  }, [stats]);

  // Memoized render functions
  const renderDashboardTile = useCallback((id: DashboardTileId) => {
    if (id in metricTiles) {
      const tile = metricTiles[id as keyof typeof metricTiles];
      return (
        <MetricCard
          title={tile.title}
          value={tile.value}
          subtitle={tile.subtitle}
          accentColor={tile.accent}
          icon={tile.icon}
          active={tile.active}
        />
      );
    }

    switch (id) {
      case 'panel-mission':
        return (
          <div
            className="h-full bg-alden-panel ring-1 ring-[rgba(255,255,255,0.2)] rounded-xl p-4 relative overflow-hidden flex flex-col"
            style={frameStyle}
          >
            {!isZenMode && <div className="absolute top-0 left-0 w-full h-1 bg-gradient-to-r from-alden-cyan/50 to-transparent opacity-60" />}
            <div className="flex flex-col w-full mb-2">
              <h2 className="text-sm font-semibold text-alden-gold uppercase tracking-widest flex items-center gap-2 font-orbitron-force mb-1">
                <CheckCircle2 size={16} />
                Mission Queue
              </h2>
              <div className="flex justify-center w-full">
                <button 
                  onClick={() => setIsTaskModalOpen(true)} 
                  className="flex items-center gap-1 px-2 py-0.5 bg-alden-cyan/10 border border-alden-cyan/30 rounded-full text-[9px] uppercase tracking-wider text-alden-cyan hover:bg-alden-cyan/20 transition-all font-semibold"
                >
                  <Plus size={8} />
                  New
                </button>
              </div>
            </div>
            <div className="space-y-2 overflow-y-auto pr-1">
              {tasks.slice(0, 5).map(task => (
                <div
                  key={task.id}
                  className="p-3 bg-alden-bg/50 border border-alden-border rounded flex items-center justify-between group hover:border-alden-cyan/30 hover:shadow-[0_0_10px_rgba(0,240,255,0.07)] transition-all cursor-pointer"
                  onClick={() => setSelectedTask(task)}
                >
                  <div className="flex items-center gap-3">
                    <div className={`w-1.5 h-1.5 rounded-full ${task.priority === 'high' ? 'bg-[#ef4444] shadow-[0_0_5px_rgba(239,68,68,0.5)]' : 'bg-alden-cyan'}`} />
                    <span className="text-sm text-[#c5d2e8] font-medium">{task.title}</span>
                  </div>
                  <div className="flex items-center gap-2">
                    <span className={`px-2 py-0.5 text-[10px] uppercase tracking-[0.12em] rounded-full ${priorityPill(task.priority)}`}>{task.priority || 'normal'}</span>
                    <span className={`px-2 py-0.5 text-[10px] uppercase tracking-[0.12em] rounded-full ${statusPill(task.status)}`}>{task.status || 'status'}</span>
                  </div>
                </div>
              ))}
              {tasks.length === 0 && (
                <div className="text-center py-6 text-[#9fb4d6] text-sm italic">
                  No active tasks. Systems nominal.
                </div>
              )}
            </div>
          </div>
        );
      case 'panel-schedule':
        return (
          <div
            className="h-full bg-alden-panel ring-1 ring-[rgba(255,255,255,0.2)] rounded-xl p-4 relative overflow-hidden flex flex-col"
            style={frameStyle}
          >
            {!isZenMode && <div className="absolute top-0 left-0 w-full h-1 bg-gradient-to-r from-alden-gold/50 to-transparent opacity-60" />}
            <div className="flex flex-col w-full mb-2">
              <h2 className="text-sm font-semibold text-alden-gold uppercase tracking-widest flex items-center gap-2 font-orbitron-force mb-1">
                <Calendar size={16} />
                Schedule
              </h2>
              <div className="flex justify-center w-full">
                <button 
                  onClick={() => setIsEventModalOpen(true)} 
                  className="flex items-center gap-1 px-2 py-0.5 bg-alden-cyan/10 border border-alden-cyan/30 rounded-full text-[9px] uppercase tracking-wider text-alden-cyan hover:bg-alden-cyan/20 transition-all font-semibold"
                >
                  <Plus size={8} />
                  New
                </button>
              </div>
            </div>
            <div className="space-y-3 overflow-y-auto pr-1">
              {events.slice(0, 3).map(event => (
                <div
                  key={event.id}
                  className="p-3 bg-alden-bg/50 border-l-2 border-alden-gold rounded-r flex flex-col hover:bg-alden-bg/80 transition-colors cursor-pointer"
                  onClick={() => setSelectedEvent(event)}
                >
                  <span className="text-xs text-alden-gold font-bold">{new Date(event.start_time).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}</span>
                  <span className="text-sm text-[#c5d2e8]">{event.title}</span>
                </div>
              ))}
              {events.length === 0 && (
                <div className="p-3 bg-alden-bg/50 border-l-2 border-alden-border rounded-r flex flex-col opacity-70">
                  <span className="text-sm text-[#c5d2e8]">No upcoming events</span>
                </div>
              )}
            </div>
          </div>
        );
      default:
        return null;
    }
  }, [metricTiles, tasks, events, priorityPill, statusPill, frameStyle]);

  const renderOverview = () => (
    <AldenDashboardGrid
      stats={stats}
      tasks={tasks}
      events={events}
      onTaskClick={setSelectedTask}
      onEventClick={setSelectedEvent}
      onNewTask={() => setIsTaskModalOpen(true)}
      onNewEvent={() => setIsEventModalOpen(true)}
    />
  );

  const renderTasksView = useCallback(() => (
    <div
      className="h-full bg-alden-panel ring-1 ring-[rgba(255,255,255,0.2)] rounded-xl p-5 flex flex-col w-full"
      style={frameStyle}
    >
      <div className="flex items-center justify-between mb-4">
        <h2 className="text-base text-alden-gold uppercase tracking-widest flex items-center gap-3 font-orbitron-force">
          <div className="w-3 h-3 bg-alden-gold rotate-45" />
          Mission Queue
        </h2>
        <div className="flex items-center gap-3">
          <div className="flex bg-[#0b1221] p-0.5 rounded-lg border border-alden-cyan/30 mr-2">
            <button
              onClick={() => setTasksViewMode('list')}
              className={`px-3 py-1 bg-transparent rounded-md flex items-center gap-2 transition-all duration-300 ${
                tasksViewMode === 'list' 
                  ? 'bg-alden-cyan/20 text-alden-cyan shadow-[0_0_8px_rgba(45,212,191,0.3)]' 
                  : 'text-slate-500 hover:text-slate-300 hover:bg-white/5'
              }`}
            >
              <List size={14} />
              <span className="text-[10px] font-bold tracking-wider uppercase">List</span>
            </button>
            <button
              onClick={() => setTasksViewMode('card')}
              className={`px-3 py-1 bg-transparent rounded-md flex items-center gap-2 transition-all duration-300 ${
                tasksViewMode === 'card' 
                  ? 'bg-alden-cyan/20 text-alden-cyan shadow-[0_0_8px_rgba(45,212,191,0.3)]' 
                  : 'text-slate-500 hover:text-slate-300 hover:bg-white/5'
              }`}
            >
              <Grid size={14} />
              <span className="text-[10px] font-bold tracking-wider uppercase">Cards</span>
            </button>
          </div>
          <div className="flex items-center gap-2">
            <span className="text-[10px] text-[#9fb4d6] uppercase tracking-wider font-bold text-right pt-[1px]">Sort by</span>
            <select
              value={tasksSortBy}
              onChange={(e) => setTasksSortBy(e.target.value as any)}
              className="bg-[#0b1221] border border-alden-cyan/30 rounded px-2 py-1.5 text-xs text-[#9fb4d6] focus:outline-none focus:border-alden-cyan/80 focus:ring-1 focus:ring-alden-cyan/40 uppercase tracking-wider"
              style={{ colorScheme: 'dark' }}
            >
              <option value="priority" className="bg-[#0b1124] text-gray-200">Priority</option>
