}

/**
 * AI Skill Bridge Class
 * 
 * Provides methods for AI systems to interact with Hearthlink skills
 */
export class AISkillBridge extends BaseBridge<{
  activeSessions: Array<[string, AISession]>;
  eventListeners: string[];
}> {
  private static instance: AISkillBridge;
  private activeSessions: Map<string, AISession> = new Map();
  private eventListeners: Map<string, Array<(payload: unknown) => void>> = new Map();

  private constructor() {
    super({
      agentId: 'ai_skill_bridge' as AgentId,
      userId: 'ai_system',
    });
  }

  public static getInstance(): AISkillBridge {
    if (!AISkillBridge.instance) {
      AISkillBridge.instance = new AISkillBridge();
    }
    return AISkillBridge.instance;
  }

  async getProps(): Promise<{
    activeSessions: Array<[string, AISession]>;
    eventListeners: string[];
  }> {
    return {
      activeSessions: Array.from(this.activeSessions.entries()),
      eventListeners: Array.from(this.eventListeners.keys()),
    };
  }

  protected async onInitialize(): Promise<void> {
    console.log('AI Skill Bridge initialized');
  }

  protected async onDestroy(): Promise<void> {
    this.activeSessions.clear();
    this.eventListeners.clear();
    console.log('AI Skill Bridge destroyed');
  }

  /**
   * Register an AI system with Hearthlink
   */
  static async registerSystem(aiSystem: AISystem): Promise<boolean> {
    try {
      await invoke('ai_register_system', { aiSystem });
      console.log(`AI system ${aiSystem.name} registered successfully`);
      return true;
    } catch (error) {
      console.error('Failed to register AI system:', error);
      return false;
    }
  }

  /**
   * Process a request from an AI system
   */
  static async processRequest(request: AISkillRequest): Promise<AISkillResponse> {
    try {
      const response = await invoke<AISkillResponse>('ai_process_request', { request });
      
      // Update local session cache
      const instance = AISkillBridge.getInstance();
      instance.updateLocalSession(request.conversationId, request, response);
      
      // Emit events for listeners
      instance.emitEvent('skill-response', {
        conversationId: request.conversationId,
        response
      });
      
      return response;
    } catch (error) {
      console.error('Failed to process AI request:', error);
      throw error;
    }
  }

  /**
   * Get session context for a conversation
   */
  static async getSessionContext(conversationId: string): Promise<AISystemContext | null> {
    try {
      const context = await invoke<AISystemContext | null>('ai_get_session_context', { conversationId });
      return context;
    } catch (error) {
      console.error('Failed to get session context:', error);
      return null;
    }
  }

  /**
   * Cleanup expired sessions
   */
  static async cleanupSessions(maxAgeHours = 24): Promise<number> {
    try {
      const cleanedCount = await invoke<number>('ai_cleanup_sessions', { maxAgeHours });
      
      // Update local session cache
      const instance = AISkillBridge.getInstance();
      instance.cleanupLocalSessions(maxAgeHours);
      
      return cleanedCount;
    } catch (error) {
      console.error('Failed to cleanup sessions:', error);
      return 0;
    }
  }

  /**
   * Add event listener for AI skill events
   */
  static addEventListener(event: string, callback: (payload: unknown) => void): void {
    const instance = AISkillBridge.getInstance();
    if (!instance.eventListeners.has(event)) {
      instance.eventListeners.set(event, []);
    }
    const listeners = instance.eventListeners.get(event);
    if (listeners) {
      listeners.push(callback);
    }
  }

  /**
   * Remove event listener
   */
  static removeEventListener(event: string, callback: (payload: unknown) => void): void {
    const instance = AISkillBridge.getInstance();
    const listeners = instance.eventListeners.get(event);
    if (listeners) {
      const index = listeners.indexOf(callback);
      if (index > -1) {
        listeners.splice(index, 1);
      }
    }
  }

  /**
   * Emit event to all listeners
   */
  private emitEvent(event: string, data: unknown): void {
    const listeners = this.eventListeners.get(event);
    if (listeners) {
      listeners.forEach((callback) => {
        try {
          callback(data);
        } catch (error) {
          console.error('Error in event listener:', error);
        }
      });
    }
  }

  /**
   * Update local session cache
   */
  private updateLocalSession(
    conversationId: string,
    request: AISkillRequest,
    response: AISkillResponse
  ): void {
    const sessionKey = `${request.aiSystem}:${conversationId}`;
    let session = this.activeSessions.get(sessionKey);

    if (!session) {
      session = {
        aiSystem: request.aiSystem,
        userId: request.userId,
        conversationId,
        context: request.context,
        loadedSkills: {},
        lastActivity: new Date().toISOString()
      };
    }

    // Update conversation history
    session.context.conversationHistory.push({
      role: 'user',
      content: request.query,
      timestamp: new Date().toISOString(),
      skillUsed: undefined
    });

    session.context.conversationHistory.push({
      role: 'assistant',
      content: response.response,
      timestamp: new Date().toISOString(),
      skillUsed: response.skillsUsed[0]
    });

    // Update active skills
    session.context.activeSkills = response.skillsUsed;
    session.lastActivity = new Date().toISOString();

    this.activeSessions.set(sessionKey, session);
  }

  /**
   * Cleanup local sessions
   */
  private cleanupLocalSessions(maxAgeHours: number): void {
    const cutoffTime = new Date(Date.now() - maxAgeHours * 60 * 60 * 1000);
    
    for (const [key, session] of this.activeSessions.entries()) {
      if (new Date(session.lastActivity) < cutoffTime) {
        this.activeSessions.delete(key);
      }
    }
  }

  /**
   * Get available skills for an AI system
   */
  static async getAvailableSkills(aiSystem: string): Promise<string[]> {
    try {
      // This would typically call a backend endpoint to get available skills
      // For now, return a hardcoded list based on integration level
      const basicSkills = ['general-assistance'];
      const advancedSkills = [...basicSkills, 'task-management', 'knowledge-retrieval'];
      const fullSkills = [...advancedSkills, 'security-monitoring', 'voice-integration', 'development-automation'];

      switch (aiSystem) {
        case 'claude':
        case 'kilo-code':
