          targetAgent: 'alden',
          capabilities: ['build', 'test', 'deploy', 'monitor']
        }
      };

      return skillInfo[skillName] || null;
    } catch (error) {
      console.error('Failed to get skill info:', error);
      return null;
    }
  }

  /**
   * Search the help catalog for context-aware assistance that agents can expand.
   */
  static searchHelp(
    query: string,
    options?: { modules?: HelpModuleId[]; tags?: string[]; contexts?: string[] },
  ): Array<{ id: string; title: string; description: string; moduleId: HelpModuleId; contexts?: string[]; tags: string[]; shortcuts?: string[] }> {
    const results = searchHelpCatalog(query, {
      modules: options?.modules,
      tags: options?.tags,
      contexts: options?.contexts,
    });

    return results.map((entry) => ({
      id: entry.id,
      title: entry.title,
      description: entry.description,
      moduleId: entry.moduleId,
      contexts: entry.contexts,
      tags: entry.tags,
      shortcuts: entry.shortcuts,
    }));
  }
}
