// Example 1: Initialize Claude and process a query
export async function exampleClaudeUsage() {
  await ClaudeIntegration.initialize();
  
  const response = await ClaudeIntegration.processQuery(
    'user123',
    'conv456',
    'Help me create a project plan for a new feature'
  );
  
  console.log('Claude response:', response);
}

// Example 2: Initialize all AI systems
export async function initializeAllAISystems() {
  const results = await Promise.allSettled([
    ClaudeIntegration.initialize(),
    GeminiIntegration.initialize(),
    KiloCodeIntegration.initialize()
  ]);
  
  results.forEach((result, index) => {
    const systems = ['Claude', 'Gemini', 'Kilo Code'];
    if (result.status === 'fulfilled') {
      console.log(`${systems[index]} initialized successfully`);
    } else {
      console.error(`${systems[index]} initialization failed:`, result.reason);
    }
  });
}

// Example 3: Listen for skill events
export function setupEventListeners() {
  AISkillBridge.addEventListener('skill-response', (data: unknown) => {
    console.log('Skill response received:', data);
    const payload = data as { response?: AISkillResponse };
    if (payload.response) {
      updateUIWithSkillResponse(payload.response);
    }
  });
}

function updateUIWithSkillResponse(response: AISkillResponse): void {
  // Implementation would update the UI based on the skill response
  console.log('Updating UI with skill response:', response);
}

