      { slot: 'primary', componentId: 'projects' },
      { slot: 'secondary', componentId: 'code-review' }
    ] 
  },
];

// --- Helpers ---

export function getComponentsForSlot(slot: PanelSlot): ComponentRegistryEntry[] {
  return COMPONENT_REGISTRY.filter(c => c.slot === slot);
}

export function getComponentsForPersona(personaId: string): ComponentRegistryEntry[] {
  return COMPONENT_REGISTRY.filter(c => {
    // If no scope defined, it's available to all (unless hidden by default? Logic suggests available)
    if (!c.personaScope || c.personaScope.length === 0) return true;
    return c.personaScope.includes(personaId);
  });
}

// Legacy helper adapter if needed, or update consumers to use the new logical checks
export function getComponentsForExpertise(expertise: string[]): ComponentRegistryEntry[] {
  return COMPONENT_REGISTRY.filter(c => {
    if (c.defaultVisible) return true;
    if (!c.expertise) return false;
    return c.expertise.some(e => expertise.includes(e));
  });
}

