        setPersonas(prev => prev.map(p =>
          p.persona_id === response.data?.persona_id
            ? { ...response.data, isActive: response.data.active }
            : p
        ));
        setSuccessMessage(`Persona "${response.data.name}" updated successfully!`);
        setShowPersonaModal(false);
        setEditingPersona(null);
      } else if (!response.success) {
        setError(response.error || 'Failed to update persona.');
      }
    } catch (err: unknown) {
      const errorMessage = err instanceof Error ? err.message : 'Failed to update persona.';
      setError(errorMessage);
    } finally {
      setLoading(false);
    }
  };

  const openEditModal = (persona: DisplayPersona) => {
    setEditingPersona({
      id: persona.persona_id,
      name: persona.name,
      description: persona.description,
      avatar: '/assets/Mimic.png', // Default avatar since MimicPersona doesn't have avatar
      tags: persona.expertise_areas.join(', '), // Use expertise_areas as tags for display
      traits: persona.traits,
      expertiseAreas: persona.expertise_areas.join(', '),
      communicationStyle: persona.communication_style,
    });
    setShowPersonaModal(true);
  };

  const closePersonaModal = () => {
    setShowPersonaModal(false);
    setEditingPersona(null);
  };

  if (loading && personas.length === 0) {
    return (
      <div className="min-h-screen bg-gradient-mythiq p-6 flex items-center justify-center">
        <div className="text-center">
          <div className="relative inline-block mb-6">
            {/* Pulsing cyan backlight */}
            <div
              className="absolute inset-0 rounded-full animate-pulse"
              style={{
                background: 'radial-gradient(circle, rgba(34, 211, 238, 0.4) 0%, rgba(34, 211, 238, 0) 70%)',
                transform: 'scale(1.5)',
                filter: 'blur(8px)',
              }}
            />
            {/* Mimic icon */}
            <img
              src="/assets/Mimic.png"
              alt="Mimic"
              className="relative w-20 h-20 object-contain animate-pulse"
              style={{
                filter: 'drop-shadow(0 0 15px rgba(34, 211, 238, 0.6)) drop-shadow(0 0 30px rgba(34, 211, 238, 0.3))',
              }}
            />
          </div>
          <div className="text-white text-lg font-medium">Loading personas...</div>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-mythiq p-6">
      <div className="max-w-7xl mx-auto">
        <div className="module-card mb-6">
        <div className="flex items-center justify-between p-6 border-b border-border">
          <div className="flex items-center gap-4">
            {/* Back to Launcher Button */}
            {onBackToLauncher && (
              <BackToLauncher onClick={onBackToLauncher} variant="minimal" />
            )}

            <div className="flex items-center gap-3">
              <img
                src="/assets/Mimic.png"
                alt="Mimic"
                className="w-12 h-12 object-contain"
                style={{
                  filter: 'drop-shadow(0 0 15px color-mix(in oklch, var(--mimic-accent) 40%, transparent))'
                }}
              />
              <div>
                <h1 className="text-2xl font-semibold">Mimic Persona Forge</h1>
                <p className="text-xs text-muted-foreground">Dynamic Persona Generator & Manager</p>
              </div>
            </div>
          </div>
          <div className="flex items-center gap-2">
            <span className="w-2 h-2 rounded-full animate-pulse" style={{ backgroundColor: 'var(--mimic-accent)' }}></span>
            <span className="text-sm" style={{ color: 'var(--mimic-accent)' }}>Adapting</span>
          </div>
        </div>

        {/* Tab Navigation */}
        <div className="px-6 pb-4 flex gap-1 border-b border-border">
          <button
            onClick={() => setActiveTab('personas')}
            role="tab"
            aria-selected={activeTab === 'personas'}
            className="px-4 py-2 text-sm font-medium rounded-t-lg transition-colors relative"
            style={{
              color: activeTab === 'personas' ? 'var(--mimic-accent)' : 'var(--muted-foreground)',
              backgroundColor: activeTab === 'personas' ? 'color-mix(in oklch, var(--mimic-accent) 10%, transparent)' : 'transparent',
            }}
          >
            Personas
            {activeTab === 'personas' && (
              <span
                className="absolute bottom-0 left-0 right-0 h-0.5"
                style={{ backgroundColor: 'var(--mimic-accent)' }}
              />
            )}
          </button>
          <button
            onClick={() => setActiveTab('skills')}
            role="tab"
            aria-selected={activeTab === 'skills'}
            className="px-4 py-2 text-sm font-medium rounded-t-lg transition-colors relative"
            style={{
              color: activeTab === 'skills' ? 'var(--mimic-accent)' : 'var(--muted-foreground)',
              backgroundColor: activeTab === 'skills' ? 'color-mix(in oklch, var(--mimic-accent) 10%, transparent)' : 'transparent',
            }}
          >
            Skills
            {activeTab === 'skills' && (
              <span
                className="absolute bottom-0 left-0 right-0 h-0.5"
                style={{ backgroundColor: 'var(--mimic-accent)' }}
              />
            )}
          </button>
        </div>
        </div>

        {error && (
          <div
            className="mb-6 p-4 rounded-md"
            style={{
              backgroundColor: 'color-mix(in oklch, var(--destructive) 20%, transparent)',
              borderColor: 'var(--destructive)',
              color: 'var(--destructive)',
              border: '1px solid var(--destructive)',
            }}
          >
            {error}
          </div>
        )}
        {/* Personas Tab Content */}
        {activeTab === 'personas' && (
          <div className="module-card">
            <div className="p-4 border-b border-border flex justify-between items-center">
              <h2 className="text-xl font-semibold">Persona Library</h2>
              <button
                onClick={() => setShowPersonaModal(true)}
                className="px-4 py-2 text-sm font-medium rounded-lg transition-opacity hover:opacity-90"
                style={{
                  backgroundColor: 'var(--mimic-accent)',
                  color: 'var(--accent-foreground)',
                }}
              >
                + Create New Persona
              </button>
            </div>
            <div className="p-4 grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
              {personas.length === 0 ? (
                <div className="col-span-full text-center py-8 text-muted-foreground">
                  No personas created yet. Click &quot;Create New Persona&quot; to get started.
                </div>
              ) : (
                personas.map(persona => (
                  <div
                    key={persona.persona_id}
                    className="p-6 rounded-lg border-2 transition-all duration-300"
                    style={{
                      borderColor: persona.isActive
                        ? 'var(--mimic-accent)'
                        : 'var(--border)',
                      backgroundColor: persona.isActive
                        ? 'color-mix(in oklch, var(--mimic-accent) 10%, var(--card))'
                        : 'var(--card)',
                      boxShadow: persona.isActive
                        ? '0 10px 25px -5px color-mix(in oklch, var(--mimic-accent) 20%, transparent)'
                        : 'none',
                    }}
                  >
                    <div className="flex items-center gap-4 mb-4">
                      <img
                        src="/assets/Mimic.png"
                        alt={persona.name}
                        className="w-16 h-16 rounded-full border-2"
                        style={{ borderColor: 'color-mix(in oklch, var(--mimic-accent) 50%, transparent)' }}
                      />
                      <div>
                        <h3 className="text-xl font-bold text-foreground">{persona.name}</h3>
                        {persona.isActive && (
                          <span
                            className="text-xs font-medium"
                            style={{ color: 'var(--mimic-accent)' }}
                          >
                            ACTIVE
                          </span>
                        )}
                      </div>
                    </div>
                    <p className="text-sm text-muted-foreground mb-4 h-12 overflow-hidden">{persona.description}</p>
                    <div className="flex flex-wrap gap-2 mb-4">
                      {persona.expertise_areas.map(area => (
                        <span
                          key={area}
                          className="text-xs px-2 py-1 rounded-full"
                          style={{
                            backgroundColor: 'color-mix(in oklch, var(--mimic-accent) 15%, transparent)',
                            color: 'var(--mimic-accent)',
                          }}
                        >
                          {area}
                        </span>
                      ))}
                    </div>
                    <div className="flex gap-2">
                      <button
                        onClick={() => togglePersona(persona.persona_id)}
                        disabled={persona.isActive}
                        className="flex-1 py-2 text-sm font-semibold rounded-lg transition-colors disabled:opacity-50 disabled:cursor-not-allowed"
                        style={{
                          backgroundColor: persona.isActive ? 'var(--muted)' : 'var(--mimic-accent)',
                          color: persona.isActive ? 'var(--muted-foreground)' : 'var(--accent-foreground)',
                        }}
                      >
                        {persona.isActive ? 'Active' : 'Activate'}
                      </button>
                      <button
                        onClick={() => openEditModal(persona)}
                        className="py-2 px-4 text-sm font-semibold rounded-lg transition-colors hover:opacity-80"
                        style={{
                          backgroundColor: 'var(--muted)',
                          color: 'var(--foreground)',
                        }}
                      >
                        Edit
                      </button>
                    </div>
                  </div>
                ))
              )}
            </div>
          </div>
        )}

        {/* Skills Tab Content */}
        {activeTab === 'skills' && (
          <div className="module-card p-6">
            <SkillMarketplace
              personaId={personas.find(p => p.isActive)?.persona_id || 'default'}
              personaName={personas.find(p => p.isActive)?.name || 'Default Persona'}
              onSkillToggle={(skillId: string, enabled: boolean) => {
                setSuccessMessage(`Skill ${enabled ? 'enabled' : 'disabled'} successfully`);
              }}
            />
          </div>
        )}

      </div>
      {successMessage && (
        <div
          className="fixed top-4 right-4 z-50 p-4 rounded-md shadow-lg animate-in slide-in-from-top"
          style={{
            backgroundColor: 'var(--success)',
            color: 'white',
          }}
        >
          {successMessage}
        </div>
      )}
      <PersonaModal
        isOpen={showPersonaModal}
        onClose={closePersonaModal}
        onSubmit={editingPersona ? handleEditPersona : handleCreatePersona}
        initialData={editingPersona}
      />
    </div>
  );
};

