  onDeactivate,
  resolveSkillName
}) => {
  const handleInstanceKeyDown = (event: React.KeyboardEvent, instanceId: string) => {
    if (event.key === 'Enter' || event.key === ' ') {
      event.preventDefault();
      onDeactivate(instanceId);
    }
  };

  return (
    <div className="flex flex-col h-full bg-[#0b1221]/50 rounded-xl border border-white/5 overflow-hidden" role="region" aria-label="Active skill instances">
      <div className="p-4 border-b border-white/5 bg-black/20 flex items-center gap-2">
        <Activity size={16} className="text-alden-gold" aria-hidden="true" />
        <h3 className="text-xs font-semibold uppercase tracking-widest text-slate-300">
          Active Instances
        </h3>
        <span className="ml-auto text-[10px] text-slate-500 py-0.5 px-2 rounded-full bg-white/5" aria-label={`${instances.length} active instances`}>
          {instances.length}
        </span>
      </div>
      
      <div className="flex-1 overflow-y-auto p-2 space-y-2 scrollbar-thin scrollbar-thumb-white/10" role="list" aria-label="List of active skill instances">
        {instances.length === 0 ? (
          <div className="flex flex-col items-center justify-center h-32 text-slate-500 gap-2 opacity-60">
            <Activity size={24} className="opacity-50" />
            <span className="text-xs italic">No active instances</span>
          </div>
        ) : (
          <AnimatePresence mode="popLayout">
            {instances.map((instance) => (
              <motion.div
                key={instance.instance_id}
                layout
                initial={{ opacity: 0, x: -10 }}
                animate={{ opacity: 1, x: 0 }}
                exit={{ opacity: 0, x: 10 }}
                className="group flex items-center justify-between p-3 bg-black/40 border border-white/5 rounded-lg hover:border-alden-gold/30 hover:bg-alden-gold/5 transition-all focus-within:ring-2 focus-within:ring-alden-gold/50"
                data-testid="skill-instance"
                role="listitem"
                aria-label={`${resolveSkillName(instance.skill_id)} - ${instance.enabled ? 'Enabled' : 'Disabled'}`}
              >
                <div className="flex flex-col gap-1 min-w-0">
                  <span className="text-sm font-medium text-slate-200 truncate" data-testid="skill-name">
                    {resolveSkillName(instance.skill_id)}
                  </span>
                  <div className="flex items-center gap-2">
                    <div className={`w-1.5 h-1.5 rounded-full ${instance.enabled ? 'bg-emerald-400 shadow-[0_0_8px_rgba(52,211,153,0.4)]' : 'bg-slate-600'}`} />
                    <span 
                      className={`text-[10px] uppercase tracking-wider font-mono ${instance.enabled ? 'text-emerald-400' : 'text-slate-500'}`}
                      data-testid="instance-status"
                    >
                      {instance.enabled ? 'Enabled' : 'Disabled'}
                    </span>
                  </div>
                </div>
                
                <button
                  onClick={() => onDeactivate(instance.instance_id)}
                  onKeyDown={(e) => handleInstanceKeyDown(e, instance.instance_id)}
                  data-testid="deactivate-skill"
                  aria-label={`Deactivate ${resolveSkillName(instance.skill_id)} skill`}
                  className="p-2 rounded-md text-slate-600 hover:text-red-400 hover:bg-red-500/10 opacity-0 group-hover:opacity-100 transition-all focus:opacity-100 focus:outline-none focus:ring-2 focus:ring-red-400/50"
                >
                  <Power size={14} aria-hidden="true" />
                </button>
              </motion.div>
            ))}
          </AnimatePresence>
        )}
      </div>
    </div>
  );
};

