 */
export function getModuleSelector(moduleName: string): string {
  const lowerName = moduleName.toLowerCase();
  const primary = `[data-testid="module-${lowerName}"]`;
  const fallback = `[role="navigation"] button:has-text("${moduleName}")`;
  return `${primary}, ${fallback}`;
}

/**
 * Get button by text (use sparingly, prefer data-testid)
 */
export function getButtonByText(text: string | RegExp): string {
  return `[role="button"]:has-text("${text}")`;
}

/**
 * Get input by label (use sparingly, prefer data-testid)
 */
export function getInputByLabel(label: string): string {
  return `input[aria-label="${label}"], [aria-labelledby*="${label}"]`;
}

/**
 * Get element by role and name (accessible selector)
 */
export function getByRoleAndName(role: string, name: string | RegExp): string {
  return `[role="${role}"][name="${name}"]`;
}

// ========================================
// SELECTOR VALIDATION
// ========================================

/**
 * Validate that a selector follows best practices
 */
export function validateSelector(selector: string): {
  valid: boolean;
  issues: string[];
} {
  const issues: string[] = [];

  // Check for CSS class selectors (brittle)
  if (selector.match(/\.[a-z0-9_-]+(\s+[a-z0-9_-]+)*/)) {
    issues.push('Contains CSS class selector (brittle, prefer data-testid)');
  }

  // Check for text-based selectors (brittle)
  if (selector.match(/:has-text\(/)) {
    issues.push('Contains text-based selector (brittle, prefer data-testid)');
  }

  // Check for complex selectors (hard to maintain)
  if (selector.length > 100) {
    issues.push('Selector is too long (> 100 chars, consider simplifying)');
  }

  // Check for data-testid (good)
  if (selector.match(/data-testid/)) {
    // This is good, no issue
  } else {
    issues.push('Missing data-testid attribute (prefer data-testid for stability)');
  }

  return {
    valid: issues.length === 0,
    issues,
  };
}

