import { resolveAsset } from '@/utils/assets';

export interface HearthlinkLoadingSequenceProps {
  onComplete?: () => void;
  message?: string;
  subtext?: string;
  size?: number | string;
  className?: string;
  fullScreen?: boolean;
}

export const HearthlinkLoadingSequence: React.FC<HearthlinkLoadingSequenceProps> = ({
  onComplete,
  message = 'Initializing multi-agent system...',
  subtext,
  size = 500,
  className = '',
  fullScreen = false,
}) => {
  const videoRef = useRef<HTMLVideoElement>(null);
  const containerRef = useRef<HTMLDivElement>(null);
  const videoSrc = resolveAsset('assets/Logo_Animation_From_Star_To_Hearthlink.mp4');

  useEffect(() => {
    const video = videoRef.current;
    if (!video) return;

    // Try to play immediately
    video.muted = true; // Autoplay usually requires muted
    video.play().catch((err) => {
      console.warn("Video autoplay failed:", err);
      // If autoplay fails, we might want to trigger complete immediately 
      // or show a play button. For a loading screen, standard practice 
      // is to fail gracefully to the app.
      if (onComplete) onComplete(); 
    });

    const handleEnded = () => {
      if (onComplete) {
        onComplete();
      }
    };

    video.addEventListener('ended', handleEnded);
    return () => {
      video.removeEventListener('ended', handleEnded);
    };
  }, [onComplete]);

  // If we wanted to ensure the video loads before showing anything, we could use onLoadedData
  // But for now, we'll let it stream/play as soon as possible.

  return (
    <div
      ref={containerRef}
