export interface CreateAldenTaskRequest {
  userId?: string; // Optional - bridge will fallback to authenticated user
  title: string;
  description?: string;
  priority?: AldenTask['priority'];
  due_date?: string; // Changed to string (ISO 8601)
  tags?: string[];
  parent_id?: string;
  natural_language?: string; // For NLP parsing in Rust backend
}

// For backward compatibility with Alden module
export type CreateTaskRequest = CreateAldenTaskRequest;

export interface TaskUpdates {
  title?: string;
  description?: string;
  status?: Task['status'];
  priority?: Task['priority'];
  due_date?: string; // Changed to string (ISO 8601)
  tags?: string[];
}

export interface TaskViewPreferences {
  sortBy: 'priority' | 'due_date' | 'created_at' | 'title';
  filterBy?: 'status' | 'priority' | 'tags';
  groupBy?: 'status' | 'priority' | 'assignee';
}

export interface TaskAnalytics {
  total_tasks: number;
  completed_tasks: number;
  completion_rate: number;
  average_completion_time: number; // in hours
  tasks_by_priority: Record<Task['priority'], number>;
  tasks_by_status: Record<Task['status'], number>;
}

export interface TaskManagementProps {
  tasks: Task[];
  createTask: (task: CreateTaskRequest) => Promise<Task>;
  updateTask: (taskId: string, updates: TaskUpdates) => Promise<Task>;
  deleteTask: (taskId: string) => Promise<void>;
  viewPreferences: TaskViewPreferences;
  analytics: TaskAnalytics;
}

// High-priority directives (extend task with execution metadata)
export interface Directive extends Task {
  urgency_level: 'critical' | 'standard';
  protocol_ref: string; // e.g., Chimera
  issued_by?: string;
  acknowledged_at?: string;
}

// Calendar Management
export interface CalendarEvent {
  id: string;
  event_id: string; // legacy alias required by UI
  title: string;
  description?: string;
  start_time: string; // Changed to string (ISO 8601)
  end_time: string; // Changed to string (ISO 8601)
  location?: string;
  video_link?: string;
  attendees?: string[];
  event_type?: string;
  color?: string;
  reminder?: number; // minutes before event
  recurrence?: RecurrencePattern;
  calendar_id: string;
  created_at: string; // Changed to string (ISO 8601)
  updated_at: string; // Changed to string (ISO 8601)
}

export interface RecurrencePattern {
  frequency: 'daily' | 'weekly' | 'monthly' | 'yearly';
  interval: number;
  end_date?: string; // Changed to string (ISO 8601)
  exceptions?: string[]; // Changed to string (ISO 8601)
}

export interface CreateEventRequest {
  title: string;
  description?: string;
  start_time: string; // Changed to string (ISO 8601)
  end_time: string; // Changed to string (ISO 8601)
  location?: string;
  attendees?: string[];
  reminder?: number;
  recurrence?: RecurrencePattern;
  calendar_id: string;
  event_type?: string;
}

export interface EventUpdates {
  title?: string;
  description?: string;
  start_time?: string; // Changed to string (ISO 8601)
  end_time?: string; // Changed to string (ISO 8601)
  location?: string;
  attendees?: string[];
  reminder?: number;
}

export interface CalendarViewPreferences {
  view: 'day' | 'week' | 'month' | 'year';
  start_of_week: 'sunday' | 'monday';
  time_format: '12h' | '24h';
}

export interface AvailabilitySlot {
  start: string;
  end: string;
  isFree: boolean;
  eventTitle?: string;
}

export interface CalendarManagementProps {
  events: CalendarEvent[];
  createEvent: (event: CreateEventRequest) => Promise<CalendarEvent>;
  updateEvent: (eventId: string, updates: EventUpdates) => Promise<CalendarEvent>;
  deleteEvent: (eventId: string) => Promise<void>;
  viewPreferences: CalendarViewPreferences;
  availability: AvailabilitySlot[];
}

// Email Management
export interface EmailAccount {
  id: string; // Changed from account_id to id
  account_id?: string; // legacy alias
  email_address: string;
  display_name: string;
  protocol: 'imap' | 'pop3';
  server: string;
  server_host?: string; // legacy alias
  server_port?: number; // legacy alias
  port: number;
  ssl_enabled: boolean;
  last_sync: string; // Changed to string (ISO 8601)
  status: 'active' | 'error' | 'disabled';
  provider?: string; // optional metadata for UI
  is_active?: boolean; // legacy alias
}

export interface EmailMessage {
  id: string; // Changed from message_id to id
  account_id: string;
  from: string;
  to: string[];
  cc?: string[];
  bcc?: string[];
  subject: string;
  body: string;
  body_html?: string;
  received_date: string; // Changed to string (ISO 8601)
  read: boolean;
  starred: boolean;
  folder: string;
  labels?: string[];
  attachments?: EmailAttachment[];
}

export interface EmailAttachment {
  filename: string;
  mime_type: string;
  size: number; // bytes
  content_id?: string;
}

export interface EmailSendRequest {
  account_id: string;
  to: string[];
  cc?: string[];
  bcc?: string[];
  subject: string;
  body: string;
  body_html?: string;
  attachments?: File[];
}

export interface EmailTemplate {
  template_id: string;
  name: string;
  subject: string;
  body: string;
  variables?: string[];
}

export interface EmailManagementProps {
  accounts: EmailAccount[];
  messages: EmailMessage[];
  addAccount: (account: Omit<EmailAccount, 'account_id' | 'last_sync' | 'status'>) => Promise<EmailAccount>;
  sendEmail: (request: EmailSendRequest) => Promise<void>;
  convertToTask: (messageId: string) => Promise<Task>;
  templates: EmailTemplate[];
}

