export interface HearthlinkLoadingSequenceProps {
  onComplete?: () => void;
  message?: string;
  subtext?: string;
  size?: number | string;
  className?: string;
  fullScreen?: boolean;
  variant?: 'video' | 'pulse';
}

export const HearthlinkLoadingSequence: React.FC<HearthlinkLoadingSequenceProps> = ({
  onComplete,
  message = 'Initializing multi-agent system...',
  subtext,
  size = 500,
  className = '',
  fullScreen = false,
  variant = 'video',
}) => {
  const videoRef = useRef<HTMLVideoElement>(null);
  const containerRef = useRef<HTMLDivElement>(null);
  const videoSrc = resolveAsset('assets/Logo_Animation_From_Star_To_Hearthlink.mp4');
  const logoSrc = resolveAsset('assets/Hearthlink.png');
  const isVideo = variant === 'video';

  useEffect(() => {
    if (!isVideo) return;
    const video = videoRef.current;
    if (!video) return;

    video.muted = true;
    video.play().catch((err) => {
      console.warn("Video autoplay failed:", err);
      if (onComplete) onComplete();
    });

    const handleEnded = () => {
      if (onComplete) {
        onComplete();
      }
    };

    video.addEventListener('ended', handleEnded);
    return () => {
      video.removeEventListener('ended', handleEnded);
    };
  }, [onComplete, isVideo]);

  return (
    <div
      ref={containerRef}
      className={`${fullScreen ? 'fixed inset-0 z-50 flex items-center justify-center bg-[#030810]' : 'flex items-center justify-center'} ${className}`}
      role="status"
      aria-live="assertive"
    >
      <div className="flex flex-col items-center justify-center w-full max-w-4xl p-8">
        {isVideo ? (
          <div 
            className="relative overflow-hidden rounded-lg shadow-[0_0_50px_rgba(0,229,255,0.15)] border border-cyan-500/30 bg-black"
            style={{ maxWidth: typeof size === 'number' ? `${size}px` : size }}
          >
              <video
                ref={videoRef}
                src={videoSrc}
                className="w-full h-auto object-contain relative z-0"
                style={{ 
                  transform: 'scale(1.15)',
                }}
                playsInline
                muted
              />

              <div className="absolute inset-0 pointer-events-none z-10">
                  <div className="absolute top-0 left-0 w-8 h-8 border-t-2 border-l-2 border-cyan-500/80 rounded-tl-sm shadow-[0_0_10px_rgba(6,182,212,0.5)]" />
                  <div className="absolute top-0 right-0 w-8 h-8 border-t-2 border-r-2 border-cyan-500/80 rounded-tr-sm shadow-[0_0_10px_rgba(6,182,212,0.5)]" />
                  <div className="absolute bottom-0 left-0 w-8 h-8 border-b-2 border-l-2 border-cyan-500/80 rounded-bl-sm shadow-[0_0_10px_rgba(6,182,212,0.5)]" />
                  <div className="absolute bottom-0 right-0 w-8 h-8 border-b-2 border-r-2 border-cyan-500/80 rounded-br-sm shadow-[0_0_10px_rgba(6,182,212,0.5)]" />

                  <div className="absolute inset-0 bg-[linear-gradient(transparent_50%,rgba(0,0,0,0.25)_50%)] bg-[length:100%_4px] opacity-20" />
              </div>
          </div>
        ) : (
          <div
            className="relative flex items-center justify-center"
            style={{
              width: typeof size === 'number' ? `${Math.min(size, 240)}px` : size,
              height: typeof size === 'number' ? `${Math.min(size, 240)}px` : size,
            }}
          >
            <div className="absolute inset-0 rounded-full bg-cyan-500/10 blur-2xl animate-pulse" />
            <img
              src={logoSrc}
              alt="Hearthlink"
              className="relative w-full h-full object-contain animate-pulse"
            />
          </div>
        )}

        {(message || subtext) && (
          <div className="mt-8 text-center space-y-2 z-20 animate-fadeIn relative">
            {message && (
              <p
                className="text-cyan-400 text-sm font-orbitron tracking-[0.2em] font-medium uppercase"
                style={{ textShadow: '0 0 10px rgba(34, 211, 238, 0.4)' }}
              >
                {message}
              </p>
            )}
            {subtext && <p className="text-xs text-slate-500 font-mono tracking-wide mt-1">{subtext}</p>}
          </div>
        )}
      </div>
    </div>
  );
};
