import React, { useCallback, useEffect, useRef, useState } from 'react';
// eslint-disable-next-line import/named
import { FitAddon } from '@xterm/addon-fit';
import { Terminal } from '@xterm/xterm';
import '@xterm/xterm/css/xterm.css';
import { AldenBridge } from '../../bridges';
import { CliCustomCommand, CommandResult } from '../../types/agents';

interface AldenTerminalProps {
  userId?: string;
  className?: string;
}

const BUILT_IN_COMMANDS = ['help', 'history', 'clear', 'tasks', 'calendar', 'status', 'sync'];

export const AldenTerminal: React.FC<AldenTerminalProps> = ({ userId = 'default_user', className = '' }) => {
  const getErrorMessage = (err: unknown) => (err instanceof Error ? err.message : String(err));

  const containerRef = useRef<HTMLDivElement>(null);
  const terminalRef = useRef<Terminal | null>(null);
