    term.writeln('\x1b[1;36mÆ’""Æ’"?Æ’"?Æ’"?Æ’"?Æ’"?Æ’"?Æ’"?Æ’"?Æ’"?Æ’"?Æ’"?Æ’"?Æ’"?Æ’"?Æ’"?Æ’"?Æ’"?Æ’"?Æ’"?Æ’"?Æ’"?Æ’"?Æ’"?Æ’"?Æ’"?Æ’"?Æ’"?Æ’"?Æ’"?Æ’"?Æ’"?Æ’"?Æ’"?Æ’"?Æ’"?Æ’"?Æ’"?Æ’"?Æ’"?Æ’"?Æ’"?Æ’"?Æ’"?Æ’"?Æ’"?Æ’"?Æ’"?Æ’"?Æ’"?Æ’"?Æ’"?Æ’"?Æ’"?Æ’"?Æ’"?Æ’"?Æ’"?Æ’"?Æ’"?Æ’"?Æ’"?Æ’"?Æ’"~\x1b[0m');
    term.writeln('Type \x1b[1;32mhelp\x1b[0m for a list of commands.');
    term.writeln('');
  }, []);

  const loadCommandHistory = useCallback(async () => {
    if (!bridgeRef.current) return;

    try {
      const response = await bridgeRef.current.getCommandHistory(userId);
      if (response.success) {
        setCommandHistory(response.data || []);
        setHistoryIndex(-1);
      } else if (response.error) {
        setTerminalError(response.error);
      }
    } catch (error: unknown) {
      setTerminalError(getErrorMessage(error) || 'Failed to load CLI history');
    }
  }, [userId]);

  const loadCustomCommands = useCallback(async () => {
    if (!bridgeRef.current) return;

    try {
      const response = await bridgeRef.current.listCustomCommands(userId);
      if (response.success) {
        setCustomCommands(response.data || []);
        setCommandManagerError(null);
      } else if (response.error) {
        setCommandManagerError(response.error);
      }
    } catch (error: unknown) {
      setCommandManagerError(getErrorMessage(error) || 'Failed to load custom commands');
    }
  }, [userId]);

  const runBuiltInCommand = useCallback((command: string) => {
    const term = terminalRef.current;
    if (!term) return false;

    switch (command) {
      case 'clear':
        term.clear();
        return true;
      case 'help':
        term.writeln('Available commands:');
        term.writeln('  help     Show this help message');
        term.writeln('  history  Display recently executed commands');
        term.writeln('  clear    Clear terminal');
        term.writeln('  tasks    (Example) Show task commands');
        term.writeln('  calendar (Example) Show calendar commands');
        term.writeln('  status   Query Alden system status');
        term.writeln('  sync     Trigger latest workspace sync');
        return true;
      case 'history':
        if (historyRef.current.length === 0) {
          term.writeln('No commands executed yet.');
        } else {
          historyRef.current.forEach((entry, index) => {
            term.writeln(`${index + 1}`.padStart(4, ' ') + `  ${entry}`);
          });
        }
        return true;
      default:
        return false;
    }
  }, []);

  const runCommand = useCallback(async (input: string) => {
    const term = terminalRef.current;
    if (!term) return;

    const trimmed = input.trim();
